import { Apollo, QueryRef } from 'apollo-angular';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import {
  ContentType,
  CONTENT_TYPES,
  UnsubscribeComponent,
  WorkflowService,
  FormsQueryResponse,
  AddFormMutationResponse,
} from '@oort-front/shared';
import { ActivatedRoute } from '@angular/router';
import { takeUntil } from 'rxjs';
import { ADD_FORM } from '../../graphql/mutations';
import { GET_FORMS } from '../../graphql/queries';
import { Dialog } from '@angular/cdk/dialog';
import { SnackbarService } from '@oort-front/ui';

/** Default items per query for pagination */
const ITEMS_PER_PAGE = 10;

/**
 * Add step page component.
 */
@Component({
  selector: 'app-add-step',
  templateUrl: './add-step.component.html',
  styleUrls: ['./add-step.component.scss'],
})
export class AddStepComponent extends UnsubscribeComponent implements OnInit {
  // === DATA ===
  public contentTypes = CONTENT_TYPES.filter((x) => x.value !== 'workflow');
  public formsQuery!: QueryRef<FormsQueryResponse>;

  // === REACTIVE FORM ===
  public stepForm = this.fb.group({
    type: ['', Validators.required],
    content: [''],
  });
  public stage = 1;

  /**
   * Add step page component
   *
   * @param route Angular activated route
   * @param fb Angular form builder
   * @param dialog Dialog service
   * @param snackBar Shared snackbar service
   * @param apollo Apollo service
   * @param workflowService Shared workflow service
   */
  constructor(
    private route: ActivatedRoute,
    private fb: FormBuilder,
    public dialog: Dialog,
    private snackBar: SnackbarService,
    private apollo: Apollo,
    private workflowService: WorkflowService
  ) {
    super();
  }

  ngOnInit(): void {
    this.stepForm.get('type')?.valueChanges.subscribe((type) => {
      const contentControl = this.stepForm.controls.content;
      if (type === ContentType.form) {
        this.formsQuery = this.apollo.watchQuery<FormsQueryResponse>({
          query: GET_FORMS,
          variables: {
            first: ITEMS_PER_PAGE,
            sortField: 'name',
          },
        });
        contentControl.setValidators([Validators.required]);
        contentControl.updateValueAndValidity();
      } else {
        contentControl.setValidators(null);
        contentControl.setValue(null);
        contentControl.updateValueAndValidity();
      }
      this.onNext();
    });
  }

  /**
   * Check if form stage is valid
   *
   * @param stage index of stage
   * @returns is stage valid
   */
  isStageValid(stage: number): boolean {
    switch (stage) {
      case 1: {
        return this.stepForm.controls.type.valid;
      }
      case 2: {
        return this.stepForm.controls.content.valid;
      }
      default: {
        return true;
      }
    }
  }

  /**
   * Submit form to workflow service
   */
  onSubmit(): void {
    this.workflowService.addStep(this.stepForm.value, this.route);
  }

  /**
   * Go to previous stage
   */
  onBack(): void {
    this.stage -= 1;
  }

  /**
   * Go to next stage
   */
  onNext(): void {
    switch (this.stage) {
      case 1: {
        // eslint-disable-next-line @typescript-eslint/no-unused-expressions
        this.stepForm.controls.type.value === ContentType.form
          ? (this.stage += 1)
          : this.onSubmit();
        break;
      }
      case 2: {
        this.onSubmit();
        break;
      }
      default: {
        this.stage += 1;
        break;
      }
    }
  }

  /**
   * Open add form component
   */
  async onAdd(): Promise<void> {
    const { AddFormModalComponent } = await import(
      '../../../../../components/add-form-modal/add-form-modal.component'
    );
    const dialogRef = this.dialog.open(AddFormModalComponent);
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        const variablesData = { name: value.name };
        Object.assign(
          variablesData,
          value.resource && { resource: value.resource },
          value.template && { template: value.template }
        );
        this.apollo
          .mutate<AddFormMutationResponse>({
            mutation: ADD_FORM,
            variables: variablesData,
          })
          .subscribe({
            next: ({ data }) => {
              if (data) {
                const { id } = data.addForm;
                this.stepForm.controls.content.setValue(id as string);
                this.onSubmit();
              }
            },
            error: (err) => {
              this.snackBar.openSnackBar(err.message, { error: true });
            },
          });
      }
    });
  }

  /**
   * Changes the query according to search text
   *
   * @param search Search text from the graphql select
   */
  onSearchChange(search: string): void {
    const variables = this.formsQuery.variables;
    this.formsQuery.refetch({
      ...variables,
      filter: {
        logic: 'and',
        filters: [
          {
            field: 'name',
            operator: 'contains',
            value: search,
          },
        ],
      },
    });
  }
}

import { Component, OnInit } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import { Dialog } from '@angular/cdk/dialog';
import {
  Aggregation,
  AggregationService,
  ConfirmService,
  Resource,
  UnsubscribeComponent,
  ResourceQueryResponse,
} from '@oort-front/shared';
import { Apollo, QueryRef } from 'apollo-angular';
import get from 'lodash/get';
import {
  getCachedValues,
  updateQueryUniqueValues,
} from '../../../../utils/update-queries';
import { GET_RESOURCE_AGGREGATIONS } from './graphql/queries';
import { takeUntil } from 'rxjs';
import { UIPageChangeEvent, handleTablePageEvent } from '@oort-front/ui';

/**
 * Aggregations tab of resource page
 */
@Component({
  selector: 'app-aggregations-tab',
  templateUrl: './aggregations-tab.component.html',
  styleUrls: ['./aggregations-tab.component.scss'],
})
export class AggregationsTabComponent
  extends UnsubscribeComponent
  implements OnInit
{
  public resource!: Resource;
  public aggregations: Aggregation[] = [];
  public loading = true;

  public displayedColumnsAggregations: string[] = [
    'name',
    'createdAt',
    '_actions',
  ];

  // ==== PAGINATION ====
  private aggregationsQuery!: QueryRef<ResourceQueryResponse>;
  private cachedAggregations: Aggregation[] = [];
  public pageInfo = {
    pageIndex: 0,
    pageSize: 10,
    length: 0,
    endCursor: '',
  };

  /**
   * Aggregations tab of resource page
   *
   * @param apollo Apollo service
   * @param dialog Dialog service
   * @param aggregationService Grid aggregation service
   * @param confirmService Shared confirm service
   * @param translate Angular translate service
   */
  constructor(
    private apollo: Apollo,
    private dialog: Dialog,
    private aggregationService: AggregationService,
    private confirmService: ConfirmService,
    private translate: TranslateService
  ) {
    super();
  }

  ngOnInit(): void {
    const state = history.state;
    this.resource = get(state, 'resource', null);

    this.aggregationsQuery = this.apollo.watchQuery<ResourceQueryResponse>({
      query: GET_RESOURCE_AGGREGATIONS,
      variables: {
        first: this.pageInfo.pageSize,
        id: this.resource?.id,
        afterCursor: this.pageInfo.endCursor,
      },
    });

    this.aggregationsQuery.valueChanges.subscribe(({ data, loading }) => {
      this.updateValues(data, loading);
    });
  }

  /**
   * Handles page event.
   *
   * @param e page event.
   */
  onPage(e: UIPageChangeEvent): void {
    const cachedData = handleTablePageEvent(
      e,
      this.pageInfo,
      this.cachedAggregations
    );
    if (cachedData && cachedData.length === this.pageInfo.pageSize) {
      this.aggregations = cachedData;
    } else {
      this.fetchAggregations();
    }
  }

  /**
   * Fetches aggregations from resource.
   *
   */
  private fetchAggregations(): void {
    this.loading = true;
    const variables = {
      id: this.resource.id,
      first: this.pageInfo.pageSize,
      afterCursor: this.pageInfo.endCursor,
    };
    const cachedValues: ResourceQueryResponse = getCachedValues(
      this.apollo.client,
      GET_RESOURCE_AGGREGATIONS,
      variables
    );
    if (cachedValues) {
      this.updateValues(cachedValues, false);
    } else {
      this.aggregationsQuery
        .fetchMore({
          variables,
        })
        .then((results) => this.updateValues(results.data, results.loading));
    }
  }

  /**
   * Handle action from the data list presentation component
   *
   * @param action action containing action type and aggregation item if exists
   * @param action.type type of action, add, edit, delete
   * @param action.item aggregation item for the given action type
   */
  handleAction(action: {
    type: 'add' | 'edit' | 'delete';
    item?: Aggregation | null;
  }) {
    if (action.type === 'add') {
      this.onAddAggregation();
    } else if (action.type === 'edit') {
      this.onEditAggregation(action.item as Aggregation);
    } else if (action.type === 'delete') {
      this.onDeleteAggregation(action.item as Aggregation);
    }
  }

  /**
   * Adds a new aggregation for the resource.
   */
  async onAddAggregation(): Promise<void> {
    const { EditAggregationModalComponent } = await import(
      '@oort-front/shared'
    );
    const dialogRef = this.dialog.open(EditAggregationModalComponent, {
      disableClose: true,
      data: {
        resource: this.resource,
      },
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.aggregationService
          .addAggregation(value, this.resource.id)
          .subscribe(({ data }: any) => {
            if (data.addAggregation) {
              this.aggregations = [...this.aggregations, data?.addAggregation];
              this.pageInfo.length += 1;
            }
          });
      }
    });
  }

  /**
   * Edits a aggregation. Opens a popup for edition.
   *
   * @param aggregation Aggregation to edit
   */
  async onEditAggregation(aggregation: Aggregation): Promise<void> {
    const { EditAggregationModalComponent } = await import(
      '@oort-front/shared'
    );
    const dialogRef = this.dialog.open(EditAggregationModalComponent, {
      disableClose: true,
      data: {
        resource: this.resource,
        aggregation,
      },
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.aggregationService
          .editAggregation(aggregation, value, this.resource.id)
          .subscribe(({ data }: any) => {
            if (data.editAggregation) {
              this.aggregations = this.aggregations.map((x: any) => {
                if (x.id === aggregation.id) {
                  return data.editAggregation;
                } else {
                  return x;
                }
              });
            }
          });
      }
    });
  }

  /**
   * Deletes a aggregation.
   *
   * @param aggregation Aggregation to delete
   */
  onDeleteAggregation(aggregation: Aggregation): void {
    const dialogRef = this.confirmService.openConfirmModal({
      title: this.translate.instant('common.deleteObject', {
        name: this.translate.instant('common.aggregation.one'),
      }),
      content: this.translate.instant(
        'components.form.aggregation.delete.confirmationMessage',
        {
          name: aggregation.name,
        }
      ),
      confirmText: this.translate.instant('components.confirmModal.delete'),
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.aggregationService
          .deleteAggregation(aggregation, this.resource.id)
          .subscribe(({ data }: any) => {
            if (data.deleteAggregation) {
              this.aggregations = this.aggregations.filter(
                (x: any) => x.id !== aggregation.id
              );
              this.pageInfo.length -= 1;
            }
          });
      }
    });
  }

  /**
   * Update aggregation data value
   *
   * @param data query response data
   * @param loading loading status
   */
  private updateValues(data: ResourceQueryResponse, loading: boolean) {
    if (data.resource) {
      const mappedValues =
        data.resource.aggregations?.edges.map((x) => x.node) ?? [];
      this.cachedAggregations = updateQueryUniqueValues(
        this.cachedAggregations,
        mappedValues
      );
      this.pageInfo.length = data.resource.aggregations?.totalCount || 0;
      this.pageInfo.endCursor =
        data.resource.aggregations?.pageInfo.endCursor || '';
      this.aggregations = this.cachedAggregations.slice(
        this.pageInfo.pageSize * this.pageInfo.pageIndex,
        this.pageInfo.pageSize * (this.pageInfo.pageIndex + 1)
      );
    }
    this.loading = loading;
  }
}

import { Component, OnInit } from '@angular/core';
import { AbstractControl, FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { TranslateService } from '@ngx-translate/core';
import {
  ApiConfiguration,
  authType,
  ApiProxyService,
  status,
  BreadcrumbService,
  UnsubscribeComponent,
  ApiConfigurationQueryResponse,
  EditApiConfigurationMutationResponse,
} from '@oort-front/shared';
import { Apollo } from 'apollo-angular';
import { takeUntil } from 'rxjs/operators';
import { apiValidator } from '../../../utils/nameValidation';
import { EDIT_API_CONFIGURATION } from './graphql/mutations';
import { GET_API_CONFIGURATION } from './graphql/queries';
import { SnackbarService } from '@oort-front/ui';

/**
 * Default value shown for private settings fields
 */
const ENCRYPTED_VALUE = '●●●●●●●●●●●●●';

/**
 * API configuration page component.
 */
@Component({
  selector: 'app-api-configuration',
  templateUrl: './api-configuration.component.html',
  styleUrls: ['./api-configuration.component.scss'],
})
export class ApiConfigurationComponent
  extends UnsubscribeComponent
  implements OnInit
{
  /** Loading indicator */
  public loading = true;
  /** Api configuration id */
  public id = '';
  /** Edited api configuration */
  public apiConfiguration?: ApiConfiguration;
  /** Api form group */
  public apiForm!: ReturnType<typeof this.createApiForm>;
  /** Reference to status enum */
  public status = status;
  /** Available status choices */
  public statusChoices = Object.values(status);
  /** Reference to auth type enum */
  public authType = authType;
  /** Available auth types */
  public authTypeChoices = Object.values(authType);

  /** @returns API configuration name */
  get name(): AbstractControl | null {
    return this.apiForm.get('name');
  }

  /**
   * API configuration page component
   *
   * @param apollo Apollo service
   * @param route Angular activated route
   * @param snackBar Shared snackbar service
   * @param router Angular router
   * @param fb Angular form builder
   * @param apiProxy Shared API proxy service
   * @param translate Angular translate service
   * @param breadcrumbService Shared breadcrumb service
   */
  constructor(
    private apollo: Apollo,
    private route: ActivatedRoute,
    private snackBar: SnackbarService,
    private router: Router,
    private fb: FormBuilder,
    private apiProxy: ApiProxyService,
    private translate: TranslateService,
    private breadcrumbService: BreadcrumbService
  ) {
    super();
  }

  ngOnInit(): void {
    this.id = this.route.snapshot.paramMap.get('id') || '';
    if (this.id) {
      this.apollo
        .watchQuery<ApiConfigurationQueryResponse>({
          query: GET_API_CONFIGURATION,
          variables: {
            id: this.id,
          },
        })
        .valueChanges.pipe(takeUntil(this.destroy$))
        .subscribe({
          next: ({ data, loading }) => {
            if (data.apiConfiguration) {
              this.apiConfiguration = data.apiConfiguration;
              this.breadcrumbService.setBreadcrumb(
                '@api',
                this.apiConfiguration.name as string
              );
              this.apiForm = this.createApiForm(data.apiConfiguration);
              this.apiForm.controls.authType?.valueChanges
                .pipe(takeUntil(this.destroy$))
                .subscribe((value) => {
                  this.resetFormSettings(value);
                });
              this.loading = loading;
            } else {
              this.snackBar.openSnackBar(
                this.translate.instant(
                  'common.notifications.accessNotProvided',
                  {
                    type: this.translate
                      .instant('common.resource.one')
                      .toLowerCase(),
                    error: '',
                  }
                ),
                { error: true }
              );
              this.router.navigate(['/settings/apiconfigurations']);
            }
          },
          error: (err) => {
            this.snackBar.openSnackBar(err.message, { error: true });
            this.router.navigate(['/settings/apiconfigurations']);
          },
        });
    }
  }

  /**
   * Create API form
   *
   * @param api API configuration
   * @returns form group
   */
  private createApiForm(api: ApiConfiguration) {
    return this.fb.group({
      id: [{ value: api.id, disabled: true }],
      name: [api.name, [Validators.required, Validators.pattern(apiValidator)]],
      status: [api.status, Validators.required],
      authType: this.fb.nonNullable.control(
        api.authType || '',
        Validators.required
      ),
      endpoint: [api.endpoint, Validators.required],
      pingUrl: [api.pingUrl],
      settings: this.buildSettingsForm(api.authType || ''),
      graphQLEndpoint: api.graphQLEndpoint,
    });
  }

  /**
   * Reset settings configuration form with the given API configuration
   *
   * @param authType current auth type of the API configuration
   */
  private resetFormSettings(authType: string) {
    (this.apiForm as any).removeControl('settings', { emitEvent: false });
    this.apiForm.addControl('settings', this.buildSettingsForm(authType));
    this.apiForm.updateValueAndValidity();
  }

  /**
   * Create the settings form depending on the authType
   *
   * @param type type of API connection
   * @returns settings form group
   */
  private buildSettingsForm(type: string) {
    if (type === authType.serviceToService) {
      return this.fb.group({
        authTargetUrl: [
          this.apiConfiguration?.settings &&
          this.apiConfiguration?.settings.authTargetUrl
            ? ENCRYPTED_VALUE
            : '',
          Validators.required,
        ],
        apiClientID: [
          this.apiConfiguration?.settings &&
          this.apiConfiguration?.settings.apiClientID
            ? ENCRYPTED_VALUE
            : '',
          Validators.minLength(3),
        ],
        safeSecret: [
          this.apiConfiguration?.settings &&
          this.apiConfiguration?.settings.safeSecret
            ? ENCRYPTED_VALUE
            : '',
          Validators.minLength(3),
        ],
        scope: [
          this.apiConfiguration?.settings &&
          this.apiConfiguration?.settings.scope
            ? ENCRYPTED_VALUE
            : '',
          null,
        ],
      });
    } else if (type === authType.userToService) {
      return this.fb.group({
        token: [
          this.apiConfiguration?.settings &&
          this.apiConfiguration?.settings.token
            ? ENCRYPTED_VALUE
            : '',
          Validators.required,
        ],
      });
    }
    return this.fb.group({});
  }

  /**
   * Edit the permissions layer.
   *
   * @param e permissions
   */
  saveAccess(e: any): void {
    this.loading = true;
    this.apollo
      .mutate<EditApiConfigurationMutationResponse>({
        mutation: EDIT_API_CONFIGURATION,
        variables: {
          id: this.id,
          permissions: e,
        },
      })
      .subscribe(({ errors, data, loading }) => {
        if (errors) {
          this.snackBar.openSnackBar(
            this.translate.instant('common.notifications.objectNotUpdated', {
              type: this.translate.instant('common.apiConfiguration.one'),
              error: errors ? errors[0].message : '',
            }),
            { error: true }
          );
        } else {
          if (data) {
            this.apiConfiguration = data.editApiConfiguration;
          }
        }
        this.loading = loading;
      });
  }

  /** Edit the API Configuration using apiForm changes */
  onSave(): void {
    this.loading = true;
    const variables = { id: this.id };
    Object.assign(
      variables,
      this.apiForm.value.name !== this.apiConfiguration?.name && {
        name: this.apiForm.value.name,
      },
      this.apiForm.value.status !== this.apiConfiguration?.status && {
        status: this.apiForm.value.status,
      },
      this.apiForm.value.authType !== this.apiConfiguration?.authType && {
        authType: this.apiForm.value.authType,
      },
      this.apiForm.value.endpoint !== this.apiConfiguration?.endpoint && {
        endpoint: this.apiForm.value.endpoint,
      },
      this.apiForm.value.graphQLEndpoint !==
        this.apiConfiguration?.graphQLEndpoint && {
        graphQLEndpoint: this.apiForm.value.graphQLEndpoint,
      },
      this.apiForm.value.pingUrl !== this.apiConfiguration?.pingUrl && {
        pingUrl: this.apiForm.value.pingUrl,
      },
      // If settings is touched we will go through each settings param to save only the ones that are not the encrypted display value and that exist
      this.apiForm.controls.settings.touched && {
        settings: {
          ...(this.apiForm.value.authType === authType.serviceToService &&
            this.apiForm.value.settings?.authTargetUrl !== ENCRYPTED_VALUE && {
              authTargetUrl: this.apiForm.value.settings?.authTargetUrl,
            }),
          ...(this.apiForm.value.authType === authType.serviceToService &&
            this.apiForm.value.settings?.apiClientID !== ENCRYPTED_VALUE && {
              apiClientID: this.apiForm.value.settings?.apiClientID,
            }),
          ...(this.apiForm.value.authType === authType.serviceToService &&
            this.apiForm.value.settings?.safeSecret !== ENCRYPTED_VALUE && {
              safeSecret: this.apiForm.value.settings?.safeSecret,
            }),
          ...(this.apiForm.value.authType === authType.serviceToService &&
            this.apiForm.value.settings?.scope !== ENCRYPTED_VALUE && {
              scope: this.apiForm.value.settings?.scope,
            }),
          ...(this.apiForm.value.authType === authType.userToService &&
            this.apiForm.value.settings?.token !== ENCRYPTED_VALUE && {
              token: this.apiForm.value.settings?.token,
            }),
        },
      }
    );
    this.apollo
      .mutate<EditApiConfigurationMutationResponse>({
        mutation: EDIT_API_CONFIGURATION,
        variables,
      })
      .subscribe(({ errors, data, loading }) => {
        if (errors) {
          this.snackBar.openSnackBar(
            this.translate.instant('common.notifications.objectNotUpdated', {
              type: this.translate.instant('common.apiConfiguration.one'),
              error: errors ? errors[0].message : '',
            }),
            { error: true }
          );
        } else {
          this.apiConfiguration = data?.editApiConfiguration;
          this.resetFormSettings(this.apiConfiguration?.authType as string);
          this.loading = loading || false;
        }
        this.loading = loading;
      });
  }

  /** Send a ping request to test the configuration */
  onPing(): void {
    this.apiProxy
      .buildPingRequest(this.apiForm.getRawValue() as ApiConfiguration)
      ?.subscribe(
        (res: any) => {
          if (res) {
            if (res.access_token) {
              this.snackBar.openSnackBar(
                this.translate.instant(
                  'pages.apiConfiguration.notifications.authTokenFetched'
                )
              );
            } else {
              this.snackBar.openSnackBar(
                this.translate.instant(
                  'pages.apiConfiguration.notifications.pingReceived'
                )
              );
            }
          } else {
            this.snackBar.openSnackBar(
              this.translate.instant(
                'pages.apiConfiguration.notifications.pingFailed'
              ),
              { error: true }
            );
          }
        },
        () => {
          this.snackBar.openSnackBar(
            this.translate.instant(
              'pages.apiConfiguration.notifications.pingFailed'
            ),
            { error: true }
          );
        }
      );
  }

  /**
   * Clear the value of the given control, if not updated by the user
   *
   * @param key control key from settings control to clear
   */
  clearControl(key: string) {
    const control = this.apiForm.get(key);
    if (control && control.pristine) {
      control.setValue('');
    }
  }
}

import { Component, OnInit } from '@angular/core';
import { Dialog } from '@angular/cdk/dialog';
import { Apollo, QueryRef } from 'apollo-angular';
import {
  AddApiConfigurationMutationResponse,
  ApiConfiguration,
  ConfirmService,
  UnsubscribeComponent,
  ApiConfigurationsQueryResponse,
  DeleteApiConfigurationMutationResponse,
} from '@oort-front/shared';
import {
  ADD_API_CONFIGURATION,
  DELETE_API_CONFIGURATION,
} from './graphql/mutations';
import { Router } from '@angular/router';
import { TranslateService } from '@ngx-translate/core';
import { takeUntil } from 'rxjs/operators';
import {
  getCachedValues,
  updateQueryUniqueValues,
} from '../../../utils/update-queries';
import { ApolloQueryResult } from '@apollo/client';
import {
  TableSort,
  UIPageChangeEvent,
  handleTablePageEvent,
} from '@oort-front/ui';
import { SnackbarService } from '@oort-front/ui';
import { GET_API_CONFIGURATIONS } from './graphql/queries';
import { FormBuilder } from '@angular/forms';

/** Default items per page for pagination. */
const ITEMS_PER_PAGE = 10;

/**
 * API configurations page component.
 */
@Component({
  selector: 'app-api-configurations',
  templateUrl: './api-configurations.component.html',
  styleUrls: ['./api-configurations.component.scss'],
})
export class ApiConfigurationsComponent
  extends UnsubscribeComponent
  implements OnInit
{
  // === DATA ===
  public loading = true;
  private apiConfigurationsQuery!: QueryRef<ApiConfigurationsQueryResponse>;
  displayedColumns = ['name', 'status', 'authType', 'actions'];
  dataSource = new Array<ApiConfiguration>();
  filteredDataSources = new Array<ApiConfiguration>();
  public cachedApiConfigurations: ApiConfiguration[] = [];

  // === SORTING ===
  sort?: TableSort;

  // === FILTERS ===
  form = this.fb.group({});
  public showFilters = false;
  public searchText = '';
  public statusFilter = '';

  public pageInfo = {
    pageIndex: 0,
    pageSize: ITEMS_PER_PAGE,
    length: 0,
    endCursor: '',
  };

  /**
   * API configurations page component
   *
   * @param apollo Apollo service
   * @param dialog Dialog service
   * @param snackBar Shared snackbar service
   * @param confirmService Shared confirm service
   * @param router Angular router
   * @param translate Angular translate service
   * @param fb Angular Form Builder
   */
  constructor(
    private apollo: Apollo,
    public dialog: Dialog,
    private snackBar: SnackbarService,
    private confirmService: ConfirmService,
    private router: Router,
    private translate: TranslateService, // private uiTableWrapper: TableWrapperDirective
    private fb: FormBuilder
  ) {
    super();
  }

  /**
   * Creates the API configuration query, and subscribes to the query changes.
   */
  ngOnInit(): void {
    this.apiConfigurationsQuery =
      this.apollo.watchQuery<ApiConfigurationsQueryResponse>({
        query: GET_API_CONFIGURATIONS,
        variables: {
          first: ITEMS_PER_PAGE,
          afterCursor: this.pageInfo.endCursor,
        },
      });

    this.apiConfigurationsQuery.valueChanges
      .pipe(takeUntil(this.destroy$))
      .subscribe((results) => {
        this.updateValues(results.data, results.loading);
      });
    // Initializing sort to an empty one
    this.sort = {
      active: '',
      sortDirection: '',
    };
  }

  /**
   * Handles page event.
   *
   * @param e page event.
   */
  onPage(e: UIPageChangeEvent): void {
    const cachedData = handleTablePageEvent(
      e,
      this.pageInfo,
      this.cachedApiConfigurations
    );
    if (cachedData && cachedData.length === this.pageInfo.pageSize) {
      this.dataSource = cachedData;
      this.filteredDataSources = this.dataSource;
    } else {
      this.fetchApiConfigurations();
    }
  }

  /**
   * Frontend filtering.
   */
  private filterPredicate(): void {
    this.filteredDataSources = this.dataSource.filter(
      (data: any) =>
        (this.searchText.trim().length === 0 ||
          (this.searchText.trim().length > 0 &&
            data.name.toLowerCase().includes(this.searchText.trim()))) &&
        (this.statusFilter.trim().length === 0 ||
          (this.statusFilter.trim().length > 0 &&
            data.status.toLowerCase().includes(this.statusFilter.trim())))
    );
  }

  /**
   * Applies the filter to the data source.
   *
   * @param event event value of the filter.
   */
  applyFilter(event: any): void {
    if (event.statusFilter) {
      this.statusFilter = event.statusFilter;
    } else {
      this.statusFilter = '';
    }
    if (event.search) {
      this.searchText = event.search.toLowerCase();
    } else {
      this.searchText = '';
    }
    this.filterPredicate();
  }

  /**
   * Displays the AddApiConfiguration modal.
   * Creates a new apiConfiguration on closed if result.
   */
  async onAdd(): Promise<void> {
    const { AddApiConfigurationComponent } = await import(
      './components/add-api-configuration/add-api-configuration.component'
    );
    const dialogRef = this.dialog.open(AddApiConfigurationComponent);
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.apollo
          .mutate<AddApiConfigurationMutationResponse>({
            mutation: ADD_API_CONFIGURATION,
            variables: {
              name: value.name,
            },
          })
          .subscribe({
            next: ({ errors, data }) => {
              if (errors) {
                this.snackBar.openSnackBar(
                  this.translate.instant(
                    'common.notifications.objectNotCreated',
                    {
                      type: this.translate
                        .instant('common.apiConfiguration.one')
                        .toLowerCase(),
                      error: errors ? errors[0].message : '',
                    }
                  ),
                  { error: true }
                );
              } else {
                this.snackBar.openSnackBar(
                  this.translate.instant('common.notifications.objectCreated', {
                    type: this.translate
                      .instant('common.apiConfiguration.one')
                      .toLowerCase(),
                    value: data?.addApiConfiguration.name,
                  })
                );
                if (data) {
                  this.router.navigate([
                    '/settings/apiconfigurations',
                    data.addApiConfiguration.id,
                  ]);
                }
              }
            },
            error: (err) => {
              this.snackBar.openSnackBar(err.message, { error: true });
            },
          });
      }
    });
  }

  /**
   * Removes an apiConfiguration if authorized.
   *
   * @param element API config to delete.
   * @param e click event.
   */
  onDelete(element: any, e: any): void {
    e.stopPropagation();
    const dialogRef = this.confirmService.openConfirmModal({
      title: this.translate.instant('components.apiConfiguration.delete.title'),
      content: this.translate.instant(
        'components.apiConfiguration.delete.confirmationMessage',
        {
          name: element.name,
        }
      ),
      confirmText: this.translate.instant('components.confirmModal.delete'),
      confirmVariant: 'danger',
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.apollo
          .mutate<DeleteApiConfigurationMutationResponse>({
            mutation: DELETE_API_CONFIGURATION,
            variables: {
              id: element.id,
            },
          })
          .subscribe({
            next: (res) => {
              if (res && !res.errors) {
                this.snackBar.openSnackBar(
                  this.translate.instant('common.notifications.objectDeleted', {
                    value: this.translate.instant(
                      'common.apiConfiguration.one'
                    ),
                  })
                );
                this.dataSource = this.dataSource.filter(
                  (x) => x.id !== element.id
                );
                this.filteredDataSources = this.dataSource;
              } else {
                this.snackBar.openSnackBar(
                  this.translate.instant(
                    'common.notifications.objectNotDeleted',
                    {
                      value: this.translate.instant(
                        'common.apiConfiguration.one'
                      ),
                      error: res.errors ? res.errors[0].message : '',
                    }
                  ),
                  { error: true }
                );
              }
            },
            error: (err) => {
              this.snackBar.openSnackBar(err.message, { error: true });
            },
          });
      }
    });
  }

  /**
   * Updates local list with given data
   *
   * @param data New values to update forms
   * @param loading Loading state
   */
  private updateValues(
    data: ApiConfigurationsQueryResponse,
    loading: boolean
  ): void {
    const mappedValues = data.apiConfigurations.edges.map((x) => x.node);
    this.cachedApiConfigurations = updateQueryUniqueValues(
      this.cachedApiConfigurations,
      mappedValues
    );
    // then slice the array to correctly match the items in the current page
    this.dataSource = this.cachedApiConfigurations.slice(
      this.pageInfo.pageSize * this.pageInfo.pageIndex,
      this.pageInfo.pageSize * (this.pageInfo.pageIndex + 1)
    );
    this.pageInfo.length = data.apiConfigurations.totalCount;
    this.pageInfo.endCursor = data.apiConfigurations.pageInfo.endCursor;
    this.loading = loading;
    this.filterPredicate();
  }

  /**
   * Handle sort change.
   *
   * @param event sort event
   */
  onSort(event: TableSort): void {
    // We change the sort for the current value
    this.sort = event;
    this.fetchApiConfigurations(true);
  }

  /**
   * Update api configurations query.
   *
   * @param refetch erase previous query results
   */
  private fetchApiConfigurations(refetch?: boolean): void {
    this.loading = true;
    const variables = {
      first: this.pageInfo.pageSize,
      afterCursor: refetch ? null : this.pageInfo.endCursor,
      sortField: this.sort?.sortDirection && this.sort.active,
      sortOrder: this.sort?.sortDirection,
    };
    const cachedValues: ApiConfigurationsQueryResponse = getCachedValues(
      this.apollo.client,
      GET_API_CONFIGURATIONS,
      variables
    );
    if (refetch) {
      this.cachedApiConfigurations = [];
      this.pageInfo.pageIndex = 0;
    }
    if (cachedValues) {
      this.updateValues(cachedValues, false);
    } else {
      if (refetch) {
        // Rebuild the query
        this.apiConfigurationsQuery.refetch(variables);
      } else {
        // Fetch more records
        this.apiConfigurationsQuery
          .fetchMore({
            variables,
          })
          .then(
            (results: ApolloQueryResult<ApiConfigurationsQueryResponse>) => {
              this.updateValues(results.data, results.loading);
            }
          );
      }
    }
  }
}

