import { Component, HostListener, OnDestroy, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { TranslateService } from '@ngx-translate/core';
import {
  Application,
  ContentType,
  ApplicationService,
  ConfirmService,
  UnsubscribeComponent,
} from '@oort-front/shared';
import get from 'lodash/get';
import { takeUntil, map } from 'rxjs/operators';
import { Observable } from 'rxjs';

/**
 * Main component of Application view.
 */
@Component({
  selector: 'app-application',
  templateUrl: './application.component.html',
  styleUrls: ['./application.component.scss'],
})
export class ApplicationComponent
  extends UnsubscribeComponent
  implements OnInit, OnDestroy
{
  /** Application title */
  public title = '';
  /** Navigation groups */
  public navGroups: any[] = [];
  /** Admin pages */
  public adminNavItems: any[] = [];
  /** Current application */
  public application?: Application;
  /** Use side menu or not */
  public sideMenu = false;
  /** Is large device */
  public largeDevice: boolean;
  /** Loading indicator */
  public loading = true;

  /**
   * Main component of application view
   *
   * @param applicationService Shared application service
   * @param route Angular activated route
   * @param router Angular router
   * @param translate Angular translate service
   * @param confirmService Shared confirmation service
   */
  constructor(
    private applicationService: ApplicationService,
    public route: ActivatedRoute,
    private router: Router,
    private translate: TranslateService,
    private confirmService: ConfirmService
  ) {
    super();
    this.largeDevice = window.innerWidth > 1024;
  }

  ngOnInit(): void {
    this.route.params.pipe(takeUntil(this.destroy$)).subscribe((params) => {
      this.loading = true;
      this.applicationService.loadApplication(params.id);
    });
    this.applicationService.application$
      .pipe(takeUntil(this.destroy$))
      .subscribe((application: Application | null) => {
        if (application) {
          this.loading = false;
          this.title = application.name || '';
          const displayNavItems: any[] =
            application.pages
              ?.filter((x: any) => x.content && x.canSee)
              .map((x: any) => ({
                id: x.id,
                name: x.name,
                path:
                  x.type === ContentType.form
                    ? `./${x.type}/${x.id}`
                    : `./${x.type}/${x.content}`,
                icon: x.icon || this.getNavIcon(x.type || ''),
                fontFamily: x.icon ? 'fa' : 'material',
                class: null,
                orderable: true,
                visible: x.visible ?? true,
                action: x.canDelete && {
                  icon: 'delete',
                  toolTip: this.translate.instant('common.deleteObject', {
                    name: this.translate
                      .instant('common.page.one')
                      .toLowerCase(),
                  }),
                  callback: () => this.onDelete(x),
                },
              })) || [];
          if (application.canUpdate) {
            this.adminNavItems = [
              {
                name: this.translate.instant('common.settings'),
                path: './settings/edit',
                icon: 'settings',
              },
              {
                name: this.translate.instant('common.template.few'),
                path: './settings/templates',
                icon: 'description',
              },
              {
                name: this.translate.instant('common.distributionList.few'),
                path: './settings/distribution-lists',
                icon: 'mail',
              },
              {
                name: this.translate.instant('common.customNotification.few'),
                path: './settings/notifications',
                icon: 'schedule_send',
              },
              {
                name: this.translate.instant('common.user.few'),
                path: './settings/users',
                icon: 'supervisor_account',
              },
              {
                name: this.translate.instant('common.role.few'),
                path: './settings/roles',
                icon: 'verified_user',
              },
              {
                name: this.translate.instant(
                  'pages.application.positionAttributes.title'
                ),
                path: './settings/position',
                icon: 'edit_attributes',
              },
              {
                name: this.translate.instant('common.channel.few'),
                path: './settings/channels',
                icon: 'dns',
              },
              {
                name: this.translate.instant('common.subscription.few'),
                path: './settings/subscriptions',
                icon: 'add_to_queue',
              },
            ];
          }
          if (application.canUpdate) {
            this.adminNavItems.push({
              name: this.translate.instant('common.archive.few'),
              path: './settings/archive',
              icon: 'delete',
            });
          }
          this.navGroups = [
            {
              name: this.translate.instant('common.page.few'),
              navItems: displayNavItems,
            },
          ];
          if (!this.application || application.id !== this.application.id) {
            const firstPage = get(application, 'pages', [])[0];
            if (this.router.url.endsWith(application?.id || '') || !firstPage) {
              if (firstPage) {
                this.router.navigate(
                  [
                    `./${firstPage.type}/${
                      firstPage.type === ContentType.form
                        ? firstPage.id
                        : firstPage.content
                    }`,
                  ],
                  { relativeTo: this.route }
                );
              } else {
                this.router.navigate([`./`], { relativeTo: this.route });
              }
            }
          }
          this.application = application;
          this.sideMenu = this.application?.sideMenu ?? false;
        } else {
          this.title = '';
          this.navGroups = [];
        }
      });
  }

  /**
   * Get icons from type of page
   *
   * @param type type of page
   * @returns icon as string
   */
  private getNavIcon(type: string): string {
    switch (type) {
      case 'workflow':
        return 'linear_scale';
      case 'form':
        return 'description';
      default:
        return 'dashboard';
    }
  }

  /**
   * Change the display depending on windows size.
   *
   * @param event Event that implies a change in window size
   */
  @HostListener('window:resize', ['$event'])
  onResize(event: any): void {
    this.largeDevice = event.target.innerWidth > 1024;
  }

  /**
   * Delete item, prompt for confirmation
   *
   * @param item item to delete
   */
  onDelete(item: any): void {
    const dialogRef = this.confirmService.openConfirmModal({
      title: this.translate.instant('common.deleteObject', {
        name: this.translate.instant('common.page.one'),
      }),
      content: this.translate.instant(
        'components.application.pages.delete.confirmationMessage',
        { name: item.name }
      ),
      confirmText: this.translate.instant('components.confirmModal.delete'),
      confirmVariant: 'danger',
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.applicationService.deletePage(item.id);
      }
    });
  }

  /**
   * Reorder pages
   *
   * @param event Reorder event
   */
  onReorder(event: any): void {
    this.applicationService.reorderPages(
      event.filter((x: any) => x.id).map((x: any) => x.id)
    );
  }

  /**
   * Show modal confirmation before leave the page if has changes on custom styling
   *
   * @returns boolean of observable of boolean
   */
  canDeactivate(): Observable<boolean> | boolean {
    if (this.applicationService.customStyleEdited) {
      const dialogRef = this.confirmService.openConfirmModal({
        title: this.translate.instant('components.form.update.exit'),
        content: this.translate.instant(
          'components.widget.settings.close.confirmationMessage'
        ),
        confirmText: this.translate.instant('components.confirmModal.confirm'),
        confirmVariant: 'primary',
      });
      return dialogRef.closed.pipe(
        map((confirm) => {
          if (confirm) {
            return true;
          }
          return false;
        })
      );
    }
    return true;
  }

  /**
   * Remove application data such as styling when existing application edition.
   */
  override ngOnDestroy(): void {
    super.ngOnDestroy();
    this.applicationService.leaveApplication();
  }
}

import { Component, Input, OnInit } from '@angular/core';
import { Dialog } from '@angular/cdk/dialog';
import { TranslateService } from '@ngx-translate/core';
import { takeUntil } from 'rxjs/operators';
import { SnackbarService } from '@oort-front/ui';
import {
  Application,
  ApplicationService,
  ConfirmService,
  UnsubscribeComponent,
} from '@oort-front/shared';

/**
 * Header component visible when editing application.
 * Appear on top of the view.
 */
@Component({
  selector: 'app-application-header',
  templateUrl: './application-header.component.html',
  styleUrls: ['./application-header.component.scss'],
})
export class ApplicationHeaderComponent
  extends UnsubscribeComponent
  implements OnInit
{
  /** Application title */
  @Input() title = '';
  /** Admin routes */
  @Input() navItems: any[] = [];
  /** Can user update application */
  @Input() canUpdate = false;
  /** Current application */
  public application: Application | null = null;
  /** Is edition of application locked */
  public locked: boolean | undefined = undefined;
  /** User that locked edition */
  public lockedByUser: boolean | undefined = undefined;
  /** Can application be published */
  public canPublish = false;

  /**
   * Header component visible when editing application
   *
   * @param applicationService Shared application service
   * @param dialog Dialog service
   * @param snackBar Shared snackbar service
   * @param confirmService Shared confirm service
   * @param translate Angular translate service
   */
  constructor(
    private applicationService: ApplicationService,
    public dialog: Dialog,
    private snackBar: SnackbarService,
    private confirmService: ConfirmService,
    private translate: TranslateService
  ) {
    super();
  }

  ngOnInit(): void {
    this.applicationService.application$
      .pipe(takeUntil(this.destroy$))
      .subscribe((application: Application | null) => {
        this.application = application;
        this.locked = this.application?.locked;
        this.lockedByUser = this.application?.lockedByUser;
        this.canPublish =
          !!this.application && this.application.pages
            ? this.application.pages.length > 0
            : false;
      });
  }

  /**
   * Unlocks the application, and controls edition.
   */
  onUnlock(): void {
    const dialogRef = this.confirmService.openConfirmModal({
      title: this.translate.instant('components.application.unlock.title'),
      content: this.translate.instant(
        'components.application.unlock.confirmationMessage',
        {
          name: this.application?.name,
        }
      ),
      confirmVariant: 'primary',
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe(() => {
      this.applicationService.toggleApplicationLock();
    });
  }

  /**
   * Confirms publication of application, and makes it active.
   */
  onPublish(): void {
    if (this.locked && !this.lockedByUser) {
      this.snackBar.openSnackBar(
        this.translate.instant('common.notifications.objectLocked', {
          name: this.application?.name,
        })
      );
    } else {
      const dialogRef = this.confirmService.openConfirmModal({
        title: this.translate.instant('components.application.publish.title'),
        content: this.translate.instant(
          'components.application.publish.confirmationMessage',
          {
            name: this.application?.name,
          }
        ),
        confirmVariant: 'primary',
      });
      dialogRef.closed
        .pipe(takeUntil(this.destroy$))
        .subscribe((value: any) => {
          if (value) {
            this.applicationService.publish();
          }
        });
    }
  }
}

import { Apollo, QueryRef } from 'apollo-angular';
import { Component, OnInit } from '@angular/core';
import { Dialog } from '@angular/cdk/dialog';
import { Router } from '@angular/router';
import {
  AddApplicationMutationResponse,
  Application,
  ConfirmService,
  UnsubscribeComponent,
  ApplicationsApplicationNodesQueryResponse,
  DeleteApplicationMutationResponse,
  EditApplicationMutationResponse,
} from '@oort-front/shared';
import {
  DELETE_APPLICATION,
  ADD_APPLICATION,
  EDIT_APPLICATION,
} from './graphql/mutations';
import { PreviewService } from '../../../services/preview.service';
import { TranslateService } from '@ngx-translate/core';
import { takeUntil } from 'rxjs/operators';
import { ApolloQueryResult } from '@apollo/client';
import {
  getCachedValues,
  updateQueryUniqueValues,
} from '../../../utils/update-queries';
import {
  TableSort,
  UIPageChangeEvent,
  handleTablePageEvent,
} from '@oort-front/ui';
import { SnackbarService } from '@oort-front/ui';
import { GET_APPLICATIONS } from './graphql/queries';

/** Default number of items per request for pagination */
const DEFAULT_PAGE_SIZE = 10;

/** Applications page component. */
@Component({
  selector: 'app-applications',
  templateUrl: './applications.component.html',
  styleUrls: ['./applications.component.scss'],
})
export class ApplicationsComponent
  extends UnsubscribeComponent
  implements OnInit
{
  // === DATA ===
  public loading = true;
  public updating = false;
  private applicationsQuery!: QueryRef<ApplicationsApplicationNodesQueryResponse>;
  public applications = new Array<Application>();
  public cachedApplications: Application[] = [];
  public displayedColumns = [
    'name',
    'createdAt',
    'status',
    'usersCount',
    'actions',
  ];
  public newApplications: Application[] = [];
  public filter: any = {
    filters: [],
    logic: 'and',
  };
  private sort: TableSort = { active: '', sortDirection: '' };

  public pageInfo = {
    pageIndex: 0,
    pageSize: DEFAULT_PAGE_SIZE,
    length: 0,
    endCursor: '',
  };

  /**
   * Applications page component
   *
   * @param apollo Apollo service
   * @param dialog Dialog service
   * @param router Angular router
   * @param snackBar Shared snackbar service
   * @param previewService Shared preview service
   * @param confirmService Share confirm service
   * @param translate Angular translate service
   */
  constructor(
    private apollo: Apollo,
    public dialog: Dialog,
    private router: Router,
    private snackBar: SnackbarService,
    private previewService: PreviewService,
    private confirmService: ConfirmService,
    private translate: TranslateService
  ) {
    super();
  }

  /**
   * Creates the application query and subscribes to the query changes.
   */
  ngOnInit(): void {
    this.applicationsQuery =
      this.apollo.watchQuery<ApplicationsApplicationNodesQueryResponse>({
        query: GET_APPLICATIONS,
        variables: {
          first: DEFAULT_PAGE_SIZE,
          afterCursor: null,
          filter: this.filter,
          sortField: this.sort?.sortDirection && this.sort.active,
          sortOrder: this.sort?.sortDirection,
        },
      });
    this.apollo
      .query<ApplicationsApplicationNodesQueryResponse>({
        query: GET_APPLICATIONS,
        fetchPolicy: 'no-cache',
        variables: {
          first: 5,
          sortField: 'modifiedAt',
          sortOrder: 'desc',
        },
      })
      .pipe(takeUntil(this.destroy$))
      .subscribe(({ data }) => {
        this.newApplications = data.applications.edges.map((x) => x.node);
      });
    this.applicationsQuery.valueChanges
      .pipe(takeUntil(this.destroy$))
      .subscribe(
        (
          results: ApolloQueryResult<ApplicationsApplicationNodesQueryResponse>
        ) => {
          this.updateValues(results.data, results.loading);
        }
      );
  }

  /**
   * Handles page event.
   *
   * @param e page event.
   */
  onPage(e: UIPageChangeEvent): void {
    const cachedData = handleTablePageEvent(
      e,
      this.pageInfo,
      this.cachedApplications
    );
    if (cachedData && cachedData.length === this.pageInfo.pageSize) {
      this.applications = cachedData;
    } else {
      this.fetchApplications();
    }
  }

  /**
   * Filters applications and updates table.
   *
   * @param filter filter event.
   */
  onFilter(filter: any): void {
    this.filter = filter;
    this.fetchApplications(true);
  }

  /**
   * Handle sort change.
   *
   * @param event sort event
   */
  onSort(event: TableSort): void {
    this.sort = event;
    this.fetchApplications(true);
  }

  /**
   * Update applications query.
   *
   * @param refetch erase previous query results
   */
  private fetchApplications(refetch?: boolean): void {
    this.updating = true;
    const variables = {
      first: this.pageInfo.pageSize,
      afterCursor: refetch ? null : this.pageInfo.endCursor,
      filter: this.filter,
      sortField: this.sort?.sortDirection && this.sort.active,
      sortOrder: this.sort?.sortDirection,
    };
    const cachedValues: ApplicationsApplicationNodesQueryResponse =
      getCachedValues(this.apollo.client, GET_APPLICATIONS, variables);
    if (refetch) {
      this.cachedApplications = [];
      this.pageInfo.pageIndex = 0;
    }
    if (cachedValues) {
      this.updateValues(cachedValues, false);
    } else {
      if (refetch) {
        // Rebuild the query
        this.applicationsQuery.refetch(variables);
      } else {
        // Fetch more records
        this.applicationsQuery
          .fetchMore({
            variables,
          })
          .then(
            (
              results: ApolloQueryResult<ApplicationsApplicationNodesQueryResponse>
            ) => {
              this.updateValues(results.data, results.loading);
            }
          );
      }
    }
  }

  /**
   * Deletes an application if authorized.
   *
   * @param element application.
   * @param e click event.
   */
  onDelete(element: any, e?: any): void {
    if (e) {
      e.stopPropagation();
    }
    const dialogRef = this.confirmService.openConfirmModal({
      title: this.translate.instant('common.deleteObject', {
        name: this.translate.instant('common.application.one'),
      }),
      content: this.translate.instant(
        'components.application.delete.confirmationMessage',
        { name: element.name }
      ),
      confirmText: this.translate.instant('components.confirmModal.delete'),
      confirmVariant: 'danger',
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        const id = element.id;
        this.apollo
          .mutate<DeleteApplicationMutationResponse>({
            mutation: DELETE_APPLICATION,
            variables: {
              id,
            },
          })
          .subscribe({
            next: ({ errors, data }) => {
              if (errors) {
                this.snackBar.openSnackBar(
                  this.translate.instant(
                    'common.notifications.objectNotDeleted',
                    {
                      value: this.translate.instant('common.application.one'),
                      error: errors ? errors[0].message : '',
                    }
                  ),
                  { error: true }
                );
              } else {
                this.snackBar.openSnackBar(
                  this.translate.instant('common.notifications.objectDeleted', {
                    value: this.translate.instant('common.application.one'),
                  })
                );
                this.applications = this.applications.filter(
                  (x) => x.id !== data?.deleteApplication.id
                );
                this.newApplications = this.newApplications.filter(
                  (x) => x.id !== data?.deleteApplication.id
                );
              }
            },
            error: (err) => {
              this.snackBar.openSnackBar(err.message, { error: true });
            },
          });
      }
    });
  }

  /**
   * Displays the AddApplication component.
   * Adds a new application once closed, if result exists.
   */
  onAdd(): void {
    this.apollo
      .mutate<AddApplicationMutationResponse>({
        mutation: ADD_APPLICATION,
      })
      .subscribe({
        next: ({ errors, data }) => {
          if (errors?.length) {
            this.snackBar.openSnackBar(
              this.translate.instant('common.notifications.objectNotCreated', {
                type: this.translate
                  .instant('common.application.one')
                  .toLowerCase(),
                error: errors ? errors[0].message : '',
              }),
              { error: true }
            );
          } else {
            if (data) {
              this.snackBar.openSnackBar(
                this.translate.instant('common.notifications.objectCreated', {
                  type: this.translate.instant('common.application.one'),
                  value: data.addApplication.name,
                })
              );
              const id = data.addApplication.id;
              this.router.navigate(['/applications', id]);
            }
          }
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
        },
      });
  }

  /**
   * Edits the permissions layer.
   *
   * @param e permissions.
   * @param element application.
   */
  saveAccess(e: any, element: Application): void {
    this.apollo
      .mutate<EditApplicationMutationResponse>({
        mutation: EDIT_APPLICATION,
        variables: {
          id: element.id,
          permissions: e,
        },
      })
      .subscribe({
        next: ({ errors, data }) => {
          if (errors) {
            this.snackBar.openSnackBar(
              this.translate.instant('common.notifications.objectNotUpdated', {
                type: this.translate.instant('common.access'),
                error: errors ? errors[0].message : '',
              }),
              { error: true }
            );
          } else {
            if (data) {
              this.snackBar.openSnackBar(
                this.translate.instant('common.notifications.objectUpdated', {
                  type: this.translate.instant('common.access'),
                  value: element.name,
                })
              );
              const index = this.applications.findIndex(
                (x) => x.id === element.id
              );
              this.applications[index] = data.editApplication;
              // eslint-disable-next-line no-self-assign
              this.applications = this.applications;
            }
          }
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
        },
      });
  }

  /**
   * Opens a dialog to choose roles to fit in the preview.
   *
   * @param element application to preview.
   */
  async onPreview(element: Application): Promise<void> {
    const { ChoseRoleComponent } = await import(
      './components/chose-role/chose-role.component'
    );
    const dialogRef = this.dialog.open(ChoseRoleComponent, {
      data: {
        application: element.id,
      },
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.previewService.setRole(value.role);
        this.router.navigate(['./app-preview', element.id]);
      }
    });
  }

  /**
   * Opens a dialog to give a name for the duplicated application.
   *
   * @param application application to duplicate.
   */
  async onClone(application: Application): Promise<void> {
    const { DuplicateApplicationModalComponent } = await import(
      '../../../components/duplicate-application-modal/duplicate-application-modal.component'
    );
    const dialogRef = this.dialog.open(DuplicateApplicationModalComponent, {
      data: {
        id: application.id,
        name: application.name,
      },
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.onOpenApplication(value.id);
      }
    });
  }

  /**
   * Navigates to application.
   *
   * @param id application id.
   */
  onOpenApplication(id: string): void {
    this.router.navigate(['/applications', id]);
  }

  /**
   * Updates local list with given data
   *
   * @param data New values to update forms
   * @param loading Loading state
   */
  private updateValues(
    data: ApplicationsApplicationNodesQueryResponse,
    loading: boolean
  ): void {
    const mappedValues = data.applications.edges.map((x) => x.node);
    this.cachedApplications = updateQueryUniqueValues(
      this.cachedApplications,
      mappedValues
    );
    this.pageInfo.length = data.applications.totalCount;
    this.pageInfo.endCursor = data.applications.pageInfo.endCursor;
    this.loading = loading;
    this.applications = this.cachedApplications.slice(
      this.pageInfo.pageSize * this.pageInfo.pageIndex,
      this.pageInfo.pageSize * (this.pageInfo.pageIndex + 1)
    );
    this.updating = false;
  }
}

import { Component, HostListener, OnDestroy, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { AbilityBuilder } from '@casl/ability';
import { TranslateService } from '@ngx-translate/core';
import {
  AppAbility,
  Application,
  ContentType,
  ApplicationService,
  UnsubscribeComponent,
} from '@oort-front/shared';
import get from 'lodash/get';
import { takeUntil } from 'rxjs/operators';
import { PreviewService } from '../services/preview.service';

/**
 * Creates a ability object the app preview
 * given the application and the role to preview with
 *
 * @param app The application being previewed
 * @param role The role to preview with
 * @returns The ability object
 */
const getAbilityForAppPreview = (app: Application, role: string) => {
  const { can, rules } = new AbilityBuilder(AppAbility);
  const permissions =
    app.roles?.find((x) => x.id === role)?.permissions?.map((p) => p.type) ||
    [];
  // === Role ===
  if (permissions.includes('can_see_roles')) {
    can(['create', 'read', 'update', 'delete'], ['Role', 'Channel']);
  }

  // === User ===
  if (permissions.includes('can_see_users')) {
    can(['create', 'read', 'update', 'delete'], 'User');
  }

  // === Template ===
  if (permissions.includes('can_manage_templates')) {
    can(['create', 'read', 'update', 'delete', 'manage'], 'Template');
  }

  // === Distribution list ===
  if (permissions.includes('can_manage_distribution_lists')) {
    can(['create', 'read', 'update', 'delete', 'manage'], 'DistributionList');
  }

  // === Custom notifications===
  if (permissions.includes('can_manage_custom_notifications')) {
    can(['create', 'read', 'update', 'delete', 'manage'], 'CustomNotification');
  }

  return new AppAbility(rules);
};

/**
 * Main component of Application preview capacity.
 */
@Component({
  selector: 'app-app-preview',
  templateUrl: './app-preview.component.html',
  styleUrls: ['./app-preview.component.scss'],
})
export class AppPreviewComponent
  extends UnsubscribeComponent
  implements OnInit, OnDestroy
{
  /**
   * Title of application.
   */
  public title = '';
  /**
   * Nav Groups of the application.
   */
  public navGroups: any[] = [];
  /**
   * Current application.
   */
  public application: Application | null = null;
  /**
   * Role to preview with.
   */
  public role = '';
  /**
   * Use side menu or not.
   */
  public sideMenu = false;
  /**
   * Is large device.
   */
  public largeDevice: boolean;

  /**
   * Main component of Application preview capacity.
   *
   * @param route Current route
   * @param applicationService Shared application service
   * @param previewService Custom preview service
   * @param router Angular Router
   * @param translate Angular Translate service
   */
  constructor(
    private route: ActivatedRoute,
    private applicationService: ApplicationService,
    private previewService: PreviewService,
    private router: Router,
    private translate: TranslateService
  ) {
    super();
    this.largeDevice = window.innerWidth > 1024;
  }

  /**
   * Change the display depending on windows size.
   *
   * @param event Event that implies a change in window size
   */
  @HostListener('window:resize', ['$event'])
  onResize(event: any): void {
    this.largeDevice = event.target.innerWidth > 1024;
  }

  /**
   * Generates the routes from the application that is loaded.
   */
  ngOnInit(): void {
    this.route.params.pipe(takeUntil(this.destroy$)).subscribe((params) => {
      this.previewService.roleId$
        .pipe(takeUntil(this.destroy$))
        .subscribe((role: string) => {
          this.applicationService.loadApplication(params.id, role);
          this.role = role;
        });
    });
    this.applicationService.application$
      .pipe(takeUntil(this.destroy$))
      .subscribe((application: Application | null) => {
        if (application) {
          this.title = application.name + ' (Preview)';
          const ability = getAbilityForAppPreview(application, this.role);
          const adminNavItems: any[] = [];
          this.sideMenu = application?.sideMenu ?? false;
          if (ability.can('read', 'User')) {
            adminNavItems.push({
              name: this.translate.instant('common.user.few'),
              path: './settings/users',
              icon: 'supervisor_account',
              visible: true,
            });
          }
          if (ability.can('read', 'Role')) {
            adminNavItems.push({
              name: this.translate.instant('common.role.few'),
              path: './settings/roles',
              icon: 'admin_panel_settings',
              visible: true,
            });
          }
          if (ability.can('manage', 'Template')) {
            adminNavItems.push({
              name: this.translate.instant('common.template.few'),
              path: './settings/templates',
              icon: 'description',
              visible: true,
            });
          }
          if (ability.can('manage', 'DistributionList')) {
            adminNavItems.push({
              name: this.translate.instant('common.distributionList.few'),
              path: './settings/distribution-lists',
              icon: 'mail',
              visible: true,
            });
          }
          if (ability.can('manage', 'CustomNotification')) {
            adminNavItems.push({
              name: this.translate.instant('common.customNotification.few'),
              path: './settings/notifications',
              icon: 'schedule_send',
              visible: true,
            });
          }
          this.navGroups = [
            {
              name: 'Pages',
              navItems: application.pages
                ?.filter((x) => x.content)
                .map((x) => ({
                  name: x.name,
                  path:
                    x.type === ContentType.form
                      ? `./${x.type}/${x.id}`
                      : `./${x.type}/${x.content}`,
                  icon: x.icon || this.getNavIcon(x.type || ''),
                  fontFamily: x.icon ? 'fa' : 'material',
                  visible: x.visible ?? false,
                })),
            },
            {
              name: 'Administration',
              navItems: adminNavItems,
            },
          ];
          if (!this.application || application.id !== this.application.id) {
            const firstPage = get(application, 'pages', [])[0];
            if (
              this.router.url.endsWith('/') ||
              (this.application && application.id !== this.application?.id) ||
              !firstPage ||
              (!this.application && application)
            ) {
              if (firstPage) {
                this.router.navigate(
                  [
                    `./${firstPage.type}/${
                      firstPage.type === ContentType.form
                        ? firstPage.id
                        : firstPage.content
                    }`,
                  ],
                  { relativeTo: this.route }
                );
              } else {
                this.router.navigate([`./`], { relativeTo: this.route });
              }
            }
          }
          this.application = application;
        } else {
          this.navGroups = [];
        }
      });
  }

  /**
   * Remove application data such as styling when exiting preview.
   */
  override ngOnDestroy(): void {
    super.ngOnDestroy();
    this.applicationService.leaveApplication();
  }

  /**
   * Returns nav icon from the page's content type.
   *
   * @param type content type of the page.
   * @returns name of the icon.
   */
  private getNavIcon(type: string): string {
    switch (type) {
      case 'workflow':
        return 'linear_scale';
      case 'form':
        return 'description';
      default:
        return 'dashboard';
    }
  }
}

import { Apollo } from 'apollo-angular';
import { Component, OnInit } from '@angular/core';
import {
  Application,
  ArchivePage,
  Page,
  ApplicationService,
  UnsubscribeComponent,
  ApplicationQueryResponse,
} from '@oort-front/shared';
import { TranslateService } from '@ngx-translate/core';
import { filter, map, switchMap, takeUntil } from 'rxjs/operators';
import { SnackbarService } from '@oort-front/ui';
import { combineLatest, of } from 'rxjs';
import { PreviewService } from '../../../services/preview.service';
import { GET_APPLICATION_ARCHIVED_PAGES } from './graphql/queries';

/**
 * Archive page component for application preview.
 */
@Component({
  selector: 'app-archive',
  templateUrl: './archive.component.html',
  styleUrls: ['./archive.component.scss'],
})
export class ArchiveComponent extends UnsubscribeComponent implements OnInit {
  // === DATA ===
  public loading = true;
  public pages: ArchivePage[] = [];

  // === PREVIEWED ROLE ===
  public role = '';

  /**
   * Workflow page component for application preview
   *
   * @param apollo Apollo service
   * @param applicationService Shared ApplicationService
   * @param snackBar Shared snackbar service
   * @param translate Angular translate service
   * @param previewService Shared preview service
   */
  constructor(
    private apollo: Apollo,
    private applicationService: ApplicationService,
    private snackBar: SnackbarService,
    private translate: TranslateService,
    private previewService: PreviewService
  ) {
    super();
  }

  ngOnInit(): void {
    const applicationStream$ = combineLatest([
      this.applicationService.application$.pipe(
        filter(Boolean),
        map((application: Application) => application.id ?? null)
      ),
      this.previewService.roleId$,
    ]);
    applicationStream$
      .pipe(
        switchMap(([id, asRole]) => {
          if (id !== null) {
            return this.apollo.query<ApplicationQueryResponse>({
              query: GET_APPLICATION_ARCHIVED_PAGES,
              variables: {
                id,
                asRole,
              },
            });
          } else {
            return of({ data: { application: { pages: [] as Page[] } } });
          }
        }),
        takeUntil(this.destroy$)
      )
      .subscribe({
        next: ({ data }) => {
          if (data) {
            this.pages =
              (data.application.pages || []).map((page) => {
                return {
                  id: page.id,
                  name: page.name,
                  autoDeletedAt: page.autoDeletedAt,
                } as ArchivePage;
              }) ?? [];
          } else {
            this.snackBar.openSnackBar(
              this.translate.instant('common.notifications.accessNotProvided', {
                type: this.translate
                  .instant('common.workflow.one')
                  .toLowerCase(),
                error: '',
              }),
              { error: true }
            );
          }
          this.loading = false;
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
          this.loading = false;
        },
      });
  }
}

import { Component, OnInit } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import {
  EditResourceMutationResponse,
  Resource,
  UnsubscribeComponent,
} from '@oort-front/shared';
import { Apollo } from 'apollo-angular';
import get from 'lodash/get';
import { Calculated_FIELD_UPDATE } from './graphql/mutations';
import { Dialog } from '@angular/cdk/dialog';
import { SnackbarService } from '@oort-front/ui';
import { takeUntil } from 'rxjs';

/**
 * Calculated fields tab of resource page
 */
@Component({
  selector: 'app-calculated-fields-tab',
  templateUrl: './calculated-fields-tab.component.html',
  styleUrls: ['./calculated-fields-tab.component.scss'],
})
export class CalculatedFieldsTabComponent
  extends UnsubscribeComponent
  implements OnInit
{
  public resource!: Resource;
  public fields: any[] = [];

  public displayedColumns: string[] = ['name', 'createdAt', '_actions'];

  /**
   * Layouts tab of resource page
   *
   * @param apollo Apollo service
   * @param dialog Dialog service
   * @param translate Angular translate service
   * @param snackBar Shared snackbar service
   */
  constructor(
    private apollo: Apollo,
    private dialog: Dialog,
    private translate: TranslateService,
    private snackBar: SnackbarService
  ) {
    super();
  }

  ngOnInit(): void {
    const state = history.state;
    this.resource = get(state, 'resource', null);

    if (this.resource && this.resource.fields) {
      this.fields = this.resource.fields.filter((f: any) => f.isCalculated);
    } else {
      this.fields = [];
    }
  }

  /**
   * Adds a new Calculated field for the resource.
   */
  async onAddCalculatedField(): Promise<void> {
    const { EditCalculatedFieldModalComponent } = await import(
      '@oort-front/shared'
    );
    const dialogRef = this.dialog.open(EditCalculatedFieldModalComponent, {
      disableClose: true,
      data: {
        calculatedField: null,
        resourceFields: this.resource.fields.filter(
          (f: any) => !f.isCalculated
        ),
      },
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      this.handleCalculatedFieldResponse(value);
    });
  }

  /**
   * Edits a layout. Opens a popup for edition.
   *
   * @param field Calculated field to edit
   */
  async onEditCalculatedField(field: any): Promise<void> {
    const { EditCalculatedFieldModalComponent } = await import(
      '@oort-front/shared'
    );
    const dialogRef = this.dialog.open(EditCalculatedFieldModalComponent, {
      disableClose: true,
      data: {
        calculatedField: field,
        resourceFields: this.resource.fields.filter(
          (f: any) => !f.isCalculated
        ),
      },
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      this.handleCalculatedFieldResponse(value, field);
    });
  }

  /**
   * Handle calculated field mutation response for add/update a calculated field
   *
   * @param value retrieved from the mutation response
   * @param field field to update if it's an update mutation
   */
  private handleCalculatedFieldResponse(value: any, field?: any) {
    if (value) {
      const expression = value.expression
        .replace(/<[^>]*>/gi, ' ')
        .replace(/<\/[^>]*>/gi, ' ')
        .replace(/&nbsp;|&#160;/gi, ' ')
        .replace(/\s+/gi, ' ')
        .trim();
      this.apollo
        .mutate<EditResourceMutationResponse>({
          mutation: Calculated_FIELD_UPDATE,
          variables: {
            resourceId: this.resource.id,
            calculatedField: {
              ...(!field && {
                add: {
                  name: value.name,
                  expression,
                },
              }),
              ...(field && {
                update: {
                  oldName: field.name,
                  name: value.name,
                  expression,
                },
              }),
            },
          },
        })
        .subscribe({
          next: (res) => {
            if (res.data?.editResource) {
              // Needed to update the field as table data source
              this.fields = field
                ? res.data.editResource.fields.filter(
                    (f: any) => f.isCalculated
                  )
                : this.fields.concat(
                    res.data.editResource.fields.find(
                      (f: any) => f.name === value.name
                    )
                  );
            }
            if (res.errors) {
              this.snackBar.openSnackBar(res.errors[0].message, {
                error: true,
              });
            }
          },
          error: (err) => {
            this.snackBar.openSnackBar(err.message, { error: true });
          },
        });
    }
  }

  /**
   * Deletes a Calculated field.
   *
   * @param field Calculated field to delete
   */
  async onDeleteCalculatedField(field: any): Promise<void> {
    const { ConfirmModalComponent } = await import('@oort-front/shared');
    const dialogRef = this.dialog.open(ConfirmModalComponent, {
      data: {
        title: this.translate.instant('common.deleteObject', {
          name: this.translate.instant('common.calculatedField.one'),
        }),
        content: this.translate.instant(
          'components.calculatedFields.delete.confirmationMessage',
          {
            name: field.name,
          }
        ),
        confirmText: this.translate.instant('components.confirmModal.delete'),
        cancelText: this.translate.instant('components.confirmModal.cancel'),
      },
    });

    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.apollo
          .mutate<EditResourceMutationResponse>({
            mutation: Calculated_FIELD_UPDATE,
            variables: {
              resourceId: this.resource.id,
              calculatedField: {
                remove: {
                  name: field.name,
                },
              },
            },
          })
          .subscribe({
            next: (res) => {
              if (res.data?.editResource) {
                this.fields = this.fields.filter(
                  (f: any) => f.name !== field.name
                );
              }
              if (res.errors) {
                this.snackBar.openSnackBar(res.errors[0].message, {
                  error: true,
                });
              }
            },
            error: (err) => {
              this.snackBar.openSnackBar(err.message, { error: true });
            },
          });
      }
    });
  }
}

