import { IconModule } from '@oort-front/ui';
import { DialogModule } from '@oort-front/ui';
import {
  SpinnerModule,
  DividerModule,
  MenuModule,
  ButtonModule,
  FormWrapperModule,
} from '@oort-front/ui';
import { Channel } from '@oort-front/shared';

/**
 * Channel component, act as modal.
 * Used for both edition and addition of channels.
 */
