import { Component, Inject, OnInit, ViewChild } from '@angular/core';
import { CommonModule } from '@angular/common';
import {
  FormControl,
  FormGroup,
  FormsModule,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { TranslateModule } from '@ngx-translate/core';
import { DialogRef, DIALOG_DATA } from '@angular/cdk/dialog';
import {
  PageContextT,
  ReferenceData,
  ReferenceDataQueryResponse,
  ReferenceDatasQueryResponse,
  Resource,
  UnsubscribeComponent,
  ResourceQueryResponse,
  ResourcesQueryResponse,
} from '@oort-front/shared';
import { takeUntil } from 'rxjs';
import { Apollo, QueryRef } from 'apollo-angular';
import {
  GET_REFERENCE_DATA,
  GET_REFERENCE_DATAS,
  GET_RESOURCE,
  GET_RESOURCES,
} from './graphql/queries';
import {
  ButtonModule,
  SelectMenuModule,
  FormWrapperModule,
  AlertModule,
  DialogModule,
  TooltipModule,
  GraphQLSelectComponent,
  GraphQLSelectModule,
  IconModule,
} from '@oort-front/ui';

/** Default items per resources query, for pagination */
const ITEMS_PER_PAGE = 10;

/**
 * Create a form group for the page context datasource selection
 *
 * @param data the initial page context
 * @returns the form group
 */
const createContextDatasourceForm = (data?: PageContextT) => {
  const origin =
    data && 'resource' in data ? 'resource' : data ? 'refData' : 'resource';

  return new FormGroup({
    origin: new FormControl<typeof origin>(origin, [Validators.required]),
    resource: new FormControl(
      data && 'resource' in data ? data.resource : null
    ),
    refData: new FormControl(data && 'refData' in data ? data.refData : null),
    displayField: new FormControl(
      data && data.displayField ? data.displayField : null,
      [Validators.required]
    ),
  });
};

/** Component for selecting the dashboard context datasource */
@Component({
  selector: 'app-context-datasource',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    TranslateModule,
    DialogModule,
    IconModule,
    TooltipModule,
    ButtonModule,
    SelectMenuModule,
    FormWrapperModule,
    AlertModule,
    GraphQLSelectModule,
  ],
  templateUrl: './context-datasource.component.html',
  styleUrls: ['./context-datasource.component.scss'],
})
export class ContextDatasourceComponent
  extends UnsubscribeComponent
  implements OnInit
{
  // Form
  public form!: ReturnType<typeof createContextDatasourceForm>;

  // Data
  public resource: Resource | null = null;
  public refData: ReferenceData | null = null;
  public displayField: string | null = null;

  // Queries
  public resourcesQuery!: QueryRef<ResourcesQueryResponse>;
  public refDatasQuery!: QueryRef<ReferenceDatasQueryResponse>;

  @ViewChild(GraphQLSelectComponent)
  resourceSelect?: GraphQLSelectComponent;
  @ViewChild(GraphQLSelectComponent)
  refDataSelect?: GraphQLSelectComponent;

  // Available fields
  public availableFields: string[] = [];

  /**
   * Component for selecting the dashboard context datasource
   *
   * @param apollo apollo client
   * @param data initial context
   * @param dialogRef dialog reference
   */
  constructor(
    private apollo: Apollo,
    @Inject(DIALOG_DATA) public data: PageContextT,
    public dialogRef: DialogRef<ContextDatasourceComponent>
  ) {
    super();
    this.form = createContextDatasourceForm(data);
  }

  ngOnInit(): void {
    this.initQueries();

    // When origin changes, reset the other fields
    this.form
      .get('origin')
      ?.valueChanges.pipe(takeUntil(this.destroy$))
      .subscribe(() => {
        this.form.get('resource')?.setValue(null);
        this.form.get('refData')?.setValue(null);
        this.form.get('displayField')?.setValue(null);
      });

    // When resource or refData changes, reset the displayField
    // and update the respective variables values
    (['resource', 'refData'] as const).forEach((field) => {
      this.form
        .get(field)
        ?.valueChanges.pipe(takeUntil(this.destroy$))
        .subscribe((value) => {
          // Set displayField to null
          const displayField = this.form.get('displayField');
          displayField?.setValue(null);

          if (value) displayField?.enable();
          else displayField?.disable();

          // Update the respective variables values
          this[field] =
            this[`${field}Select`]?.elements
              .getValue()
              .find((x) => x.id === value) ?? null;

          // Update the displayField options
          this.updateDisplayFieldOptions();
        });
    });

    const sourceSelected =
      !!this.form.get('resource')?.value || !!this.form.get('refData')?.value;

    if (!sourceSelected) this.form.get('displayField')?.disable();
  }

  /** Initializes queries and fetches initial data */
  private initQueries(): void {
    this.resourcesQuery = this.apollo.watchQuery<ResourcesQueryResponse>({
      query: GET_RESOURCES,
      variables: {
        first: ITEMS_PER_PAGE,
        sortField: 'name',
      },
    });

    this.refDatasQuery = this.apollo.watchQuery<ReferenceDatasQueryResponse>({
      query: GET_REFERENCE_DATAS,
      variables: {
        first: ITEMS_PER_PAGE,
      },
    });

    // If the form has a resource, fetch it
    const resourceID = this.form.get('resource')?.value;
    if (resourceID) {
      this.apollo
        .query<ResourceQueryResponse>({
          query: GET_RESOURCE,
          variables: {
            id: resourceID,
          },
        })
        .subscribe(({ data }) => {
          this.resource = data.resource;
          this.updateDisplayFieldOptions();
        });
    }

    // If form has a refData, fetch it
    const refDataID = this.form.get('refData')?.value;
    if (refDataID) {
      this.apollo
        .query<ReferenceDataQueryResponse>({
          query: GET_REFERENCE_DATA,
          variables: {
            id: refDataID,
          },
        })
        .subscribe(({ data }) => {
          this.refData = data.referenceData;
          this.updateDisplayFieldOptions();
        });
    }
  }

  /** Updates the options for the display field select */
  private updateDisplayFieldOptions(): void {
    const origin = this.form.get('origin')?.value;

    switch (origin) {
      case 'resource':
        this.availableFields =
          this.resource?.fields.map((x: any) => x.name) ?? [];
        break;
      case 'refData':
        // TODO: When frontend changes about referenceData fields are merged,
        // swap to the commented line to remove any casting
        // this.availableFields = this.refData?.fields?.map((x) => x.name) ?? [];
        this.availableFields =
          this.refData?.fields?.map((x: any) => x.name) ?? [];
        break;
      default:
        this.availableFields = [];
    }
  }

  /**
   * Changes the query according to search text
   *
   * @param search Search text from the graphql select
   */
  public onResourceSearchChange(search: string): void {
    this.resourcesQuery.refetch({
      first: ITEMS_PER_PAGE,
      sortField: 'name',
      filter: {
        logic: 'and',
        filters: [
          {
            field: 'name',
            operator: 'contains',
            value: search,
          },
        ],
      },
    });
  }

  /** Emits the selected context */
  public onSubmit(): void {
    const formValue = this.form.getRawValue();

    const noResource = !this.resource && formValue.origin === 'resource';
    const noRefData = !this.refData && formValue.origin === 'refData';

    if (!formValue.displayField || !formValue.origin || noResource || noRefData)
      return;

    const context: PageContextT =
      formValue.origin === 'resource'
        ? {
            resource: this.resource?.id ?? '',
            displayField: formValue.displayField ?? '',
          }
        : {
            refData: this.refData?.id ?? '',
            displayField: formValue.displayField ?? '',
          };

    this.dialogRef.close(context as any);
  }
}

import {
  Component,
  Output,
  EventEmitter,
  OnInit,
  OnDestroy,
  Inject,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormControl, FormsModule, ReactiveFormsModule } from '@angular/forms';
import { MonacoEditorModule } from 'ngx-monaco-editor-v2';
import {
  Application,
  ApplicationService,
  UnsubscribeComponent,
  ConfirmService,
  RestService,
  BlobType,
  DownloadService,
} from '@oort-front/shared';
import { takeUntil } from 'rxjs/operators';
import { Apollo } from 'apollo-angular';
import { TranslateModule, TranslateService } from '@ngx-translate/core';
import {
  ButtonModule,
  SnackbarService,
  SpinnerModule,
  TooltipModule,
} from '@oort-front/ui';
import { debounceTime, distinctUntilChanged } from 'rxjs/operators';
import { DOCUMENT } from '@angular/common';

/** Default css style example to initialize the form and editor */
const DEFAULT_STYLE = '';

/** Component that allow custom styling to the application using free scss editor */
@Component({
  selector: 'app-custom-style',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    MonacoEditorModule,
    TranslateModule,
    ButtonModule,
    SpinnerModule,
    TooltipModule,
  ],
  templateUrl: './custom-style.component.html',
  styleUrls: ['./custom-style.component.scss'],
})
export class CustomStyleComponent
  extends UnsubscribeComponent
  implements OnInit, OnDestroy
{
  public formControl = new FormControl(DEFAULT_STYLE);
  public applicationId?: string;
  @Output() style = new EventEmitter<string>();
  @Output() cancel = new EventEmitter();
  public editorOptions = {
    theme: 'vs-dark',
    language: 'scss',
    fixedOverflowWidgets: false,
  };
  private rawCustomStyle!: string;
  private savedStyle = '';
  public loading = false;

  /**
   * Creates an instance of CustomStyleComponent, form and updates.
   *
   * @param applicationService Shared application service
   * @param snackBar Shared snackbar service
   * @param apollo Apollo service
   * @param translate Angular translate service
   * @param confirmService Shared confirmation service
   * @param restService Shared rest service
   * @param document document
   * @param downloadService Shared download service
   */
  constructor(
    private applicationService: ApplicationService,
    private snackBar: SnackbarService,
    private apollo: Apollo,
    private translate: TranslateService,
    private confirmService: ConfirmService,
    private restService: RestService,
    @Inject(DOCUMENT) private document: Document,
    private downloadService: DownloadService
  ) {
    super();
    // Updates the style when the value changes
    this.formControl.valueChanges
      .pipe(debounceTime(1000), distinctUntilChanged())
      .subscribe((value: any) => {
        const scss = value as string;
        this.restService
          .post('style/scss-to-css', { scss }, { responseType: 'text' })
          .subscribe({
            next: (css) => {
              if (this.applicationService.customStyle) {
                this.applicationService.customStyle.innerText = css;
              }
            },
          });
        this.applicationService.customStyleEdited = true;
        this.rawCustomStyle = value;
      });
  }

  ngOnInit(): void {
    if (this.applicationService.rawCustomStyle) {
      this.savedStyle = this.applicationService.customStyle?.innerText || '';
      this.rawCustomStyle = this.applicationService.rawCustomStyle;
      this.formControl.setValue(this.rawCustomStyle, { emitEvent: false });
      this.formControl.markAsPristine();
    } else {
      const styleElement = this.document.createElement('style');
      styleElement.innerText = '';
      this.document.getElementsByTagName('head')[0].appendChild(styleElement);
      this.applicationService.rawCustomStyle = this.rawCustomStyle;
      this.applicationService.customStyle = styleElement;
    }

    this.applicationService.application$
      .pipe(takeUntil(this.destroy$))
      .subscribe((application: Application | null) => {
        if (application) {
          this.applicationId = application.id;
        }
      });
  }

  /** When clicking on the close button */
  onClose(): void {
    if (this.formControl.pristine) {
      this.cancel.emit(true);
    } else {
      /** If not saved changes, confirm before close */
      const confirmDialogRef = this.confirmService.openConfirmModal({
        title: this.translate.instant('components.form.update.exit'),
        content: this.translate.instant(
          'components.widget.settings.close.confirmationMessage'
        ),
        confirmText: this.translate.instant('components.confirmModal.confirm'),
        confirmVariant: 'danger',
      });
      confirmDialogRef.closed
        .pipe(takeUntil(this.destroy$))
        .subscribe((confirm: any) => {
          if (confirm) {
            // eslint-disable-next-line @typescript-eslint/no-non-null-assertion

            if (this.applicationService.customStyle) {
              this.applicationService.customStyleEdited = false;
              this.applicationService.customStyle.innerText = this.savedStyle;
            }
            this.cancel.emit(true);
          }
        });
    }
  }

  /** Save application custom css styling */
  async onSave(): Promise<void> {
    // todo(beta): check
    this.loading = true;
    if (!this.applicationId) {
      throw new Error('No application id');
    }

    const file = new File(
      [this.formControl.value as string],
      'customStyle.scss',
      {
        type: 'scss',
      }
    );

    const path = await this.downloadService.uploadBlob(
      file,
      BlobType.APPLICATION_STYLE,
      this.applicationId
    );

    this.snackBar.openSnackBar(
      this.translate.instant('common.notifications.objectUpdated', {
        value: this.translate.instant('components.application.customStyling'),
        type: '',
      })
    );
    // todo(beta): check
    if (path) {
      this.snackBar.openSnackBar(
        this.translate.instant('common.notifications.objectUpdated', {
          value: this.translate.instant('components.application.customStyling'),
          type: '',
        })
      );
      this.formControl.markAsPristine();
      this.applicationService.customStyleEdited = false;
      this.applicationService.rawCustomStyle = this.rawCustomStyle;
      this.savedStyle = this.applicationService.customStyle?.innerText || '';
    }
    this.loading = false;
  }

  /**
   * On initialization of editor, format code
   *
   * @param editor monaco editor used for scss edition
   */
  public initEditor(editor: any): void {
    if (editor) {
      setTimeout(() => {
        editor
          .getAction('editor.action.formatDocument')
          .run()
          .finally(() => {
            this.formControl.markAsPristine();
            this.applicationService.customStyleEdited = false;
          });
      }, 100);
    }
  }

  override ngOnDestroy(): void {
    super.ngOnDestroy();
    if (
      this.applicationService.customStyleEdited &&
      this.applicationService.customStyle
    ) {
      this.applicationService.customStyleEdited = false;
      this.applicationService.customStyle.innerText = this.savedStyle;
    }
  }
}

import { Component } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import { AppAbility, UnsubscribeComponent } from '@oort-front/shared';
import { takeUntil } from 'rxjs';

/**
 * Main BO dashboard component
 */
@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss'],
})
export class DashboardComponent extends UnsubscribeComponent {
  // === HEADER TITLE ===
  public title = 'Back-office';

  // === AVAILABLE ROUTES, DEPENDS ON USER ===
  public navGroups: any[] = [];

  /**
   * Main BO dashboard component
   *
   * @param translate Angular translate service
   * @param ability user ability
   */
  constructor(
    private translate: TranslateService,
    private ability: AppAbility
  ) {
    super();
    this.setNavGroups();
    translate.onLangChange.pipe(takeUntil(this.destroy$)).subscribe(() => {
      this.setNavGroups();
    });
  }

  /**
   * Set the navigation items, from the permissions of user.
   */
  setNavGroups(): void {
    const navGroups = [];
    // Site builder items ( applications )
    const siteBuilderItems = [];
    if (this.ability.can('read', 'Application')) {
      siteBuilderItems.push({
        name: this.translate.instant('pages.applications.title'),
        path: '/applications',
        icon: 'apps',
      });
    }
    if (siteBuilderItems.length > 0) {
      navGroups.push({
        name: this.translate.instant('pages.appBuilder.title'),
        navItems: siteBuilderItems,
      });
    }
    // Data items ( forms / resources / ref data )
    const dataItems = [];
    if (this.ability.can('read', 'Form')) {
      dataItems.push({
        name: this.translate.instant('common.form.few'),
        path: '/forms',
        icon: 'poll',
      });
    }
    if (this.ability.can('read', 'Resource')) {
      dataItems.push({
        name: this.translate.instant('common.resource.few'),
        path: '/resources',
        icon: 'storage',
      });
    }
    if (this.ability.can('read', 'ReferenceData')) {
      dataItems.push({
        name: this.translate.instant('common.referenceData.few'),
        path: '/referencedata',
        icon: 'cloud_download',
      });
    }
    if (dataItems.length > 0) {
      navGroups.push({
        name: this.translate.instant('pages.formBuilder.title'),
        navItems: dataItems,
      });
    }
    // Administrative items ( users / roles / APIs / automated actions )
    const administrationItems = [];
    if (this.ability.can('read', 'User')) {
      administrationItems.push({
        name: this.translate.instant('common.user.few'),
        path: '/settings/users',
        icon: 'supervisor_account',
      });
    }
    if (this.ability.can('read', 'Role')) {
      administrationItems.push({
        name: this.translate.instant('common.role.few'),
        path: '/settings/roles',
        icon: 'admin_panel_settings',
      });
    }
    if (this.ability.can('read', 'ApiConfiguration')) {
      administrationItems.push({
        name: this.translate.instant('common.apiConfiguration.few'),
        path: '/settings/apiconfigurations',
        icon: 'settings_input_composite',
      });
    }
    if (this.ability.can('read', 'PullJob')) {
      administrationItems.push({
        name: this.translate.instant('common.pullJob.few'),
        path: '/settings/pulljobs',
        icon: 'cloud_download',
      });
    }
    if (administrationItems.length > 0) {
      navGroups.push({
        name: this.translate.instant('pages.administration.title'),
        navItems: administrationItems,
      });
    }

    this.navGroups = navGroups;
  }
}

import { Apollo } from 'apollo-angular';
import {
  Component,
  EventEmitter,
  OnDestroy,
  OnInit,
  Output,
} from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { GET_DASHBOARD_BY_ID } from './graphql/queries';
import {
  Dashboard,
  DashboardQueryResponse,
  DashboardService,
  UnsubscribeComponent,
} from '@oort-front/shared';
import { TranslateService } from '@ngx-translate/core';
import { takeUntil } from 'rxjs/operators';
import { SnackbarService } from '@oort-front/ui';

/**
 * Dashboard component page, for application preview.
 */
@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss'],
})
export class DashboardComponent
  extends UnsubscribeComponent
  implements OnInit, OnDestroy
{
  /** Id of loaded dashboard */
  public id = '';
  /** Loading indicator */
  public loading = true;
  /** Current widgets */
  public tiles = [];
  /** Current dashboard */
  public dashboard?: Dashboard;
  /** Emit event when changing steps */
  @Output() changeStep: EventEmitter<number> = new EventEmitter();

  /**
   * Dashboard component page for application preview.
   *
   * @param apollo Apollo service
   * @param route Angular activated route
   * @param router Angular router
   * @param snackBar Shared snackbar service
   * @param dashboardService Shared dashboard service
   * @param translate Angular translate service
   */
  constructor(
    private apollo: Apollo,
    private route: ActivatedRoute,
    private router: Router,
    private snackBar: SnackbarService,
    private dashboardService: DashboardService,
    private translate: TranslateService
  ) {
    super();
  }

  /**
   * Gets the dashboard from the page parameters.
   */
  ngOnInit(): void {
    this.route.params.pipe(takeUntil(this.destroy$)).subscribe((params) => {
      this.loading = true;
      this.id = params.id;
      this.apollo
        .query<DashboardQueryResponse>({
          query: GET_DASHBOARD_BY_ID,
          variables: {
            id: this.id,
          },
        })
        .subscribe({
          next: ({ data, loading }) => {
            if (data.dashboard) {
              this.dashboard = data.dashboard;
              this.dashboardService.openDashboard(this.dashboard);
              this.widgets = data.dashboard.structure
                ? data.dashboard.structure
                : [];
              this.loading = loading;
            } else {
              this.snackBar.openSnackBar(
                this.translate.instant(
                  'common.notifications.accessNotProvided',
                  {
                    type: this.translate
                      .instant('common.dashboard.one')
                      .toLowerCase(),
                    error: '',
                  }
                ),
                { error: true }
              );
              this.router.navigate(['/dashboards']);
            }
          },
          error: (err) => {
            this.snackBar.openSnackBar(err.message, { error: true });
            this.router.navigate(['/dashboards']);
          },
        });
    });
  }

  /**
   * Destroys all subscriptions of the page.
   */
  override ngOnDestroy(): void {
    super.ngOnDestroy();
    this.dashboardService.closeDashboard();
  }
}

import { Apollo, QueryRef } from 'apollo-angular';
import {
  Component,
  ElementRef,
  EventEmitter,
  Inject,
  OnDestroy,
  OnInit,
  Output,
  Renderer2,
  ViewChild,
} from '@angular/core';
import { ActivatedRoute, NavigationEnd, Router } from '@angular/router';
import {
  Dashboard,
  ApplicationService,
  WorkflowService,
  DashboardService,
  AuthService,
  Application,
  UnsubscribeComponent,
  WidgetGridComponent,
  ConfirmService,
  ReferenceDataService,
  Record,
  ButtonActionT,
  ResourceRecordsNodesQueryResponse,
  DashboardQueryResponse,
  EditDashboardMutationResponse,
  RecordQueryResponse,
} from '@oort-front/shared';
import { EDIT_DASHBOARD } from './graphql/mutations';
import {
  GET_DASHBOARD_BY_ID,
  GET_RECORD_BY_ID,
  GET_RESOURCE_RECORDS,
} from './graphql/queries';
import { TranslateService } from '@ngx-translate/core';
import {
  map,
  takeUntil,
  filter,
  startWith,
  debounceTime,
} from 'rxjs/operators';
import { Observable, firstValueFrom } from 'rxjs';
import { FormControl } from '@angular/forms';
import { isEqual } from 'lodash';
import { Dialog } from '@angular/cdk/dialog';
import { SnackbarService, UILayoutService } from '@oort-front/ui';
import localForage from 'localforage';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';
import { ContextService, CustomWidgetStyleComponent } from '@oort-front/shared';
import { DOCUMENT } from '@angular/common';
import { Clipboard } from '@angular/cdk/clipboard';

/** Default number of records fetched per page */
const ITEMS_PER_PAGE = 10;

/**
 * Back-office Dashboard page.
 * Edition of the dashboard ( if user has permission ).
 */
@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss'],
})
export class DashboardComponent
  extends UnsubscribeComponent
  implements OnInit, OnDestroy
{
  /** Change step event ( in workflow ) */
  @Output() changeStep: EventEmitter<number> = new EventEmitter();
  /** Widget grid reference */
  @ViewChild(WidgetGridComponent)
  widgetGridComponent!: WidgetGridComponent;
  /** Is dashboard in fullscreen mode */
  public isFullScreen = false;
  /** Dashboard id */
  public id = '';
  /** Application id */
  public applicationId?: string;
  /** Is dashboard loading */
  public loading = true;
  /** List of widgets */
  public widgets: any[] = [];
  /** Current dashboard */
  public dashboard?: Dashboard;
  /** Show dashboard filter */
  public showFilter!: boolean;
  /** User can update dashboard */
  public canUpdate = false;
  /** Dashboard name edition is active */
  public formActive = false;
  /** Show application menu */
  public showAppMenu = false;
  /** List of available applications */
  public applications: Application[] = [];
  /** Contextual reference data elements  */
  public refDataElements: any[] = [];
  /** Contextual records query */
  public recordsQuery!: QueryRef<ResourceRecordsNodesQueryResponse>;
  /** Contextual template id */
  public contextId = new FormControl<string | number | null>(null);
  /** Field of contextual reference data */
  public refDataValueField = '';
  /** Contextual record */
  public contextRecord: Record | null = null;
  /** Configured dashboard quick actions */
  public buttonActions: ButtonActionT[] = [];

  private timeoutListener!: NodeJS.Timeout;

  /** @returns get newest widget id from existing ids */
  get newestId(): number {
    const widgets = this.widgets?.slice() || [];
    return widgets.length === 0
      ? 0
      : Math.max(...widgets.map((x: any) => x.id)) + 1;
  }

  /** @returns type of context element */
  get contextType() {
    if (this.dashboard?.page?.context) {
      return 'resource' in this.dashboard.page.context ? 'record' : 'element';
    } else {
      return;
    }
  }

  /** @returns is dashboard a step or a page */
  get isStep(): boolean {
    return this.router.url.includes('/workflow/');
  }

  /**
   * Back-office Dashboard page.
   * Edition of the dashboard ( if user has permission ).
   *
   * @param applicationService Shared application service
   * @param workflowService Shared workflow service
   * @param apollo Apollo service
   * @param route Angular activated route
   * @param router Angular router
   * @param dialog Dialog service
   * @param snackBar Shared snackbar service
   * @param dashboardService Shared dashboard service
   * @param translate Angular translate service
   * @param authService Shared authentication service
   * @param confirmService Shared confirm service
   * @param contextService Dashboard context service
   * @param refDataService Shared reference data service
   * @param renderer Angular renderer
   * @param elementRef Angular element ref
   * @param layoutService Shared layout service
   * @param document Document
   * @param clipboard Angular clipboard service
   */
  constructor(
    private applicationService: ApplicationService,
    private workflowService: WorkflowService,
    private apollo: Apollo,
    private route: ActivatedRoute,
    private router: Router,
    public dialog: Dialog,
    private snackBar: SnackbarService,
    private dashboardService: DashboardService,
    private translate: TranslateService,
    private authService: AuthService,
    private confirmService: ConfirmService,
    private contextService: ContextService,
    private refDataService: ReferenceDataService,
    private renderer: Renderer2,
    private elementRef: ElementRef,
    private layoutService: UILayoutService,
    @Inject(DOCUMENT) private document: Document,
    private clipboard: Clipboard
  ) {
    super();
  }

  ngOnInit(): void {
    this.contextId.valueChanges
      .pipe(debounceTime(500), takeUntil(this.destroy$))
      .subscribe((value) => {
        // Load template, or go back to default one
        this.onContextChange(value);
      });
    /** Listen to router events navigation end, to get last version of params & queryParams. */
    this.router.events
      .pipe(
        filter((event) => event instanceof NavigationEnd),
        startWith(this.router), // initialize
        takeUntil(this.destroy$)
      )
      .subscribe(() => {
        this.loading = true;
        // Reset context
        this.contextRecord = null;
        this.contextId.setValue(null, {
          emitEvent: false,
          emitModelToViewChange: false,
          emitViewToModelChange: false,
        });
        this.contextId.markAsPristine();
        this.contextId.markAsUntouched();
        // Reset scroll when changing page
        const pageContainer = this.document.getElementById('appPageContainer');
        if (pageContainer) {
          pageContainer.scrollTop = 0;
        }

        /** Extract main dashboard id */
        const id = this.route.snapshot.paramMap.get('id');
        /** Extract query id to load template */
        const queryId = this.route.snapshot.queryParamMap.get('id');
        if (id) {
          if (queryId) {
            this.loadDashboard(id).then(() => {
              const templates = this.dashboard?.page?.contentWithContext;
              const type = this.contextType;
              if (type) {
                // Find template from parent's templates, based on query params id
                const template = templates?.find((d) => {
                  // If templates use reference data
                  if (type === 'element')
                    return (
                      'element' in d &&
                      d.element.toString().trim() === queryId.trim()
                    );
                  // If templates use resource
                  else if (type === 'record')
                    return (
                      'record' in d &&
                      d.record.toString().trim() === queryId.trim()
                    );
                  return false;
                });

                if (template) {
                  // if we found the contextual dashboard, load it
                  this.loadDashboard(template.content).then(
                    () => (this.loading = false)
                  );
                } else {
                  if (this.dashboard?.page && this.canUpdate) {
                    this.snackBar.openSnackBar(
                      this.translate.instant(
                        'models.dashboard.context.notifications.creatingTemplate'
                      )
                    );
                    this.dashboardService
                      .createDashboardWithContext(
                        this.dashboard?.page?.id as string,
                        type, // type of context
                        queryId // id of the context
                      )
                      .then(({ data }) => {
                        if (!data?.addDashboardWithContext?.id) return;
                        this.snackBar.openSnackBar(
                          this.translate.instant(
                            'models.dashboard.context.notifications.templateCreated'
                          )
                        );
                        // load the contextual dashboard
                        this.loadDashboard(
                          data.addDashboardWithContext.id
                        ).then(() => (this.loading = false));
                      });
                  }
                }
              } else {
                this.loading = false;
              }
            });
          } else {
            // if there is no id, we are not on a contextual dashboard, we simply load the dashboard
            this.loadDashboard(id).then(() => (this.loading = false));
          }
        }
      });
  }

  /**
   * Init the dashboard
   *
   * @param id Dashboard id
   * @returns Promise
   */
  private async loadDashboard(id: string) {
    // don't init the dashboard if the id is the same
    if (this.dashboard?.id === id) {
      return;
    }

    const rootElement = this.elementRef.nativeElement;
    this.renderer.setAttribute(rootElement, 'data-dashboard-id', id);
    this.formActive = false;
    this.loading = true;
    this.id = id;
    return firstValueFrom(
      this.apollo.query<DashboardQueryResponse>({
        query: GET_DASHBOARD_BY_ID,
        variables: {
          id: this.id,
        },
      })
    )
      .then(({ data }) => {
        if (data.dashboard) {
          this.dashboard = data.dashboard;
          this.initContext();
          this.updateContextOptions();
          this.canUpdate =
            (this.dashboard?.page
              ? this.dashboard?.page?.canUpdate
              : this.dashboard?.step?.canUpdate) || false;

          this.dashboardService.openDashboard(this.dashboard);
          this.widgets = this.dashboard.structure
            ? [...this.dashboard.structure.filter((x: any) => x !== null)]
            : [];
          this.applicationId = this.dashboard.page
            ? this.dashboard.page.application?.id
            : this.dashboard.step
            ? this.dashboard.step.workflow?.page?.application?.id
            : '';
          this.buttonActions = this.dashboard.buttons || [];
          this.showFilter = this.dashboard.showFilter ?? false;
          this.contextService.isFilterEnabled.next(this.showFilter);
        } else {
          this.contextService.isFilterEnabled.next(false);
          this.snackBar.openSnackBar(
            this.translate.instant('common.notifications.accessNotProvided', {
              type: this.translate
                .instant('common.dashboard.one')
                .toLowerCase(),
              error: '',
            }),
            { error: true }
          );
          this.router.navigate(['/applications']);
        }
      })
      .catch((err) => {
        this.snackBar.openSnackBar(err.message, { error: true });
        this.router.navigate(['/applications']);
      });
  }

  /**
   * Leave dashboard
   */
  override ngOnDestroy(): void {
    super.ngOnDestroy();
    if (this.timeoutListener) {
      clearTimeout(this.timeoutListener);
    }
    localForage.removeItem(this.applicationId + 'contextualFilterPosition'); //remove temporary contextual filter data
    localForage.removeItem(this.applicationId + 'contextualFilter');
    this.dashboardService.closeDashboard();
  }

  /**
   * Show modal confirmation before leave the page if has changes on form
   *
   * @returns boolean of observable of boolean
   */
  canDeactivate(): Observable<boolean> | boolean {
    if (this.widgetGridComponent && !this.widgetGridComponent?.canDeactivate) {
      const dialogRef = this.confirmService.openConfirmModal({
        title: this.translate.instant('pages.dashboard.update.exit'),
        content: this.translate.instant('pages.dashboard.update.exitMessage'),
        confirmText: this.translate.instant('components.confirmModal.confirm'),
        confirmVariant: 'primary',
      });
      return dialogRef.closed.pipe(takeUntil(this.destroy$)).pipe(
        map((confirm) => {
          if (confirm) {
            return true;
          }
          return false;
        })
      );
    }
    return true;
  }

  /**
   * Add a new widget to the dashboard.
   *
   * @param e add event
   */
  onAdd(e: any): void {
    const widget = JSON.parse(JSON.stringify(e));
    widget.id = this.newestId;
    this.widgets = [...this.widgets, widget];
    this.autoSaveChanges();
    if (this.timeoutListener) {
      clearTimeout(this.timeoutListener);
    }
    // scroll to the element once it is created
    this.timeoutListener = setTimeout(() => {
      const el = this.document.getElementById(`widget-${widget.id}`);
      el?.scrollIntoView({ behavior: 'smooth' });
    });
  }

  /**
   * Edits the settings or display of a widget.
   *
   * @param e widget to save.
   */
  onEditTile(e: any): void {
    // make sure that we save the default layout.
    const index = this.widgets.findIndex((v: any) => v.id === e.id);
    const options = this.widgets[index]?.settings?.defaultLayout
      ? {
          ...e.options,
          defaultLayout: this.widgets[index].settings.defaultLayout,
        }
      : e.options;
    if (options) {
      switch (e.type) {
        case 'display': {
          this.widgets = this.widgets.map((x) => {
            if (x.id === e.id) {
              x.defaultCols = options.cols;
              x.defaultRows = options.rows;
            }
            return x;
          });
          this.autoSaveChanges();
          break;
        }
        case 'data': {
          this.widgets = this.widgets.map((x) => {
            if (x.id === e.id) {
              x = { ...x, settings: options };
            }
            return x;
          });
          this.autoSaveChanges();
          break;
        }
        default: {
          break;
        }
      }
    }
  }

  /**
   * Remove a widget from the dashboard.
   *
   * @param e delete event
   */
  onDeleteTile(e: any): void {
    this.widgets = this.widgets.filter((x) => x.id !== e.id);
    this.autoSaveChanges();
  }

  /**
   * Style a widget from the dashboard.
   *
   * @param e style event
   */
  onStyleTile(e: any): void {
    this.layoutService.setRightSidenav({
      component: CustomWidgetStyleComponent,
      inputs: {
        widgetComp: e,
        save: (tile: any) => this.onEditTile(tile),
      },
    });
    this.layoutService.closeRightSidenav = true;
  }

  /**
   * Drags and drops a widget to move it.
   *
   * @param e move event.
   */
  onMove(e: any): void {
    // Duplicates array, some times the arrays is write protected
    this.widgets = this.widgets.slice();
    [this.widgets[e.oldIndex], this.widgets[e.newIndex]] = [
      this.widgets[e.newIndex],
      this.widgets[e.oldIndex],
    ];
    this.autoSaveChanges();
  }

  /** Save the dashboard changes in the database. */
  private autoSaveChanges(): void {
    this.apollo
      .mutate<EditDashboardMutationResponse>({
        mutation: EDIT_DASHBOARD,
        variables: {
          id: this.id,
          structure: this.widgets,
        },
      })
      .subscribe({
        next: ({ errors }) => {
          this.applicationService.handleEditionMutationResponse(
            errors,
            this.translate.instant('common.dashboard.one')
          );
          if (!errors) {
            this.dashboardService.openDashboard({
              ...this.dashboard,
              structure: this.widgets,
            });
          }
        },
        complete: () => (this.loading = false),
      });
  }

  /**
   * Toggle visibility of form.
   */
  toggleFormActive(): void {
    if (
      this.dashboard?.page
        ? this.dashboard.page.canUpdate
        : this.dashboard?.step?.canUpdate
    ) {
      this.formActive = !this.formActive;
    }
  }

  /**
   * Update the name of the dashboard and the step or page linked to it.
   *
   * @param {string} dashboardName new dashboard name
   */
  saveName(dashboardName: string): void {
    if (dashboardName && dashboardName !== this.dashboard?.name) {
      const callback = () => {
        this.dashboard = { ...this.dashboard, name: dashboardName };
      };
      if (this.contextId.value) {
        // Seeing a template
        this.dashboardService.editName(dashboardName, callback);
      } else {
        // Not part of contextual page
        if (this.isStep) {
          this.workflowService.updateStepName(
            {
              id: this.dashboard?.step?.id,
              name: dashboardName,
            },
            callback
          );
        } else {
          this.applicationService.updatePageName(
            {
              id: this.dashboard?.page?.id,
              name: dashboardName,
            },
            callback
          );
        }
      }
    }
  }

  /**
   * Toggles the filter for the current dashboard.
   */
  toggleFiltering(): void {
    if (this.dashboard) {
      this.showFilter = !this.showFilter;
      this.apollo
        .mutate<EditDashboardMutationResponse>({
          mutation: EDIT_DASHBOARD,
          variables: {
            id: this.id,
            showFilter: this.showFilter,
          },
        })
        .subscribe({
          next: ({ data, errors }) => {
            this.applicationService.handleEditionMutationResponse(
              errors,
              this.translate.instant('common.dashboard.one')
            );
            if (!errors) {
              this.dashboardService.openDashboard({
                ...this.dashboard,
                ...(data && { showFilter: data?.editDashboard.showFilter }),
              });
            }
          },
          complete: () => {
            this.contextService.isFilterEnabled.next(this.showFilter);
            this.loading = false;
          },
        });
    }
  }

  /** Display the ShareUrl modal with the route to access the dashboard. */
  public async onShare(): Promise<void> {
    const url = `${window.origin}/share/${this.dashboard?.id}`;
    this.clipboard.copy(url);
    this.snackBar.openSnackBar(
      this.translate.instant('common.notifications.copiedToClipboard')
    );
  }

  /** Open modal to add new button action */
  public async onAddButtonAction() {
    const { EditButtonActionComponent } = await import(
      './components/edit-button-action/edit-button-action.component'
    );
    const dialogRef = this.dialog.open<ButtonActionT | undefined>(
      EditButtonActionComponent
    );

    dialogRef.closed
      .pipe(takeUntil(this.destroy$))
      .subscribe(async (button) => {
        if (!button) return;
        const currButtons =
          (await firstValueFrom(this.dashboardService.dashboard$))?.buttons ||
          [];

        this.dashboardService.saveDashboardButtons([...currButtons, button]);
        this.buttonActions.push(button);
      });
  }

  /** Opens modal for context dataset selection */
  public async selectContextDatasource() {
    const currContext =
      (await firstValueFrom(this.dashboardService.dashboard$))?.page?.context ??
      null;

    const { ContextDatasourceComponent } = await import(
      './components/context-datasource/context-datasource.component'
    );
    const dialogRef = this.dialog.open(ContextDatasourceComponent, {
      data: currContext,
    });

    const parentDashboardId = this.route.snapshot.paramMap.get('id');

    dialogRef.closed
      .pipe(takeUntil(this.destroy$))
      .subscribe(async (context: any) => {
        if (context) {
          if (isEqual(context, currContext)) return;

          await this.dashboardService.updateContext(context);
          this.dashboard =
            (await firstValueFrom(this.dashboardService.dashboard$)) ||
            undefined;

          const urlArr = this.router.url.split('/');

          // load the linked data
          this.updateContextOptions();
          // go the the parent dashboard
          urlArr[urlArr.length - 1] = `${parentDashboardId}`;
          this.router.navigateByUrl(urlArr.join('/'));
        }
      });
  }

  /**
   * Update the context options.
   * Loads elements from reference data or records from resource.
   */
  private updateContextOptions() {
    const context = this.dashboard?.page?.context;
    if (!context) return;

    if ('resource' in context) {
      this.recordsQuery =
        this.apollo.watchQuery<ResourceRecordsNodesQueryResponse>({
          query: GET_RESOURCE_RECORDS,
          variables: {
            first: ITEMS_PER_PAGE,
            id: context.resource,
          },
        });
    }

    if ('refData' in context) {
      this.refDataService.loadReferenceData(context.refData).then((refData) => {
        this.refDataValueField = refData.valueField || '';
        this.refDataService.fetchItems(refData).then((items) => {
          this.refDataElements = items;
        });
      });
    }
  }

  /**
   * Handle dashboard context change by simply updating the url.
   *
   * @param value id of the element or record
   */
  private async onContextChange(value: string | number | undefined | null) {
    if (
      !this.dashboard?.id ||
      !this.dashboard?.page?.id ||
      !this.dashboard.page.context ||
      !this.contextType
    )
      return;
    if (value) {
      this.router.navigate(['.'], {
        relativeTo: this.route,
        queryParams: {
          id: value,
        },
      });
      // const urlArr = this.router.url.split('/');
      // urlArr[urlArr.length - 1] = `${parentDashboardId}?id=${value}`;
      // this.router.navigateByUrl(urlArr.join('/'));
    } else {
      this.snackBar.openSnackBar(
        this.translate.instant(
          'models.dashboard.context.notifications.loadDefault'
        )
      );
      this.router.navigate(['.'], { relativeTo: this.route });
      // const urlArr = this.router.url.split('/');
      // urlArr[urlArr.length - 1] = parentDashboardId;
      // this.router.navigateByUrl(urlArr.join('/'));
    }
  }

  /** Initializes the dashboard context */
  private initContext() {
    if (!this.dashboard?.page?.context || !this.dashboard?.id) return;
    // Checks if the dashboard has context attached to it
    const contentWithContext = this.dashboard?.page?.contentWithContext || [];
    const id = this.dashboard.id;
    const dContext = contentWithContext.find((c) => c.content === id);

    if (!dContext) return;

    // If it has updated the form
    if ('element' in dContext) {
      this.contextId.setValue(dContext.element, { emitEvent: false });
    } else if ('record' in dContext) {
      // Get record by id
      this.apollo
        .query<RecordQueryResponse>({
          query: GET_RECORD_BY_ID,
          variables: {
            id: dContext.record,
          },
        })
        .pipe(takeUntil(this.destroy$))
        .subscribe((res) => {
          if (res?.data) {
            this.contextRecord = res.data.record;
            this.contextId.setValue(dContext.record, { emitEvent: false });
          }
        });
    }
  }

  /**
   * Reorders button actions.
   *
   * @param event Drop event
   */
  public onButtonActionDrop(event: CdkDragDrop<typeof this.buttonActions>) {
    if (event.previousIndex === event.currentIndex) return;

    moveItemInArray(
      this.buttonActions,
      event.previousIndex,
      event.currentIndex
    );

    this.dashboardService.saveDashboardButtons(this.buttonActions);
  }

  /**
   * Open settings modal.
   */
  public async onOpenSettings(): Promise<void> {
    const { ViewSettingsModalComponent } = await import(
      '../../../components/view-settings-modal/view-settings-modal.component'
    );
    const dialogRef = this.dialog.open(ViewSettingsModalComponent, {
      data: {
        type: this.isStep ? 'step' : 'page',
        applicationId: this.applicationId,
        page: this.isStep ? undefined : this.dashboard?.page,
        step: this.isStep ? this.dashboard?.step : undefined,
        visible: this.dashboard?.page?.visible,
        icon: this.isStep
          ? this.dashboard?.step?.icon
          : this.dashboard?.page?.icon,
        accessData: {
          access: this.dashboard?.permissions,
          application: this.applicationId,
          objectTypeName: this.translate.instant(
            'common.' + this.isStep ? 'step' : 'page' + '.one'
          ),
        },
        canUpdate: this.dashboard?.page
          ? this.dashboard?.page.canUpdate
          : this.dashboard?.step
          ? this.dashboard?.step.canUpdate
          : false,
      },
    });
    // Subscribes to settings updates
    const subscription = dialogRef.componentInstance?.onUpdate
      .pipe(takeUntil(this.destroy$))
      .subscribe((updates: any) => {
        if (updates) {
          if (this.isStep) {
            this.dashboard = {
              ...this.dashboard,
              ...(updates.permissions && updates),
              step: {
                ...this.dashboard?.step,
                ...(!updates.permissions && updates),
              },
            };
          } else {
            this.dashboard = {
              ...this.dashboard,
              ...(updates.permissions && updates),
              page: {
                ...this.dashboard?.page,
                ...(!updates.permissions && updates),
              },
            };
          }
        }
      });
    // Unsubscribe to dialog onUpdate event
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe(() => {
      subscription?.unsubscribe();
    });
  }
}

