import { Apollo } from 'apollo-angular';
import { FormBuilder, Validators } from '@angular/forms';
import { Component, Inject } from '@angular/core';
import { DUPLICATE_APPLICATION } from './graphql/mutations';
import {
  Application,
  DuplicateApplicationMutationResponse,
} from '@oort-front/shared';
import { TranslateService } from '@ngx-translate/core';
import { CommonModule } from '@angular/common';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { DialogRef, DIALOG_DATA } from '@angular/cdk/dialog';
import { TranslateModule } from '@ngx-translate/core';
import { DialogModule } from '@oort-front/ui';
import {
  ButtonModule,
  SnackbarService,
  FormWrapperModule,
} from '@oort-front/ui';

/**
 * Duplicate application component (modal)
 */
@Component({
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    FormWrapperModule,
    ReactiveFormsModule,
    TranslateModule,
    DialogModule,
    ButtonModule,
  ],
  selector: 'app-duplicate-application-modal',
  templateUrl: './duplicate-application-modal.component.html',
  styleUrls: ['./duplicate-application-modal.component.scss'],
})
export class DuplicateApplicationModalComponent {
  public currentApp: Application;
  public duplicateForm = this.fb.group({
    name: ['', Validators.required],
  });

  /**
   * Duplicate application component.
   *
   * @param snackBar Shared snackbar service
   * @param fb Angular form builder
   * @param apollo Apollo service
   * @param dialogRef Dialog ref
   * @param translateService Angular translate service
   * @param data Injected dialog data
   */
  constructor(
    private snackBar: SnackbarService,
    private fb: FormBuilder,
    private apollo: Apollo,
    public dialogRef: DialogRef<DuplicateApplicationModalComponent>,
    private translateService: TranslateService,
    @Inject(DIALOG_DATA) public data: any
  ) {
    this.currentApp = data;
  }

  /**
   * Submit duplicate application form.
   * Send mutation.
   */
  onSubmit(): void {
    this.apollo
      .mutate<DuplicateApplicationMutationResponse>({
        mutation: DUPLICATE_APPLICATION,
        variables: {
          name: this.duplicateForm.value.name,
          application: this.currentApp.id,
        },
      })
      .subscribe({
        next: ({ errors, data }) => {
          if (errors) {
            this.snackBar.openSnackBar(
              this.translateService.instant(
                'common.notifications.objectNotDuplicated',
                {
                  type: this.translateService
                    .instant('common.application.one')
                    .toLowerCase(),
                  error: errors ? errors[0].message : '',
                }
              ),
              { error: true }
            );
          } else {
            this.snackBar.openSnackBar(
              this.translateService.instant(
                'common.notifications.objectDuplicated',
                {
                  type: this.translateService
                    .instant('common.application.one')
                    .toLowerCase(),
                  value: this.currentApp.name,
                }
              )
            );
          }
          this.dialogRef.close(data?.duplicateApplication as any);
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
        },
      });
  }
}

import { Component, Inject, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import {
  DialogModule,
  variants as ButtonVariants,
  categories as ButtonCategories,
  FormWrapperModule,
  SelectMenuModule,
  ButtonModule,
  ToggleModule,
  DividerModule,
} from '@oort-front/ui';
import {
  FormControl,
  FormGroup,
  FormsModule,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { TranslateModule } from '@ngx-translate/core';
import { DialogRef, DIALOG_DATA } from '@angular/cdk/dialog';
import { get } from 'lodash';
import { RawEditorSettings } from 'tinymce';
import { EditorModule } from '@tinymce/tinymce-angular';
import {
  EditorService,
  EditorControlComponent,
  DataTemplateService,
  INLINE_EDITOR_CONFIG,
  ButtonActionT,
} from '@oort-front/shared';
import { Router } from '@angular/router';

/**
 * Create a form group for the button action
 *
 * @param data Data to initialize the form
 * @returns the form group
 */
const createButtonActionForm = (data?: ButtonActionT) => {
  return new FormGroup({
    text: new FormControl(get(data, 'text', ''), Validators.required),
    href: new FormControl(get(data, 'href', ''), Validators.required),
    variant: new FormControl(get(data, 'variant', 'primary')),
    category: new FormControl(get(data, 'category', 'secondary')),
    openInNewTab: new FormControl(get(data, 'openInNewTab', true)),
  });
};

/** Component for editing a dashboard button action */
@Component({
  selector: 'app-edit-button-action',
  standalone: true,
  imports: [
    CommonModule,
    DialogModule,
    FormsModule,
    ReactiveFormsModule,
    TranslateModule,
    FormWrapperModule,
    SelectMenuModule,
    ButtonModule,
    ToggleModule,
    EditorModule,
    EditorControlComponent,
    DividerModule,
  ],
  templateUrl: './edit-button-action.component.html',
  styleUrls: ['./edit-button-action.component.scss'],
})
export class EditButtonActionComponent implements OnInit {
  public form: ReturnType<typeof createButtonActionForm>;

  public variants = ButtonVariants;
  public categories = ButtonCategories;

  public isNew: boolean;

  /** tinymce href editor */
  public hrefEditor: RawEditorSettings = INLINE_EDITOR_CONFIG;

  /**
   * Component for editing a dashboard button action
   *
   * @param dialogRef dialog reference
   * @param data initial button action
   * @param editorService editor service used to get main URL and current language
   * @param dataTemplateService Shared data template service
   * @param router Router service
   */
  constructor(
    public dialogRef: DialogRef<ButtonActionT>,
    @Inject(DIALOG_DATA) private data: ButtonActionT,
    private editorService: EditorService,
    private dataTemplateService: DataTemplateService,
    private router: Router
  ) {
    this.form = createButtonActionForm(data);
    this.isNew = !data;

    // Set the editor base url based on the environment file
    this.hrefEditor.base_url = editorService.url;
    // Set the editor language
    this.hrefEditor.language = editorService.language;
  }

  ngOnInit(): void {
    this.editorService.addCalcAndKeysAutoCompleter(
      this.hrefEditor,
      this.dataTemplateService.getAutoCompleterPageKeys()
    );
  }

  /** On click on the preview button open the href */
  public preview(): void {
    let href = this.form.get('href')?.value;
    if (href) {
      //regex to verify if it's a page id key
      const regex = /{{page\((.*?)\)}}/;
      const match = href.match(regex);
      if (match) {
        href = this.dataTemplateService.getButtonLink(match[1]);
      }
      const isNewTab = this.form.get('openInNewTab')?.value ?? true;
      if (isNewTab) window.open(href, '_blank');
      else this.router.navigate([href]);
    }
  }

  /** On click on the save button close the dialog with the form value */
  public onSubmit(): void {
    this.dialogRef.close(this.form.value as any);
  }
}

import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, UntypedFormArray, Validators } from '@angular/forms';
import { DIALOG_DATA, DialogRef } from '@angular/cdk/dialog';
import {
  ApiConfiguration,
  Application,
  Channel,
  Form,
  PullJob,
  status,
  authType,
  cronValidator,
  ApplicationsApplicationNodesQueryResponse,
  ApiConfigurationsQueryResponse,
  FormQueryResponse,
  FormsQueryResponse,
  StatusOptionsComponent,
} from '@oort-front/shared';
import { Apollo, QueryRef } from 'apollo-angular';
import {
  GET_API_CONFIGURATIONS,
  GET_SHORT_FORM_BY_ID,
  GET_FORM_NAMES,
  GET_ROUTING_KEYS,
} from '../../graphql/queries';
import {
  BehaviorSubject,
  Observable,
  Subscription,
  debounceTime,
  distinctUntilChanged,
} from 'rxjs';
import get from 'lodash/get';
import {
  getCachedValues,
  updateQueryUniqueValues,
} from '../../../../../utils/update-queries';
import { CommonModule, DOCUMENT } from '@angular/common';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { TranslateModule } from '@ngx-translate/core';
import {
  ReadableCronModule,
  CronExpressionControlModule,
} from '@oort-front/shared';
import {
  TooltipModule,
  ButtonModule,
  ExpansionPanelModule,
  SelectMenuModule,
  FormWrapperModule,
  TextareaModule,
  GraphQLSelectModule,
  IconModule,
} from '@oort-front/ui';
import { DialogModule } from '@oort-front/ui';
import { MonacoEditorModule } from 'ngx-monaco-editor-v2';

/** Items per page for pagination */
const ITEMS_PER_PAGE = 10;

/** Default fields */
const DEFAULT_FIELDS = ['createdBy'];

/** Pull job modal component */
@Component({
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    TranslateModule,
    DialogModule,
    GraphQLSelectModule,
    ReadableCronModule,
    TooltipModule,
    ExpansionPanelModule,
    CronExpressionControlModule,
    IconModule,
    TextareaModule,
    ButtonModule,
    SelectMenuModule,
    FormWrapperModule,
    StatusOptionsComponent,
    MonacoEditorModule,
  ],
  selector: 'app-edit-pull-job-modal',
  templateUrl: './edit-pull-job-modal.component.html',
  styleUrls: ['./edit-pull-job-modal.component.scss'],
})
export class EditPullJobModalComponent implements OnInit {
  // === REACTIVE FORM ===
  public formGroup = this.fb.group({
    name: [get(this.data, 'pullJob.name', ''), Validators.required],
    status: [get(this.data, 'pullJob.status', ''), Validators.required],
    apiConfiguration: [
      get(this.data, 'pullJob.apiConfiguration.id', ''),
      Validators.required,
    ],
    url: [get(this.data, 'pullJob.url', '')],
    path: [get(this.data, 'pullJob.path', '')],
    schedule: [
      get(this.data, 'pullJob.schedule', ''),
      [Validators.required, cronValidator()],
    ],
    convertTo: [get(this.data, 'pullJob.convertTo.id', '')],
    channel: [get(this.data, 'pullJob.channel.id', '')],
    mapping: this.fb.array(
      this.data.pullJob && this.data.pullJob.mapping
        ? Object.keys(this.data.pullJob.mapping).map((x: any) =>
            this.fb.group({
              name: [x, Validators.required],
              value: [this.data.pullJob?.mapping[x], Validators.required],
            })
          )
        : []
    ),
    rawMapping: [
      this.data.pullJob && this.data.pullJob.mapping
        ? JSON.stringify(this.data.pullJob?.mapping, null, 2)
        : '',
    ],
    uniqueIdentifiers: [get(this.data, 'pullJob.uniqueIdentifiers', [])],
  });
  isHardcoded = true;

  // === FORMS ===
  public formsQuery!: QueryRef<FormsQueryResponse>;

  // === CHANNELS ===
  private applicationsLoading = true;
  public applications = new BehaviorSubject<Application[]>([]);
  public applications$!: Observable<Application[]>;
  private cachedApplications: Application[] = [];
  private applicationsQuery!: QueryRef<ApplicationsApplicationNodesQueryResponse>;
  private applicationsPageInfo = {
    endCursor: '',
    hasNextPage: true,
  };

  // === API ===
  public apiConfigurations: ApiConfiguration[] = [];
  public apiConfigurationsQuery!: QueryRef<ApiConfigurationsQueryResponse>;

  // === DATA ===
  public statusChoices = Object.values(status);
  public fields: any[] = [];
  private fieldsSubscription?: Subscription;

  // === RAW JSON UTILITY ===
  public openRawJSON = false;

  /** @returns pull job mapping as form array */
  get mappingArray(): UntypedFormArray {
    return this.formGroup.get('mapping') as UntypedFormArray;
  }

  /** @returns default API configuration */
  get defaultApiConfiguration(): ApiConfiguration | null {
    return this.data.pullJob?.apiConfiguration || null;
  }

  /** @returns default convert to form */
  get defaultForm(): Form | null {
    return this.data.pullJob?.convertTo || null;
  }

  /** @returns default channel */
  get defaultChannel(): Channel | null {
    return this.data.pullJob?.channel || null;
  }

  /** Monaco editor configuration, for raw edition */
  public editorOptions = {
    theme: 'vs-dark',
    language: 'json',
    fixedOverflowWidgets: true,
  };

  /**
   * Pull job modal component
   *
   * @param fb Angular form builder
   * @param dialogRef Dialog ref
   * @param apollo Apollo service
   * @param document Document
   * @param data Modal injected data
   * @param data.channels list of available channels
   * @param data.pullJob pull job
   */
  constructor(
    private fb: FormBuilder,
    public dialogRef: DialogRef<EditPullJobModalComponent>,
    private apollo: Apollo,
    @Inject(DOCUMENT) private document: Document,
    @Inject(DIALOG_DATA)
    public data: {
      channels: Channel[];
      pullJob?: PullJob;
    }
  ) {}

  ngOnInit(): void {
    this.formsQuery = this.apollo.watchQuery<FormsQueryResponse>({
      query: GET_FORM_NAMES,
      variables: {
        first: ITEMS_PER_PAGE,
        sortField: 'name',
      },
    });

    this.apiConfigurationsQuery =
      this.apollo.watchQuery<ApiConfigurationsQueryResponse>({
        query: GET_API_CONFIGURATIONS,
        variables: {
          first: ITEMS_PER_PAGE,
        },
      });
    this.apiConfigurationsQuery.valueChanges.subscribe(
      ({ data }) =>
        (this.apiConfigurations = data.apiConfigurations.edges.map(
          (x) => x.node
        ))
    );

    // Fetch form fields if any for mapping
    if (this.data.pullJob?.convertTo?.id) {
      this.getFields(this.data.pullJob?.convertTo.id);
    }
    this.formGroup.get('convertTo')?.valueChanges.subscribe((res) => {
      if (res) {
        this.getFields(res);
      }
    });

    // Fetch the applications to get the channels
    this.applicationsQuery =
      this.apollo.watchQuery<ApplicationsApplicationNodesQueryResponse>({
        query: GET_ROUTING_KEYS,
        variables: {
          first: ITEMS_PER_PAGE,
          afterCursor: this.applicationsPageInfo.endCursor,
        },
      });

    // this.applications$ = this.applications.asObservable();
    this.applicationsQuery.valueChanges.subscribe(({ data, loading }) => {
      this.updateValues(data, loading);
    });

    // Set boolean to allow additional fields if it's not isHardcoded
    this.isHardcoded = !(
      this.data.pullJob &&
      this.data.pullJob.apiConfiguration &&
      this.data.pullJob.apiConfiguration.authType &&
      this.data.pullJob.apiConfiguration.authType === authType.public
    );
    this.formGroup
      .get('apiConfiguration')
      ?.valueChanges.subscribe((apiConfiguration: string | null) => {
        if (apiConfiguration) {
          const api = this.apiConfigurations.find(
            (x) => x.id === apiConfiguration
          );
          this.isHardcoded = !(
            api &&
            api.authType &&
            api.authType === authType.public
          );
        }
      });

    this.formGroup
      .get('rawMapping')
      ?.valueChanges.pipe(debounceTime(500), distinctUntilChanged())
      .subscribe((value: any) => {
        const mapping = JSON.parse(value || '{}');
        this.formGroup.setControl(
          'mapping',
          this.fb.array(
            Object.keys(mapping).map((x: any) =>
              this.fb.group({
                name: [x, Validators.required],
                value: [mapping[x], Validators.required],
              })
            )
          )
        );
      });
  }

  /**
   * Get fields from form id.
   *
   * @param id Id of selected form.
   */
  private getFields(id: string): void {
    if (this.fieldsSubscription) {
      this.fieldsSubscription.unsubscribe();
    }
    this.fieldsSubscription = this.apollo
      .watchQuery<FormQueryResponse>({
        query: GET_SHORT_FORM_BY_ID,
        variables: {
          id,
        },
      })
      .valueChanges.subscribe((resForm) => {
        if (resForm.data.form) {
          this.fields = resForm.data.form.fields || [];
          this.fields = this.fields.concat(
            DEFAULT_FIELDS.map((x) => ({ name: x }))
          );
        }
      });
  }

  /**
   * Filters fields so we cannot add a multiple mapping for the same one.
   *
   * @param name Field name.
   * @returns Filtered fields.
   */
  public filteredFields(name: string): any[] {
    return this.fields.filter(
      (field) =>
        field.name === name ||
        !this.formGroup.value.mapping?.some((x: any) => x.name === field.name)
    );
  }

  /**
   * Removes element from the mapping
   *
   * @param index mapping element index.
   */
  onDeleteElement(index: number): void {
    this.mappingArray.removeAt(index);
  }

  /**
   * Adds new element to the mapping.
   */
  onAddElement(): void {
    this.mappingArray.push(
      this.fb.group({
        name: ['', Validators.required],
        value: ['', Validators.required],
      })
    );
  }

  /**
   * Toggles the edit mode and update form values accordingly.
   */
  toggleRawJSON(): void {
    this.synchronizeFormValueData();
    this.openRawJSON = !this.openRawJSON;
  }

  /**
   * Synchronize mapping of the form value to the raw mapping if user don't want to open raw JSON
   */
  private synchronizeFormValueData() {
    if (!this.openRawJSON) {
      const mapping = this.formGroup
        .get('mapping')
        ?.value.reduce(
          (o: any, field: any) => ({ ...o, [field.name]: field.value }),
          {}
        );
      this.formGroup
        .get('rawMapping')
        ?.setValue(JSON.stringify(mapping, null, 2));
    }
  }

  /**
   * Synchronizes mapping values on update button click.
   *
   * @returns Return form value.
   */
  returnFormValue(): any {
    this.synchronizeFormValueData();
    return this.formGroup.value;
  }

  /**
   * Adds scroll listener to channels select.
   */
  onOpenApplicationSelect(): void {
    const panel = this.document.getElementById('optionList');
    if (panel) {
      panel.onscroll = (event: any) => this.loadOnScrollApplication(event);
    }
  }

  /**
   * Fetches more channels on scroll.
   *
   * @param e scroll event.
   */
  private loadOnScrollApplication(e: any): void {
    if (
      e.target.scrollHeight - (e.target.clientHeight + e.target.scrollTop) <
      50
    ) {
      if (!this.applicationsLoading && this.applicationsPageInfo.hasNextPage) {
        this.applicationsLoading = true;
        const variables = {
          first: ITEMS_PER_PAGE,
          afterCursor: this.applicationsPageInfo.endCursor,
        };
        const cachedValues: ApplicationsApplicationNodesQueryResponse =
          getCachedValues(this.apollo.client, GET_ROUTING_KEYS, variables);
        if (cachedValues) {
          this.updateValues(cachedValues, false);
        } else {
          this.applicationsQuery
            .fetchMore({
              variables,
            })
            .then((results) =>
              this.updateValues(results.data, results.loading)
            );
        }
      }
    }
  }

  /**
   * Changes the query according to search text
   *
   * @param search Search text from the graphql select
   */
  public onFormSearchChange(search: string): void {
    const variables = this.formsQuery.variables;
    this.formsQuery.refetch({
      ...variables,
      filter: {
        logic: 'and',
        filters: [
          {
            field: 'name',
            operator: 'contains',
            value: search,
          },
        ],
      },
    });
  }

  /**
   * Update application data value
   *
   * @param data query response data
   * @param loading loading status
   */
  private updateValues(
    data: ApplicationsApplicationNodesQueryResponse,
    loading: boolean
  ) {
    const nodes = data.applications.edges
      .map((x) => x.node)
      .filter((x) => (x.channels ? x.channels.length > 0 : false));
    this.cachedApplications = updateQueryUniqueValues(
      this.cachedApplications,
      nodes
    );
    this.applications.next(this.cachedApplications);
    this.applicationsPageInfo = data.applications.pageInfo;
    this.applicationsLoading = loading;
  }
}

import {
  Component,
  EventEmitter,
  Input,
  OnInit,
  Output,
  ViewChild,
  TemplateRef,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { UnsubscribeComponent, ListFilterComponent } from '@oort-front/shared';
import { debounceTime, distinctUntilChanged, takeUntil } from 'rxjs/operators';
import { FormBuilder, ReactiveFormsModule } from '@angular/forms';
import {
  ButtonModule,
  SelectMenuModule,
  FormWrapperModule,
} from '@oort-front/ui';
import { TranslateModule } from '@ngx-translate/core';

/**
 * Filter used by the resources component
 */
@Component({
  selector: 'app-api-configuration-filter',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    ListFilterComponent,
    ButtonModule,
    SelectMenuModule,
    FormWrapperModule,
    TranslateModule,
  ],
  templateUrl: './filter.component.html',
  styleUrls: ['./filter.component.scss'],
})
export class FilterComponent extends UnsubscribeComponent implements OnInit {
  @Input() loading = false;
  @Output() filter = new EventEmitter<any>();
  /** Reference to expanded filter template */
  @ViewChild('expandedFilter')
  expandedFilter!: TemplateRef<any>;

  public form = this.fb.group({
    statusFilter: [''],
  });
  public show = false;

  /**
   * FilterComponent constructor.
   *
   * @param fb Used to create reactive forms.
   */
  constructor(private fb: FormBuilder) {
    super();
  }

  ngOnInit(): void {
    this.form.valueChanges
      .pipe(
        debounceTime(1000),
        distinctUntilChanged(),
        takeUntil(this.destroy$)
      )
      .subscribe((value) => {
        this.emitFilter(value);
      });
  }

  /**
   * Emits the filter value, so the main component can get it.
   *
   * @param value Value to be emitted.
   */
  private emitFilter(value: any): void {
    this.filter.emit(value);
  }

  /**
   * Clears form.
   */
  clear(): void {
    this.form.reset();
  }
}

import { CommonModule } from '@angular/common';
import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { FormBuilder, ReactiveFormsModule } from '@angular/forms';
import { ListFilterComponent, UnsubscribeComponent } from '@oort-front/shared';
import { debounceTime, distinctUntilChanged, takeUntil } from 'rxjs';

/**
 * Filter used by the reference data component
 */
@Component({
  selector: 'app-reference-data-filter',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, ListFilterComponent],
  template: `
    <form [formGroup]="form">
      <shared-list-filter
        [hasSiblingFilters]="false"
        [loading]="loading"
      ></shared-list-filter>
    </form>
  `,
})
export class FilterComponent extends UnsubscribeComponent implements OnInit {
  @Input() loading = false;
  @Output() filter = new EventEmitter<any>();

  public form = this.fb.group({});

  /**
   * FilterComponent constructor.
   *
   * @param fb Used to create reactive forms.
   */
  constructor(private fb: FormBuilder) {
    super();
  }

  ngOnInit(): void {
    this.form.valueChanges
      .pipe(
        debounceTime(1000),
        distinctUntilChanged(),
        takeUntil(this.destroy$)
      )
      .subscribe((value: any) => {
        this.emitFilter(value);
      });
  }

  /**
   * Emits the filter value, so the main component can get it.
   *
   * @param value filter value
   */
  private emitFilter(value: any): void {
    const filters: any[] = [];
    if (value?.search) {
      filters.push({
        field: 'name',
        operator: 'contains',
        value: value.search,
      });
    }
    const filter = {
      logic: 'and',
      filters,
    };
    this.filter.emit(filter);
  }

  /**
   * Clears form.
   */
  clear(): void {
    this.form.reset();
  }
}

