/**
 * Form answer page component.
 */
@Component({
  selector: 'app-form-answer',
  templateUrl: './form-answer.component.html',
  styleUrls: ['./form-answer.component.scss'],
})
export class FormAnswerComponent implements OnInit {
  /** Reference to shared form component */
  @ViewChild(FormComponent)
  private formComponent?: FormComponent;
  /** Loading indicator */
  public loading = true;
  /** Current form id */
  public id = '';
  /** Current form */
  public form?: Form;
  /** Is form completed */
  public completed = false;

  /**
   * Form answer page component.
   *
   * @param apollo Apollo service
   * @param route Angular activated route
   * @param breadcrumbService Shared breadcrumb service
   */
  constructor(
    private apollo: Apollo,
    private route: ActivatedRoute,
    private breadcrumbService: BreadcrumbService
  ) {}

  ngOnInit(): void {
    this.id = this.route.snapshot.paramMap.get('id') || '';
    if (this.id !== null) {
      this.apollo
        .watchQuery<FormQueryResponse>({
          query: GET_SHORT_FORM_BY_ID,
          variables: {
            id: this.id,
          },
        })
        .valueChanges.subscribe(({ data, loading }) => {
          this.loading = loading;
          this.form = data.form;
          this.breadcrumbService.setBreadcrumb(
            '@form',
            this.form.name as string
          );
          this.breadcrumbService.setBreadcrumb(
