   * Reset the form, removing answers.
   */
  clearForm(): void {
    this.formComponent?.reset();
  }
}

import { Apollo } from 'apollo-angular';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import {
  EDIT_FORM_NAME,
  EDIT_FORM_PERMISSIONS,
  EDIT_FORM_STATUS,
  EDIT_FORM_STRUCTURE,
} from './graphql/mutations';
import { GET_SHORT_FORM_BY_ID } from './graphql/queries';
import { Dialog } from '@angular/cdk/dialog';
import {
  AuthService,
  Form,
  ConfirmService,
  BreadcrumbService,
  status,
  FormQueryResponse,
  EditFormMutationResponse,
} from '@oort-front/shared';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { TranslateService } from '@ngx-translate/core';
import { SnackbarService } from '@oort-front/ui';
import { FormControl } from '@angular/forms';
import { isEqual } from 'lodash';
import { GraphQLError } from 'graphql';

/**
 * Form builder page
 */
@Component({
  selector: 'app-form-builder',
  templateUrl: './form-builder.component.html',
  styleUrls: ['./form-builder.component.scss'],
})
export class FormBuilderComponent implements OnInit {
  public loading = true;
  public id = '';
  public form?: Form;
  public structure: any;
  public activeVersions = false;
  public activeVersion: any;

  // === ENUM OF FORM STATUSES ===
  public statusControl = new FormControl<string | undefined>('');
  public statusChoices = Object.values(status);

  // === FORM EDITION ===
  public canEditName = false;
  public formActive = false;
  public hasChanges = false;
  private isStep = false;
  /** Prevent form builder to display multiple modals when exiting. */
  private deactivating = false;

  /**
   * Form builder page
   *
   * @param apollo Apollo service
   * @param route Angular activated route
   * @param router Angular router
   * @param snackBar Shared snackbar service
   * @param dialog Dialog service
   * @param authService Shared authentication service
   * @param confirmService Shared confirm service
   * @param translate Angular translate service
   * @param breadcrumbService Shared breadcrumb service
   */
  constructor(
    private apollo: Apollo,
    private route: ActivatedRoute,
    private router: Router,
    private snackBar: SnackbarService,
    public dialog: Dialog,
    private authService: AuthService,
    private confirmService: ConfirmService,
    private translate: TranslateService,
    private breadcrumbService: BreadcrumbService
  ) {}

  /**
   * Show modal confirmation before leave the page if has changes on form
   *
   * @returns boolean of observable of boolean
   */
  canDeactivate(): Observable<boolean> | boolean {
    if (this.hasChanges && !this.deactivating) {
      this.deactivating = true;
      const dialogRef = this.confirmService.openConfirmModal({
        title: this.translate.instant('components.form.update.exit'),
        content: this.translate.instant('components.form.update.exitMessage'),
        confirmText: this.translate.instant('components.confirmModal.confirm'),
        confirmVariant: 'primary',
      });
      return dialogRef.closed.pipe(
        map((value) => {
          this.deactivating = false;
          if (value) {
            this.authService.canLogout.next(true);
            window.localStorage.removeItem(`form:${this.id}`);
            return true;
          }
          return false;
        })
      );
    }
    return true;
  }

  ngOnInit(): void {
    this.formActive = false;
    this.statusControl.valueChanges.subscribe((status) => {
      if (status) {
        this.updateStatus(status);
      }
    });
    this.id = this.route.snapshot.paramMap.get('id') || '';
    if (this.id !== null) {
      this.apollo
        .watchQuery<FormQueryResponse>({
          query: GET_SHORT_FORM_BY_ID,
          variables: {
            id: this.id,
          },
        })
        .valueChanges.subscribe({
          next: ({ data, loading }) => {
            if (data.form) {
              this.loading = loading;
              this.form = data.form;
              this.statusControl.setValue(this.form.status, {
                emitEvent: false,
              });
              this.breadcrumbService.setBreadcrumb(
                '@form',
                this.form.name as string
              );
              this.breadcrumbService.setBreadcrumb(
                '@resource',
                this.form.resource?.name as string
              );
              // this.breadcrumbService.setResourceName();
              this.canEditName = this.form?.canUpdate || false;
              const storedStructure = window.localStorage.getItem(
                `form:${this.id}`
              );
              this.structure = storedStructure
                ? storedStructure
                : this.form.structure;
              if (this.structure !== this.form.structure) {
                this.hasChanges = true;
                this.authService.canLogout.next(!this.hasChanges);
              }
            } else {
              this.snackBar.openSnackBar(
                this.translate.instant(
                  'common.notifications.accessNotProvided',
                  {
                    type: this.translate
                      .instant('common.form.one')
                      .toLowerCase(),
                    error: '',
                  }
                ),
                { error: true }
              );
              // redirect to default screen if error
              this.router.navigate(['/forms']);
            }
          },
          error: (err) => {
            this.snackBar.openSnackBar(err.message, { error: true });
            // redirect to default screen if error
            this.router.navigate(['/forms']);
          },
        });
    } else {
      this.loading = false;
      // redirect to default screen if error
      this.router.navigate(['/forms']);
    }
  }

  /**
   * Activate or deactivate form
   */
  toggleFormActive(): void {
    if (this.form?.canUpdate) {
      this.formActive = !this.formActive;
    }
  }

  /**
   * Save form structure
   *
   * @param structure form structure
   */
  public async onSave(structure: any): Promise<void> {
    if (!this.form?.id) {
      alert('not valid');
    } else {
      const { StatusModalComponent } = await import('@oort-front/shared');
      const statusModal = this.dialog.open(StatusModalComponent, {
        disableClose: true,
        data: {
          title: this.translate.instant('components.formBuilder.saveSurvey'),
          showSpinner: true,
        },
      });
      this.apollo
        .mutate<EditFormMutationResponse>({
          mutation: EDIT_FORM_STRUCTURE,
          variables: {
            id: this.form.id,
            structure,
          },
        })
        .subscribe({
          next: ({ errors, data }) => {
            if (errors) {
              this.snackBar.openSnackBar(errors[0].message, {
                error: true,
              });
              statusModal.close();
            } else {
              this.snackBar.openSnackBar(
                this.translate.instant('common.notifications.objectUpdated', {
                  type: this.translate.instant('common.form.one'),
                  value: this.form?.name,
                })
              );
              this.form = { ...data?.editForm, structure };
              this.structure = structure;
              localStorage.removeItem(`form:${this.id}`);
              this.hasChanges = false;
              this.authService.canLogout.next(true);
              statusModal.close();
            }
          },
          error: (err) => {
            this.snackBar.openSnackBar(err.message, { error: true });
            statusModal.close();
          },
        });
    }
  }

  /**
   * Update the status of the form.
   *
   * @param status new status
   */
  private async updateStatus(status: string): Promise<void> {
    const statusModal = await this.getStatusModalRef();
    this.apollo
      .mutate<EditFormMutationResponse>({
        mutation: EDIT_FORM_STATUS,
        variables: {
          id: this.id,
          status,
        },
      })
      .subscribe({
        next: ({ errors, data }) => {
          this.handleFormMutationResponse(data, errors);
          statusModal.close();
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
        },
      });
  }

  /**
   * Handles form mutations response
   *
   * @param {EditFormMutationResponse} data data retrieved from the graphql mutation
   * @param {GraphQLError[]} errors errors from the graphql mutation if any
   * @param {string} formName new form name if any
   */
  private handleFormMutationResponse(
    data: EditFormMutationResponse | null | undefined,
    errors: readonly GraphQLError[] | undefined,
    formName?: string
  ) {
    if (errors) {
      this.snackBar.openSnackBar(
        this.translate.instant('common.notifications.objectNotUpdated', {
          type: this.translate.instant(
            formName ? 'common.form.one' : 'common.status'
          ),
          error: errors ? errors[0].message : '',
        }),
        { error: true }
      );
    } else {
      const successMessage = formName
        ? this.translate.instant('common.notifications.objectUpdated', {
            type: this.translate.instant('common.form.one').toLowerCase(),
            value: formName,
          })
        : this.translate.instant('common.notifications.statusUpdated', {
            value: data?.editForm.status,
          });
      this.snackBar.openSnackBar(successMessage);
      if (formName) {
        this.form = { ...this.form, name: data?.editForm.name };
        this.breadcrumbService.setBreadcrumb('@form', this.form.name as string);
      } else {
        this.form = { ...this.form, status: data?.editForm.status };
        this.statusControl.setValue(data?.editForm.status, {
          emitEvent: false,
        });
      }
    }
  }

  /**
   * Open a modal and returns it's reference
   *
   * @returns Status modal
   */
  private async getStatusModalRef() {
    const { StatusModalComponent } = await import('@oort-front/shared');
    return this.dialog.open(StatusModalComponent, {
      disableClose: true,
      data: {
        title: 'Saving survey',
        showSpinner: true,
      },
    });
  }

  /**
   * Available in previous version to change the template.
   *
   * @param id id of version
   */
  setTemplate(id: string): void {
    this.apollo
      .watchQuery<FormQueryResponse>({
        query: GET_SHORT_FORM_BY_ID,
        variables: {
          id,
        },
      })
      .valueChanges.subscribe(({ data }) => {
        this.structure = data.form.structure;
      });
  }

  /**
   * Available in previous version to change the version.
   *
   * @param e new version
   */
  public onOpenVersion(e: any): void {
    this.activeVersion = e;
    this.structure = this.activeVersion.data;
    // this.surveyCreator.makeNewViewActive('test');
    // this.surveyCreator.saveSurveyFunc = null;
  }

  /**
   * Available in previous version to change the version.
   */
  public resetActiveVersion(): void {
    this.activeVersion = null;
    this.structure = this.form?.structure;
    // this.surveyCreator.makeNewViewActive('designer');
    // this.surveyCreator.saveSurveyFunc = this.saveMySurvey;
  }

  /**
   * Edit the form name.
   *
   * @param {string} formName new form name
   */
  public async saveName(formName: string): Promise<void> {
    if (formName && formName !== this.form?.name) {
      const statusModal = await this.getStatusModalRef();
      this.apollo
        .mutate<EditFormMutationResponse>({
          mutation: EDIT_FORM_NAME,
          variables: {
            id: this.id,
            name: formName,
          },
        })
        .subscribe(({ errors, data }) => {
          this.handleFormMutationResponse(data, errors, formName);
          statusModal.close();
        });
    }
  }

  /**
   * Edit the permissions layer.
   *
   * @param e new permissions
   */
  async saveAccess(e: any): Promise<void> {
    const { StatusModalComponent } = await import('@oort-front/shared');
    const statusModal = this.dialog.open(StatusModalComponent, {
      disableClose: true,
      data: {
        title: 'Saving survey',
        showSpinner: true,
      },
    });
    this.apollo
      .mutate<EditFormMutationResponse>({
        mutation: EDIT_FORM_PERMISSIONS,
        variables: {
          id: this.id,
          permissions: e,
        },
      })
      .subscribe({
        next: ({ errors, data }) => {
          if (errors) {
            this.snackBar.openSnackBar(
              this.translate.instant('common.notifications.objectNotUpdated', {
                type: this.translate.instant('common.access'),
                error: errors ? errors[0].message : '',
              }),
              { error: true }
            );
            statusModal.close();
          } else {
            this.snackBar.openSnackBar(
              this.translate.instant('common.notifications.objectUpdated', {
                type: this.translate.instant('common.access'),
                value: '',
              })
            );
            this.form = { ...data?.editForm, structure: this.structure };
            statusModal.close();
          }
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
        },
      });
  }

  /**
   * Called when form structure is updated
   *
   * @param event update event
   */
  formStructureChange(event: any): void {
    this.hasChanges = !isEqual(
      JSON.parse(event),
      JSON.parse(this.form?.structure || '{}')
    );
    localStorage.setItem(`form:${this.id}`, event);
    this.authService.canLogout.next(!this.hasChanges);
  }
}

import { Apollo } from 'apollo-angular';
import { Component, OnInit, ViewChild } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

import {
  Form,
  Page,
  Step,
  FormComponent as SharedFormComponent,
  UnsubscribeComponent,
  StepQueryResponse,
  FormQueryResponse,
  PageQueryResponse,
} from '@oort-front/shared';
import {
  GET_SHORT_FORM_BY_ID,
  GET_PAGE_BY_ID,
  GET_STEP_BY_ID,
} from './graphql/queries';
import { takeUntil } from 'rxjs/operators';

/**
 * Application preview form page component.
 */
@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.scss'],
})
export class FormComponent extends UnsubscribeComponent implements OnInit {
  @ViewChild(SharedFormComponent)
  private formComponent?: SharedFormComponent;

  // === DATA ===
  public loading = true;
  public id = '';
  public form?: Form;
  public completed = false;
  public hideNewRecord = false;

  // === ROUTER ===
  public page?: Page;
  public step?: Step;

  // === ROUTE ===
  public isStep = false;

  /**
   * Application preview form page component.
   *
   * @param apollo Apollo service
   * @param route Angular current route
   * @param router Angular router
   */
  constructor(
    private apollo: Apollo,
    private route: ActivatedRoute,
    private router: Router
  ) {
    super();
  }

  /**
   * Gets the form from the page parameters.
   */
  ngOnInit(): void {
    this.route.params.pipe(takeUntil(this.destroy$)).subscribe((params) => {
      this.loading = true;
      this.id = params.id;
      this.isStep = this.router.url.includes('/workflow/');
      if (this.isStep) {
        this.apollo
          .query<StepQueryResponse>({
            query: GET_STEP_BY_ID,
            variables: {
              id: this.id,
            },
          })
          .subscribe((res) => {
            this.step = res.data.step;
            this.apollo
              .query<FormQueryResponse>({
                query: GET_SHORT_FORM_BY_ID,
                variables: {
                  id: this.step.content,
                },
              })
              .subscribe(({ data, loading }) => {
                this.form = data.form;
                this.loading = loading;
              });
          });
      } else {
        this.apollo
          .query<PageQueryResponse>({
            query: GET_PAGE_BY_ID,
            variables: {
              id: this.id,
            },
          })
          .subscribe((res) => {
            this.page = res.data.page;
            this.apollo
              .query<FormQueryResponse>({
                query: GET_SHORT_FORM_BY_ID,
                variables: {
                  id: this.page.content,
                },
              })
              .subscribe(({ data, loading }) => {
                if (data) {
                  this.form = data.form;
                }
                this.loading = loading;
              });
          });
      }
    });
  }

  /**
   * Handles complete event.
   *
   * @param e complete event
   * @param e.completed is event completed
   * @param e.hideNewRecord do we need to hide new record
   */
  onComplete(e: { completed: boolean; hideNewRecord?: boolean }): void {
    this.completed = e.completed;
    this.hideNewRecord = e.hideNewRecord || false;
  }

  /**
   * Resets the form component.
   */
  clearForm(): void {
    this.formComponent?.reset();
  }
}

import { Apollo } from 'apollo-angular';
import { Component, OnInit, ViewChild } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Subscription } from 'rxjs';
import {
  Form,
  Page,
  Step,
  FormComponent as SharedFormComponent,
  ApplicationService,
  WorkflowService,
  UnsubscribeComponent,
  StepQueryResponse,
  FormQueryResponse,
  PageQueryResponse,
} from '@oort-front/shared';
import {
  GET_SHORT_FORM_BY_ID,
  GET_PAGE_BY_ID,
  GET_STEP_BY_ID,
} from './graphql/queries';
import { switchMap, takeUntil } from 'rxjs/operators';
import { TranslateService } from '@ngx-translate/core';
import { Dialog } from '@angular/cdk/dialog';

/**
 * Form page in application.
 */
@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.scss'],
})
export class FormComponent extends UnsubscribeComponent implements OnInit {
  @ViewChild(SharedFormComponent)
  private formComponent?: SharedFormComponent;

  /** Loading indicator */
  public loading = true;
  /** Current form id */
  public id = '';
  /** Current application id */
  public applicationId = '';
  /** Current form */
  public form?: Form;
  /** Is form completed */
  public completed = false;
  /** Should possibility to add new records be hidden */
  public hideNewRecord = false;
  /** Query subscription */
  public querySubscription?: Subscription;
  /** Can name be edited */
  public canEditName = false;
  /** Is name form active */
  public formActive = false;
  /** Application page form is part of ( if any ) */
  public page?: Page;
  /** Application step form is part of ( if any ) */
  public step?: Step;
  /** Is form part of workflow step */
  public isStep = false;

  /**
   * Form page in application
   *
   * @param applicationService Shared application service
   * @param workflowService Shared workflow service
   * @param apollo Apollo service
   * @param route Angular activated route
   * @param router Angular router
   * @param translate Angular translate service
   * @param dialog CDK Dialog service
   */
  constructor(
    private applicationService: ApplicationService,
    private workflowService: WorkflowService,
    private apollo: Apollo,
    private route: ActivatedRoute,
    private router: Router,
    private translate: TranslateService,
    private dialog: Dialog
  ) {
    super();
  }

  ngOnInit(): void {
    this.route.params.pipe(takeUntil(this.destroy$)).subscribe((params) => {
      this.formActive = false;
      this.loading = true;
      this.id = params.id;
      this.isStep = this.router.url.includes('/workflow/');
      // If a query is already loading, cancel it
      if (this.querySubscription) {
        this.querySubscription.unsubscribe();
      }
      if (this.isStep) {
        this.querySubscription = this.apollo
          .query<StepQueryResponse>({
            query: GET_STEP_BY_ID,
            variables: {
              id: this.id,
            },
          })
          .pipe(
            switchMap(({ data }) => {
              this.step = data.step;
              return this.getFormQuery(this.step.content ?? '');
            })
          )
          .subscribe(({ data, loading }) => {
            this.handleFormQueryResponse(data, 'step');
            this.loading = loading;
          });
      } else {
        this.querySubscription = this.apollo
          .query<PageQueryResponse>({
            query: GET_PAGE_BY_ID,
            variables: {
              id: this.id,
            },
          })
          .pipe(
            switchMap(({ data }) => {
              this.page = data.page;
              return this.getFormQuery(this.page.content ?? '');
            })
          )
          .subscribe(({ data, loading }) => {
            this.handleFormQueryResponse(data, 'page');
            this.loading = loading;
          });
      }
    });
  }

  /**
   * Returns query for the given id
   *
   * @param {string} id form id to query
   * @returns form query for the given id
   */
  private getFormQuery(id: string) {
    return this.apollo.query<FormQueryResponse>({
      query: GET_SHORT_FORM_BY_ID,
      variables: {
        id,
      },
    });
  }

  /**
   * Handle response for the form query
   *
   * @param {FormQueryResponse} data form query response data
   * @param from from where the form query is done
   */
  private handleFormQueryResponse(
    data: FormQueryResponse,
    from: 'step' | 'page'
  ) {
    this.form = data.form;
    this.canEditName = this.form?.canUpdate ?? false;
    this.applicationId =
      (from === 'step'
        ? this.step?.workflow?.page?.application?.id
        : this.page?.application?.id) ?? '';
  }

  /**
   * Toggle activation of form.
   */
  toggleFormActive(): void {
    if (this.step?.canUpdate || this.page?.canUpdate) {
      this.formActive = !this.formActive;
    }
  }

  /**
   * Update the name of the tab.
   *
   * @param {string} tabName new tab name
   */
  saveName(tabName: string): void {
    const currentName = this.page ? this.page.name : this.step?.name;
    if (tabName && tabName !== currentName) {
      if (this.isStep) {
        // If form is workflow step
        const callback = () => {
          this.step = { ...this.step, name: tabName };
        };
        this.workflowService.updateStepName(
          {
            id: this.id,
            name: tabName,
          },
          callback
        );
      } else {
        // If form is page
        const callback = () => {
          this.page = { ...this.page, name: tabName };
        };
        this.applicationService.updatePageName(
          {
            id: this.id,
            name: tabName,
          },
          callback
        );
      }
    }
  }

  /**
   * Complete form
   *
   * @param e completion event
   * @param e.completed is completed
   * @param e.hideNewRecord do we show new record button
   */
  onComplete(e: { completed: boolean; hideNewRecord?: boolean }): void {
    this.completed = e.completed;
    this.hideNewRecord = e.hideNewRecord || false;
  }

  /**
   * Clear status of the form.
   */
  clearForm(): void {
    this.formComponent?.reset();
  }

  /**
   * Edit form. Open form builder.
   */
  editForm(): void {
    if (this.isStep && this.step) {
      this.router.navigate([`./builder/${this.step.content}`], {
        relativeTo: this.route,
      });
    } else {
      if (this.page) {
        this.router.navigate([`./builder/${this.page.content}`], {
          relativeTo: this.route,
        });
      }
    }
  }

  /**
   * Open settings modal.
   */
  public async onOpenSettings(): Promise<void> {
    const { ViewSettingsModalComponent } = await import(
      '../../../components/view-settings-modal/view-settings-modal.component'
    );
    const dialogRef = this.dialog.open(ViewSettingsModalComponent, {
      data: {
        type: this.isStep ? 'step' : 'page',
        applicationId: this.applicationId,
        page: this.isStep ? undefined : this.page,
        step: this.isStep ? this.step : undefined,
        icon: this.isStep ? this.step?.icon : this.page?.icon,
        visible: this.page?.visible,
        accessData: {
          access: this.page
            ? this.page.permissions
            : this.step
            ? this.step.permissions
            : null,
          application: this.applicationId,
          objectTypeName: this.translate.instant(
            'common.' + this.isStep ? 'step' : 'page' + '.one'
          ),
        },
        canUpdate:
          !this.formActive &&
          (this.page
            ? this.page.canUpdate
            : this.step
            ? this.step.canUpdate
            : false),
      },
    });
    // Subscribes to settings updates
    const subscription = dialogRef.componentInstance?.onUpdate
      .pipe(takeUntil(this.destroy$))
      .subscribe((updates: any) => {
        if (updates) {
          if (this.isStep) {
            this.step = { ...this.step, ...updates };
          } else {
            this.page = { ...this.page, ...updates };
          }
          if (updates.permissions) {
            this.form = {
              ...this.form,
              ...updates,
            };
          }
        }
      });
    // Unsubscribe to dialog onUpdate event
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe(() => {
      subscription?.unsubscribe();
    });
  }
}

