import { Component, OnInit } from '@angular/core';
import { Dialog } from '@angular/cdk/dialog';
import {
  AddPullJobMutationResponse,
  Channel,
  DeletePullJobMutationResponse,
  EditPullJobMutationResponse,
  PullJob,
  ConfirmService,
  UnsubscribeComponent,
  PullJobsNodesQueryResponse,
} from '@oort-front/shared';
import { Apollo, QueryRef } from 'apollo-angular';
import { GET_PULL_JOBS } from './graphql/queries';
import {
  ADD_PULL_JOB,
  DELETE_PULL_JOB,
  EDIT_PULL_JOB,
} from './graphql/mutations';
import { TranslateService } from '@ngx-translate/core';
import { ApolloQueryResult } from '@apollo/client';
import {
  getCachedValues,
  updateQueryUniqueValues,
} from '../../../utils/update-queries';
import { takeUntil } from 'rxjs';
import { SnackbarService, UIPageChangeEvent } from '@oort-front/ui';

/**
 * Limit of pull jobs shown at once.
 */
const ITEMS_PER_PAGE = 10;

/**
 * Shows all pull-jobs available.
 */
@Component({
  selector: 'app-pull-jobs',
  templateUrl: './pull-jobs.component.html',
  styleUrls: ['./pull-jobs.component.scss'],
})
export class PullJobsComponent extends UnsubscribeComponent implements OnInit {
  // === DATA ===
  public loading = true;
  private pullJobsQuery!: QueryRef<PullJobsNodesQueryResponse>;
  public pullJobs = new Array<PullJob>();
  public cachedPullJobs: PullJob[] = [];

  public displayedColumns: string[] = [
    'name',
    'status',
    // 'apiConfiguration',
    // 'convertTo',
    'schedule',
    'actions',
  ];

  // === SUBSCRIPTIONS ===
  private channels: Channel[] = [];

  public pageInfo = {
    pageIndex: 0,
    pageSize: ITEMS_PER_PAGE,
    length: 0,
    endCursor: '',
  };

  /**
   * PullJobsComponent constructor.
   *
   * @param dialog Used to show popup dialog.
   * @param apollo Loads the pull jobs.
   * @param snackBar Service used to show a snackbar.
   * @param confirmService Shared confirm service
   * @param translate Service used to get the translations.
   */
  constructor(
    public dialog: Dialog,
    private apollo: Apollo,
    private snackBar: SnackbarService,
    private confirmService: ConfirmService,
    private translate: TranslateService
  ) {
    super();
  }

  ngOnInit(): void {
    this.pullJobsQuery = this.apollo.watchQuery<PullJobsNodesQueryResponse>({
      query: GET_PULL_JOBS,
      variables: {
        first: ITEMS_PER_PAGE,
        afterCursor: this.pageInfo.endCursor,
      },
    });

    this.pullJobsQuery.valueChanges
      .pipe(takeUntil(this.destroy$))
      .subscribe((results) => {
        this.updateValues(results.data, results.loading);
      });
  }

  /**
   * Handles page event.
   *
   * @param e page event.
   */
  onPage(e: UIPageChangeEvent): void {
    this.pageInfo.pageIndex = e.pageIndex;
    if (
      ((e.pageIndex > e.previousPageIndex &&
        e.pageIndex * this.pageInfo.pageSize >= this.cachedPullJobs.length) ||
        e.pageSize > this.pageInfo.pageSize) &&
      e.totalItems > this.cachedPullJobs.length
    ) {
      this.loading = true;
      const variables = {
        first: ITEMS_PER_PAGE,
        afterCursor: this.pageInfo.endCursor,
      };
      const cachedValues: PullJobsNodesQueryResponse = getCachedValues(
        this.apollo.client,
        GET_PULL_JOBS,
        variables
      );
      if (cachedValues) {
        this.updateValues(cachedValues, false);
      } else {
        this.pullJobsQuery
          .fetchMore({ variables })
          .then((results: ApolloQueryResult<PullJobsNodesQueryResponse>) => {
            this.updateValues(results.data, results.loading);
          });
      }
    } else {
      this.pullJobs = this.cachedPullJobs.slice(
        ITEMS_PER_PAGE * this.pageInfo.pageIndex,
        ITEMS_PER_PAGE * (this.pageInfo.pageIndex + 1)
      );
    }
  }

  /**
   * Displays the AddSubscription modal.
   * Creates the pull job on close.
   */
  async onAdd(): Promise<void> {
    const { EditPullJobModalComponent } = await import(
      './components/edit-pull-job-modal/edit-pull-job-modal.component'
    );
    const dialogRef = this.dialog.open(EditPullJobModalComponent, {
      autoFocus: false,
      data: {
        channels: this.channels,
      },
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        const variables = {
          name: value.name,
          status: value.status,
          apiConfiguration: value.apiConfiguration,
        };
        Object.assign(
          variables,
          value.url && { url: value.url },
          value.path && { path: value.path },
          value.schedule && { schedule: value.schedule },
          value.convertTo && { convertTo: value.convertTo },
          value.channel && { channel: value.channel },
          value.rawMapping && { mapping: JSON.parse(value.rawMapping) },
          value.uniqueIdentifiers && {
            uniqueIdentifiers: value.uniqueIdentifiers,
          }
        );
        this.apollo
          .mutate<AddPullJobMutationResponse>({
            mutation: ADD_PULL_JOB,
            variables,
          })
          .subscribe({
            next: ({ errors, data }) => {
              if (errors) {
                this.snackBar.openSnackBar(
                  this.translate.instant(
                    'common.notifications.objectNotCreated',
                    {
                      type: this.translate
                        .instant('common.pullJob.one')
                        .toLowerCase(),
                      error: errors ? errors[0].message : '',
                    }
                  ),
                  { error: true }
                );
              } else {
                if (data?.addPullJob) {
                  this.snackBar.openSnackBar(
                    this.translate.instant(
                      'common.notifications.objectCreated',
                      {
                        type: this.translate.instant('common.pullJob.one'),
                        value: value.name,
                      }
                    )
                  );
                  if (this.cachedPullJobs.length === this.pageInfo.length) {
                    this.cachedPullJobs = this.cachedPullJobs.concat([
                      data?.addPullJob,
                    ]);
                    this.pullJobs = this.cachedPullJobs.slice(
                      ITEMS_PER_PAGE * this.pageInfo.pageIndex,
                      ITEMS_PER_PAGE * (this.pageInfo.pageIndex + 1)
                    );
                  }
                  this.pageInfo.length += 1;
                }
              }
            },
            error: (err) => {
              this.snackBar.openSnackBar(err.message, { error: true });
            },
          });
      }
    });
  }

  /**
   * Deletes a pull job.
   *
   * @param element pull job to delete.
   */
  onDelete(element: any): void {
    if (element) {
      const dialogRef = this.confirmService.openConfirmModal({
        title: this.translate.instant('components.pullJob.delete.title'),
        content: this.translate.instant(
          'components.pullJob.delete.confirmationMessage',
          {
            name: element.name,
          }
        ),
        confirmText: this.translate.instant('components.confirmModal.delete'),
        confirmVariant: 'danger',
      });
      dialogRef.closed
        .pipe(takeUntil(this.destroy$))
        .subscribe((value: any) => {
          if (value) {
            this.apollo
              .mutate<DeletePullJobMutationResponse>({
                mutation: DELETE_PULL_JOB,
                variables: {
                  id: element.id,
                },
              })
              .subscribe({
                next: ({ errors, data }) => {
                  if (errors) {
                    this.snackBar.openSnackBar(
                      this.translate.instant(
                        'common.notifications.objectNotDeleted',
                        {
                          value: this.translate.instant('common.pullJob.one'),
                          error: errors ? errors[0].message : '',
                        }
                      ),
                      { error: true }
                    );
                  } else {
                    if (data?.deletePullJob) {
                      this.snackBar.openSnackBar(
                        this.translate.instant(
                          'common.notifications.objectDeleted',
                          {
                            value: this.translate.instant('common.pullJob.one'),
                          }
                        )
                      );
                      this.cachedPullJobs = this.cachedPullJobs.filter(
                        (x) => x.id !== data?.deletePullJob.id
                      );
                      this.pageInfo.length -= 1;
                      this.pullJobs = this.cachedPullJobs.slice(
                        ITEMS_PER_PAGE * this.pageInfo.pageIndex,
                        ITEMS_PER_PAGE * (this.pageInfo.pageIndex + 1)
                      );
                    }
                  }
                },
                error: (err) => {
                  this.snackBar.openSnackBar(err.message, { error: true });
                },
              });
          }
        });
    }
  }

  /**
   * Edits a pull job.
   *
   * @param element pull job to edit.
   */
  async onEdit(element: any): Promise<void> {
    const { EditPullJobModalComponent } = await import(
      './components/edit-pull-job-modal/edit-pull-job-modal.component'
    );
    const dialogRef = this.dialog.open(EditPullJobModalComponent, {
      data: {
        channels: this.channels,
        pullJob: element,
      },
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        const variables = {
          id: element.id,
        };
        Object.assign(
          variables,
          value.name && { name: value.name },
          value.status && { status: value.status },
          value.apiConfiguration && {
            apiConfiguration: value.apiConfiguration,
          },
          value.url && { url: value.url },
          value.path && { path: value.path },
          value.schedule && { schedule: value.schedule },
          value.convertTo && { convertTo: value.convertTo },
          value.channel && { channel: value.channel },
          value.rawMapping && { mapping: JSON.parse(value.rawMapping) },
          value.uniqueIdentifiers && {
            uniqueIdentifiers: value.uniqueIdentifiers,
          }
        );
        this.apollo
          .mutate<EditPullJobMutationResponse>({
            mutation: EDIT_PULL_JOB,
            variables,
          })
          .subscribe({
            next: ({ errors, data }) => {
              if (errors) {
                this.snackBar.openSnackBar(
                  this.translate.instant(
                    'common.notifications.objectNotUpdated',
                    {
                      value: this.translate.instant('common.pullJob.one'),
                      error: errors ? errors[0].message : '',
                    }
                  ),
                  { error: true }
                );
              } else {
                if (data?.editPullJob) {
                  this.snackBar.openSnackBar(
                    this.translate.instant(
                      'common.notifications.objectUpdated',
                      {
                        type: this.translate
                          .instant('common.pullJob.one')
                          .toLowerCase(),
                        value: value.name,
                      }
                    )
                  );
                  this.cachedPullJobs = this.cachedPullJobs.map(
                    (pullJob: PullJob) => {
                      if (pullJob.id === data?.editPullJob.id) {
                        pullJob = data?.editPullJob || pullJob;
                      }
                      return pullJob;
                    }
                  );
                  this.pullJobs = this.cachedPullJobs.slice(
                    ITEMS_PER_PAGE * this.pageInfo.pageIndex,
                    ITEMS_PER_PAGE * (this.pageInfo.pageIndex + 1)
                  );
                }
              }
            },
            error: (err) => {
              this.snackBar.openSnackBar(err.message, { error: true });
            },
          });
      }
    });
  }

  /**
   * Updates local list with given data
   *
   * @param data New values to update forms
   * @param loading Loading state
   */
  private updateValues(
    data: PullJobsNodesQueryResponse,
    loading: boolean
  ): void {
    const mappedValues = data.pullJobs.edges.map((x) => x.node);
    this.cachedPullJobs = updateQueryUniqueValues(
      this.cachedPullJobs,
      mappedValues
    );
    this.pageInfo.length = data.pullJobs.totalCount;
    this.pageInfo.endCursor = data.pullJobs.pageInfo.endCursor;
    this.pullJobs = this.cachedPullJobs.slice(
      ITEMS_PER_PAGE * this.pageInfo.pageIndex,
      ITEMS_PER_PAGE * (this.pageInfo.pageIndex + 1)
    );
    this.loading = loading;
  }
}

import { Component, OnInit } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import {
  Record,
  Form,
  ConfirmService,
  Resource,
  DownloadService,
  UnsubscribeComponent,
  ResourceRecordsNodesQueryResponse,
  DeleteRecordMutationResponse,
  RestoreRecordMutationResponse,
} from '@oort-front/shared';
import { Apollo, QueryRef } from 'apollo-angular';
import get from 'lodash/get';
import {
  getCachedValues,
  updateQueryUniqueValues,
} from '../../../../utils/update-queries';
import { DELETE_RECORD, RESTORE_RECORD } from '../graphql/mutations';
import { GET_RESOURCE_RECORDS } from './graphql/queries';
import {
  SnackbarService,
  UIPageChangeEvent,
  handleTablePageEvent,
} from '@oort-front/ui';
import { takeUntil } from 'rxjs';
import { GraphQLError } from 'graphql';

/** Quantity of resource that will be loaded at once. */
const ITEMS_PER_PAGE = 10;

/**
 * Default fields for the records.
 */
const RECORDS_DEFAULT_COLUMNS = ['_incrementalId', '_actions'];

/**
 * Records tab of resource page.
 */
@Component({
  selector: 'app-records-tab',
  templateUrl: './records-tab.component.html',
  styleUrls: ['./records-tab.component.scss'],
})
export class RecordsTabComponent
  extends UnsubscribeComponent
  implements OnInit
{
  private recordsQuery!: QueryRef<ResourceRecordsNodesQueryResponse>;
  public dataSource = new Array<Record>();
  private cachedRecords: Record[] = [];
  public resource!: Resource;
  recordsDefaultColumns: string[] = RECORDS_DEFAULT_COLUMNS;
  displayedColumnsRecords: string[] = [];

  showDeletedRecords = false;
  public pageInfo = {
    pageIndex: 0,
    pageSize: ITEMS_PER_PAGE,
    length: 0,
    endCursor: '',
  };
  public loading = true;
  public showUpload = false;

  /** @returns True if the records tab is empty */
  get empty(): boolean {
    return !this.loading && this.dataSource.length === 0;
  }

  /**
   * Records tab of resource page
   *
   * @param apollo Apollo service
   * @param translate Angular translate service
   * @param snackBar Shared snackbar service
   * @param confirmService Shared confirm service
   * @param downloadService Service used to download.
   */
  constructor(
    private apollo: Apollo,
    private translate: TranslateService,
    private snackBar: SnackbarService,
    private confirmService: ConfirmService,
    private downloadService: DownloadService
  ) {
    super();
  }

  ngOnInit(): void {
    const state = history.state;
    this.resource = get(state, 'resource', null);
    this.setDisplayedColumns(false);
    this.recordsQuery =
      this.apollo.watchQuery<ResourceRecordsNodesQueryResponse>({
        query: GET_RESOURCE_RECORDS,
        variables: {
          id: this.resource?.id,
          first: ITEMS_PER_PAGE,
          afterCursor: null,
          display: false,
          showDeletedRecords: this.showDeletedRecords,
        },
      });
    this.recordsQuery.valueChanges
      .pipe(takeUntil(this.destroy$))
      .subscribe(({ data, loading }) => {
        this.updateValues(data, loading);
      });
  }

  /**
   * Deletes a record if authorized, open a confirmation modal if it's a hard delete.
   *
   * @param element Element to delete.
   * @param e click event.
   */
  public onDeleteRecord(element: any, e: any): void {
    e.stopPropagation();
    if (this.showDeletedRecords) {
      const dialogRef = this.confirmService.openConfirmModal({
        title: this.translate.instant('common.deleteObject', {
          name: this.translate.instant('common.record.one'),
        }),
        content: this.translate.instant(
          'components.record.delete.confirmationMessage',
          {
            name: element.name,
          }
        ),
        confirmText: this.translate.instant('components.confirmModal.delete'),
      });
      dialogRef.closed
        .pipe(takeUntil(this.destroy$))
        .subscribe((value: any) => {
          if (value) {
            this.deleteRecord(element.id);
          }
        });
    } else {
      this.deleteRecord(element.id);
    }
  }

  /**
   * Sends mutation to delete record.
   *
   * @param id Id of record to delete.
   */
  private deleteRecord(id: string): void {
    this.apollo
      .mutate<DeleteRecordMutationResponse>({
        mutation: DELETE_RECORD,
        variables: {
          id,
          hardDelete: this.showDeletedRecords,
        },
      })
      .subscribe({
        next: ({ errors }) => {
          this.handleRecordMutationResponse(
            {
              success: 'common.notifications.objectDeleted',
              error: 'common.notifications.objectNotDeleted',
            },
            errors
          );
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
        },
      });
  }

  /**
   * Handle record mutation response
   *
   * @param messageKeys Message keys containing success or error response messages
   * @param messageKeys.success success message key
   * @param messageKeys.error error message key
   * @param {GraphQLError[] | undefined} errors error array response if any
   */
  private handleRecordMutationResponse(
    messageKeys: {
      success: string;
      error: string;
    },
    errors: readonly GraphQLError[] | undefined
  ) {
    if (errors) {
      this.snackBar.openSnackBar(
        this.translate.instant(messageKeys.error, {
          value: this.translate.instant('common.record.one'),
          error: errors ? errors[0].message : '',
        }),
        { error: true }
      );
    } else {
      this.snackBar.openSnackBar(
        this.translate.instant(messageKeys.success, {
          value: this.translate.instant('common.record.one'),
        })
      );
      this.fetchRecords(true);
    }
  }

  /**
   * Restores an archived record.
   *
   * @param id Id of record to restore.
   * @param e click event.
   */
  public onRestoreRecord(id: string, e: any): void {
    e.stopPropagation();
    this.apollo
      .mutate<RestoreRecordMutationResponse>({
        mutation: RESTORE_RECORD,
        variables: {
          id,
        },
      })
      .subscribe({
        next: ({ errors }) => {
          this.handleRecordMutationResponse(
            {
              success: 'common.notifications.objectRestored',
              error: 'common.notifications.objectNotRestored',
            },
            errors
          );
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
        },
      });
  }

  /**
   * Get list of forms filtering by record form.
   *
   * @param record Record to filter templates with.
   * @returns list of different forms than the one used to create the record.
   */
  public filterTemplates(record: Record): Form[] {
    return get(this.resource, 'forms', []).filter(
      (x: Form) => x.id !== record.form?.id
    );
  }

  /**
   * Changes the list of displayed columns.
   *
   * @param core Is the form core.
   */
  private setDisplayedColumns(core: boolean): void {
    let columns = [];
    if (this.resource?.fields) {
      if (core) {
        for (const field of this.resource.fields.filter(
          (x: any) => x.isRequired === true
        )) {
          columns.push(field.name);
        }
      } else {
        for (const field of this.resource.fields) {
          columns.push(field.name);
        }
      }
    }
    const metadata = get(this.resource, 'metadata', []);
    columns = columns
      .filter((x) => {
        const fieldMeta = metadata.find((y) => y.name === x);
        return get(fieldMeta, 'canSee', false);
      })
      .concat(RECORDS_DEFAULT_COLUMNS);
    this.displayedColumnsRecords = columns;
  }

  /**
   * Downloads the list of records of the resource.
   *
   * @param type Type of the document to download ( excel or csv ).
   */
  onDownload(type: string): void {
    const path = `download/resource/records/${this.resource.id}`;
    const fileName = `${this.resource.name}.${type}`;
    const queryString = new URLSearchParams({ type }).toString();
    this.downloadService.getFile(
      `${path}?${queryString}`,
      `text/${type};charset=utf-8;`,
      fileName
    );
  }

  /**
   * Get the records template, for upload.
   */
  onDownloadTemplate(): void {
    const path = `download/resource/records/${this.resource.id}`;
    const queryString = new URLSearchParams({
      type: 'xlsx',
      template: 'true',
    }).toString();
    this.downloadService.getFile(
      `${path}?${queryString}`,
      `text/xlsx;charset=utf-8;`,
      `${this.resource.name}_template.xlsx`
    );
  }

  /**
   * Detects changes on the file.
   *
   * @param event new file event.
   */
  onFileChange(event: any): void {
    const file = event.files[0].rawFile;
    this.uploadFileData(file);
  }

  /**
   * Calls rest endpoint to upload new records for the resource.
   *
   * @param file File to upload.
   */
  uploadFileData(file: any): void {
    const path = `upload/resource/records/${this.resource.id}`;
    this.downloadService.uploadFile(path, file).subscribe({
      next: (res) => {
        if (res.status === 'OK') {
          this.fetchRecords(true);
          this.showUpload = false;
        }
      },
      error: (error: any) => {
        this.snackBar.openSnackBar(error.error, { error: true });
        this.showUpload = false;
      },
    });
  }

  /**
   * Toggle archive / active view.
   *
   * @param e click event.
   */
  onSwitchView(e: any): void {
    e.stopPropagation();
    this.showDeletedRecords = !this.showDeletedRecords;
    this.fetchRecords(true);
  }

  /**
   * Handles page event.
   *
   * @param e page event.
   */
  onPage(e: UIPageChangeEvent): void {
    const cachedData = handleTablePageEvent(
      e,
      this.pageInfo,
      this.cachedRecords
    );
    if (cachedData && cachedData.length === this.pageInfo.pageSize) {
      this.dataSource = cachedData;
    } else {
      this.fetchRecords();
    }
  }

  /**
   * Formats the passed value to be readable
   *
   * @param value Value to format
   * @returns Formatted value
   */
  formatValue(value: any): string {
    // Geo spatial field
    if (
      value &&
      typeof value === 'object' &&
      value.type === 'Feature' &&
      value.geometry
    ) {
      return [
        get(value, 'properties.address'),
        get(value, 'properties.countryName'),
      ]
        .filter((x) => x)
        .join(', ');
    }
    return value;
  }

  /**
   * Fetch records, using GraphQL
   *
   * @param refetch rebuild query
   */
  private fetchRecords(refetch?: boolean): void {
    this.loading = true;
    const variables = {
      first: this.pageInfo.pageSize,
      id: this.resource.id,
      afterCursor: refetch ? null : this.pageInfo.endCursor,
      display: false,
      showDeletedRecords: this.showDeletedRecords,
    };
    const cachedValues: ResourceRecordsNodesQueryResponse = getCachedValues(
      this.apollo.client,
      GET_RESOURCE_RECORDS,
      variables
    );
    if (refetch) {
      this.cachedRecords = [];
      this.pageInfo.pageIndex = 0;
    }
    if (cachedValues) {
      this.updateValues(cachedValues, false);
    } else {
      if (refetch) {
        this.recordsQuery.refetch(variables);
      } else {
        this.recordsQuery
          .fetchMore({
            variables,
          })
          .then((results) => this.updateValues(results.data, results.loading));
      }
    }
  }

  /**
   * Update record data value
   *
   * @param data query response data
   * @param loading loading status
   */
  private updateValues(
    data: ResourceRecordsNodesQueryResponse,
    loading: boolean
  ) {
    const mappedValues = data.resource.records.edges.map((x) => x.node);
    this.cachedRecords = updateQueryUniqueValues(
      this.cachedRecords,
      mappedValues
    );
    this.pageInfo.length = data.resource.records.totalCount;
    this.pageInfo.endCursor = data.resource.records.pageInfo.endCursor;
    this.dataSource = this.cachedRecords.slice(
      this.pageInfo.pageSize * this.pageInfo.pageIndex,
      this.pageInfo.pageSize * (this.pageInfo.pageIndex + 1)
    );
    this.loading = loading;
  }
}

