import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import {
  AbstractControl,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { TranslateService } from '@ngx-translate/core';
import {
  ApiConfiguration,
  ReferenceData,
  referenceDataType,
  BreadcrumbService,
  UnsubscribeComponent,
  ReferenceDataService,
  ApiConfigurationsQueryResponse,
  ReferenceDataQueryResponse,
  ApiConfigurationQueryResponse,
  EditReferenceDataMutationResponse,
} from '@oort-front/shared';
import { Apollo, QueryRef } from 'apollo-angular';
import { EDIT_REFERENCE_DATA } from './graphql/mutations';
import {
  GET_API_CONFIGURATION,
  GET_API_CONFIGURATIONS_NAMES,
  GET_REFERENCE_DATA,
} from './graphql/queries';
import { COMMA, ENTER, SPACE, TAB } from '@angular/cdk/keycodes';
import { takeUntil } from 'rxjs/operators';
import { firstValueFrom } from 'rxjs';
import { HttpErrorResponse } from '@angular/common/http';
import { inferTypeFromString } from './utils/inferTypeFromString';
import { get } from 'lodash';
import { SnackbarService, TextareaComponent } from '@oort-front/ui';
import { GraphQLError } from 'graphql';

/** Default graphql query */
const DEFAULT_QUERY = `query {\n  \n}`;
/** Default pagination parameter. */
const ITEMS_PER_PAGE = 10;
/** Available separator for csv */
const SEPARATOR_KEYS_CODE = [ENTER, COMMA, TAB, SPACE];

/**
 * Reference data page.
 */
@Component({
  selector: 'app-reference-data',
  templateUrl: './reference-data.component.html',
  styleUrls: ['./reference-data.component.scss'],
})
export class ReferenceDataComponent
  extends UnsubscribeComponent
  implements OnInit
{
  // === DATA ===
  public loading = true;
  public id = '';
  public referenceData?: ReferenceData;

  // === FORM ===
  public referenceForm!: ReturnType<typeof this.getRefDataForm>;
  public referenceTypeChoices = Object.values(referenceDataType);

  public selectedApiConfiguration?: ApiConfiguration;
  public apiConfigurationsQuery!: QueryRef<ApiConfigurationsQueryResponse>;

  public valueFields: NonNullable<ReferenceData['fields']> = [];
  public triedToGetFields = false;
  public loadingFields = false;
  readonly separatorKeysCodes: number[] = SEPARATOR_KEYS_CODE;

  // === CSV ===
  public csvValue = '';
  public newData: any = [];
  public csvLoading = false;
  public separator = new FormControl(',');

  @ViewChild('fieldInput') fieldInput?: ElementRef<HTMLInputElement>;
  @ViewChild('csvData') csvData?: TextareaComponent;

  // === MONACO EDITOR ===
  public editorOptions = {
    language: 'graphql',
    formatOnPaste: true,
  };

  /** @returns the graphqlQuery form control */
  get queryControl() {
    return this.referenceForm.get('query') as FormControl | null;
  }

  /** @returns name of reference model */
  get name(): AbstractControl | null {
    return this.referenceForm.get('name');
  }

  /** @returns type of reference model */
  get type(): string {
    return this.referenceForm.get('type')?.value || '';
  }

  /**
   * Reference data page.
   *
   * @param apollo Apollo service
   * @param route Angular route
   * @param snackBar Shared snackbar service
   * @param router Angular router
   * @param translateService Angular translate service
   * @param breadcrumbService Setups the breadcrumb component variables
   * @param refDataService Reference data service
   */
  constructor(
    private apollo: Apollo,
    private route: ActivatedRoute,
    private snackBar: SnackbarService,
    private router: Router,
    private translateService: TranslateService,
    private breadcrumbService: BreadcrumbService,
    private refDataService: ReferenceDataService
  ) {
    super();
  }

  /**
   * Build Reference data form group
   *
   * @returns Reference data form group
   */
  private getRefDataForm() {
    const form = new FormGroup({
      name: new FormControl(this.referenceData?.name, Validators.required),
      type: new FormControl(this.referenceData?.type, Validators.required),
      valueField: new FormControl(
        this.referenceData?.valueField,
        Validators.required
      ),
      fields: new FormControl(this.referenceData?.fields, Validators.required),
      apiConfiguration: new FormControl(
        this.referenceData?.apiConfiguration?.id
      ),
      query: new FormControl(this.referenceData?.query),
      path: new FormControl(this.referenceData?.path),
      data: new FormControl(this.referenceData?.data),
    });

    // Clear valueFields when type, apiConfiguration, path or query changes
    const clearFields = () => {
      this.triedToGetFields = false;
      this.valueFields = [];
      form.get('fields')?.setValue([]);
    };

    // Wait for the form to be initialized before subscribing to changes
    setTimeout(() => {
      form
        .get('type')
        ?.valueChanges.pipe(takeUntil(this.destroy$))
        .subscribe(() => {
          clearFields();

          // Clear the query field if the type is not GraphQL
          if (this.type !== referenceDataType.graphql)
            form.get('query')?.setValue('');

          // Set the default query if the type is GraphQL
          if (this.type === referenceDataType.graphql)
            form
              .get('query')
              ?.setValue(
                `# ${this.translateService.instant(
                  'pages.referenceData.tooltip.graphQLFilter'
                )}\n\n${DEFAULT_QUERY}`
              );
        });

      form
        .get('apiConfiguration')
        ?.valueChanges.pipe(takeUntil(this.destroy$))
        .subscribe(clearFields);

      form
        .get('path')
        ?.valueChanges.pipe(takeUntil(this.destroy$))
        .subscribe(clearFields);

      form
        .get('query')
        ?.valueChanges.pipe(takeUntil(this.destroy$))
        .subscribe(clearFields);
    }, 100);

    return form;
  }

  /**
   * Create the Reference data query, and subscribe to the query changes.
   */
  ngOnInit(): void {
    this.id = this.route.snapshot.paramMap.get('id') || '';
    if (this.id) {
      this.apollo
        .watchQuery<ReferenceDataQueryResponse>({
          query: GET_REFERENCE_DATA,
          variables: {
            id: this.id,
          },
        })
        .valueChanges.pipe(takeUntil(this.destroy$))
        .subscribe({
          next: ({ data, loading }) => {
            if (data.referenceData) {
              this.referenceData = data.referenceData;
              this.breadcrumbService.setBreadcrumb(
                '@referenceData',
                this.referenceData.name as string
              );
              this.csvValue =
                this.referenceData?.data && this.referenceData?.data.length > 0
                  ? this.convertToCSV(this.referenceData?.data)
                  : '';
              this.newData =
                this.referenceData?.data && this.referenceData?.data.length > 0
                  ? this.referenceData?.data
                  : [];
              this.referenceForm = this.getRefDataForm();
              this.valueFields = this.referenceForm?.get('fields')?.value || [];
              this.loadApiConfigurations(
                this.referenceForm?.get('type')?.value
              );
              // Adapt validators to the type of reference data
              this.referenceForm.get('type')?.valueChanges.subscribe((type) => {
                this.loadApiConfigurations(type);
              });
              this.loading = loading;
            } else {
              this.snackBar.openSnackBar(
                this.translateService.instant(
                  'common.notifications.accessNotProvided',
                  {
                    type: this.translateService
                      .instant('notification.term.resource')
                      .toLowerCase(),
                    error: '',
                  }
                ),
                { error: true }
              );
              this.router.navigate(['/referencedata']);
            }
          },
          error: (err) => {
            this.snackBar.openSnackBar(err.message, { error: true });
            this.router.navigate(['/referencedata']);
          },
        });
    }
  }

  /**
   * Load all Api Configurations.
   *
   * @param type type of API configuration
   */
  loadApiConfigurations(type?: any): void {
    if (['graphql', 'rest'].includes(type)) {
      this.referenceForm
        .get('apiConfiguration')
        ?.setValidators(Validators.required);
      this.referenceForm.get('query')?.setValidators(Validators.required);
      this.referenceForm.get('fields')?.setValidators(Validators.required);
      if (this.referenceForm.value.apiConfiguration) {
        this.apollo
          .query<ApiConfigurationQueryResponse>({
            query: GET_API_CONFIGURATION,
            variables: {
              id: this.referenceForm.value.apiConfiguration,
            },
          })
          .subscribe(({ data }) => {
            if (data.apiConfiguration) {
              this.selectedApiConfiguration = data.apiConfiguration;
            }
          });
      }

      this.apiConfigurationsQuery =
        this.apollo.watchQuery<ApiConfigurationsQueryResponse>({
          query: GET_API_CONFIGURATIONS_NAMES,
          variables: {
            first: ITEMS_PER_PAGE,
          },
        });
      // this.apiConfigurationsQuery.valueChanges.subscribe(({ loading }) => {
      //   this.loading = loading;
      // });
    } else {
      this.referenceForm.get('apiConfiguration')?.clearValidators();
      this.referenceForm.get('query')?.clearValidators();
      this.referenceForm.get('fields')?.clearValidators();
    }
    this.referenceForm?.get('apiConfiguration')?.updateValueAndValidity();
    this.referenceForm?.get('query')?.updateValueAndValidity();
    this.referenceForm?.get('fields')?.updateValueAndValidity();
  }

  /**
   * Edit the permissions layer.
   *
   * @param e permission event
   */
  saveAccess(e: any): void {
    this.loading = true;
    this.apollo
      .mutate<EditReferenceDataMutationResponse>({
        mutation: EDIT_REFERENCE_DATA,
        variables: {
          id: this.id,
          permissions: e,
        },
      })
      .subscribe({
        next: ({ errors, data, loading }) => {
          this.handleEditReferenceDataResponse(data, errors, loading);
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
        },
      });
  }

  /**
   * Handles the reference data mutation response
   *
   * @param {EditReferenceDataMutationResponse} data save mutation data
   * @param {GraphQLError[]} errors save mutation errors
   * @param {boolean} loading save mutation loading state
   * @param {boolean} usingForm if saved data comes from the reference data form
   */
  private handleEditReferenceDataResponse(
    data: EditReferenceDataMutationResponse | null | undefined,
    errors: readonly GraphQLError[] | undefined,
    loading: boolean,
    usingForm: boolean = false
  ) {
    if (errors) {
      this.snackBar.openSnackBar(
        this.translateService.instant('common.notifications.objectNotUpdated', {
          type: this.translateService.instant('common.referenceData.one'),
          error: errors ? errors[0].message : '',
        }),
        { error: true }
      );
    } else {
      if (data) {
        this.snackBar.openSnackBar(
          this.translateService.instant('common.notifications.objectUpdated', {
            type: this.translateService.instant('common.referenceData.one'),
            value: '',
          })
        );
        this.referenceData = data.editReferenceData;
      }
      if (usingForm) {
        this.referenceForm.markAsPristine();
      }
    }
    this.loading = loading;
  }

  /**
   * Edit the Reference data using referenceForm changes.
   */
  onSave(): void {
    this.loading = true;
    const variables = { id: this.id };
    Object.assign(
      variables,
      this.referenceForm.value.name !== this.referenceData?.name && {
        name: this.referenceForm.value.name,
      },
      this.referenceForm.value.type !== this.referenceData?.type && {
        type: this.referenceForm.value.type,
      },
      this.referenceForm.value.valueField !==
        this.referenceData?.valueField && {
        valueField: this.referenceForm.value.valueField,
      },
      this.referenceForm.value.fields !== this.referenceData?.fields && {
        fields: this.referenceForm.value.fields,
      }
    );
    if (
      ['graphql', 'rest'].includes(get(this.referenceForm, 'value.type', ''))
    ) {
      Object.assign(
        variables,
        this.referenceForm.value.apiConfiguration !==
          this.referenceData?.apiConfiguration?.id && {
          apiConfiguration: this.referenceForm.value.apiConfiguration,
        },
        this.referenceForm.value.path !== this.referenceData?.path && {
          path: this.referenceForm.value.path,
        },
        this.referenceForm.value.query !== this.referenceData?.query && {
          query: this.referenceForm.value.query,
        }
      );
    } else {
      Object.assign(
        variables,
        this.referenceForm.value.data !== this.referenceData?.data && {
          data: this.referenceForm.value.data,
        }
      );
    }
    this.apollo
      .mutate<EditReferenceDataMutationResponse>({
        mutation: EDIT_REFERENCE_DATA,
        variables,
      })
      .subscribe({
        next: ({ errors, data, loading }) => {
          this.handleEditReferenceDataResponse(data, errors, loading, true);
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
        },
      });
  }

  /**
   * Add an object to the chip list.
   *
   * @param event input event.
   */
  add(event: string | any): void {
    // use setTimeout to prevent add input value on focusout
    setTimeout(
      () => {
        const input =
          event.type === 'focusout'
            ? this.fieldInput?.nativeElement
            : event.input;
        const value =
          event.type === 'focusout'
            ? this.fieldInput?.nativeElement.value
            : event.value;

        // Add the mail
        if ((value || '').trim()) {
          // Deep copy needed for the edition
          const valueFieldsCopy = [...this.valueFields];
          valueFieldsCopy.push(value.trim());
          this.valueFields = valueFieldsCopy;
        }
        this.setReferenceFormValue();
        // Reset the input value
        if (input) {
          input.value = '';
        }
      },
      event.type === 'focusout' ? 500 : 0
    );
  }

  /**
   * Update reference form value programmatically with the current value fields
   */
  private setReferenceFormValue() {
    this.referenceForm?.get('fields')?.setValue(this.valueFields);
    this.referenceForm?.get('fields')?.updateValueAndValidity();
    this.referenceForm?.markAsDirty();
  }

  /**
   * Remove an object from the chip list.
   *
   * @param field field to remove.
   */
  remove(field: string): void {
    const index = this.valueFields.findIndex((x) => x.name === field);
    if (index >= 0) {
      // Deep copy needed for the edition
      const valueFieldsCopy = [...this.valueFields];
      valueFieldsCopy.splice(index, 1);
      this.valueFields = valueFieldsCopy;
    }
    this.setReferenceFormValue();
  }

  /**
   * Validate the CSV input and transform it into JSON object.
   */
  onValidateCSV(): void {
    this.csvLoading = true;
    const dataTemp = this.csvData?.value || '';
    if (dataTemp !== this.csvValue) {
      this.csvValue = dataTemp;
      this.newData = [];
      const lines = dataTemp.split('\n');
      const headers = lines[0]
        .split(this.separator.value || ',')
        .map((x: string) => x.trim());
      if (lines.length < 2) return;
      // Infer types from first line
      const fields = headers.reduce((acc: any, header: any) => {
        const value = lines[1]
          .split(this.separator.value || ',')
          [headers.indexOf(header)].trim();
        const type = inferTypeFromString(value);
        acc.push({ name: header, type });
        return acc;
      }, [] as { name: string; type: string }[]);
      this.valueFields = fields;
      for (let i = 1; i < lines.length; i++) {
        if (!lines[i]) continue;
        const obj: any = {};
        const currentline = lines[i]
          .split(this.separator.value || ',')
          .map((x: string) => x.trim());
        for (let j = 0; j < headers.length; j++) {
          obj[headers[j]] = currentline[j];
        }
        this.newData.push(obj);
      }
      this.referenceForm?.get('data')?.setValue(this.newData);
      this.referenceForm?.get('data')?.updateValueAndValidity();
      this.referenceForm?.get('fields')?.setValue(this.valueFields);
      this.referenceForm?.get('fields')?.updateValueAndValidity();
      this.referenceForm?.markAsDirty();
    }
    this.csvLoading = false;
  }

  /**
   * Convert the JSON into a csv format.
   *
   * @param obj json to convert
   * @returns csv
   */
  convertToCSV(obj: any): string {
    const array = [Object.keys(obj[0])].concat(obj);
    return array.map((it) => Object.values(it).toString()).join('\n');
  }

  /** Uses the API Configuration to get the fields and parse their types. */
  public async getFields() {
    const apiConfID = this.referenceForm.value.apiConfiguration;
    const path = this.referenceForm.value.path;
    const query = this.referenceForm.value.query;
    const type = this.referenceForm.value.type;
    if (!apiConfID || !query || !type) {
      const missingField = !this.referenceForm.value.apiConfiguration
        ? 'common.apiConfiguration.one'
        : !this.referenceForm.value.query
        ? 'pages.referenceData.queryName'
        : 'pages.referenceData.type';

      this.snackBar.openSnackBar(
        this.translateService.instant(
          'pages.referenceData.fields.missingConfig',
          {
            config: this.translateService.instant(missingField),
          }
        ),
        { error: true }
      );
      return;
    }
    // get the api configuration
    this.loadingFields = true;
    const query$ = this.apollo.query<ApiConfigurationQueryResponse>({
      query: GET_API_CONFIGURATION,
      variables: {
        id: apiConfID,
      },
    });

    const { data: apiConfData } = await firstValueFrom(query$);
    if (!apiConfData?.apiConfiguration) {
      this.loadingFields = false;
      return;
    }
    try {
      this.triedToGetFields = true;
      // get the fields
      const fields = await this.refDataService.getFields(
        apiConfData.apiConfiguration,
        path || '',
        query,
        type
      );
      this.valueFields = fields;
      this.referenceForm?.get('fields')?.setValue(this.valueFields);
    } catch (e) {
      if (e instanceof HttpErrorResponse) {
        this.snackBar.openSnackBar(e.message, { error: true });
      }
    }
    this.loadingFields = false;
  }

  /**
   * Remove a field from the list.
   *
   * @param field field to remove.
   */
  onRemoveField(field: any) {
    this.valueFields = this.valueFields.filter((x) => x.name !== field.name);
    this.referenceForm?.get('fields')?.setValue(this.valueFields);
  }

  /**
   * On initialization of editor, format code
   *
   * @param editor monaco editor used for scss edition
   */
  public initEditor(editor: any): void {
    const queryControl = this.queryControl;
    if (!queryControl) return;
    if (editor) {
      setTimeout(() => {
        editor
          .getAction('editor.action.formatDocument')
          .run()
          .finally(() => {
            queryControl.markAsPristine();
          });
      }, 100);
    }
  }
}

import { Component, OnInit } from '@angular/core';
import { Apollo, QueryRef } from 'apollo-angular';
import {
  AddReferenceDataMutationResponse,
  DeleteReferenceDataMutationResponse,
  ReferenceData,
  AuthService,
  ConfirmService,
  UnsubscribeComponent,
  ReferenceDatasQueryResponse,
} from '@oort-front/shared';
import { GET_REFERENCE_DATAS } from './graphql/queries';
import { ADD_REFERENCE_DATA, DELETE_REFERENCE_DATA } from './graphql/mutations';
import { Router } from '@angular/router';
import { TranslateService } from '@ngx-translate/core';
import { takeUntil } from 'rxjs/operators';
import {
  getCachedValues,
  updateQueryUniqueValues,
} from '../../../utils/update-queries';
import { Dialog } from '@angular/cdk/dialog';
import {
  TableSort,
  UIPageChangeEvent,
  handleTablePageEvent,
} from '@oort-front/ui';
import { ApolloQueryResult } from '@apollo/client';
import { SnackbarService } from '@oort-front/ui';

/** Default pagination settings. */
const ITEMS_PER_PAGE = 10;

/**
 * List of Reference data page.
 */
@Component({
  selector: 'app-reference-datas',
  templateUrl: './reference-datas.component.html',
  styleUrls: ['./reference-datas.component.scss'],
})
export class ReferenceDatasComponent
  extends UnsubscribeComponent
  implements OnInit
{
  // === DATA ===
  public loading = true;
  private referenceDatasQuery!: QueryRef<ReferenceDatasQueryResponse>;
  displayedColumns = [
    'name',
    'type',
    'apiConfiguration',
    'modifiedAt',
    'actions',
  ];
  dataSource = new Array<ReferenceData>();
  public cachedReferenceDatas: ReferenceData[] = [];

  // === SORTING ===
  sort?: TableSort;

  // === FILTERS ===
  public searchText = '';
  public filter: any;
  public filterLoading = false;

  public pageInfo = {
    pageIndex: 0,
    pageSize: ITEMS_PER_PAGE,
    length: 0,
    endCursor: '',
  };

  /**
   * List of Reference data page.
   *
   * @param apollo Apollo service
   * @param dialog Dialog service
   * @param snackBar Shared snackbar service
   * @param authService Shared authentication service
   * @param confirmService Shared confirm service
   * @param router Angular router
   * @param translate Angular translation service
   */
  constructor(
    private apollo: Apollo,
    public dialog: Dialog,
    private snackBar: SnackbarService,
    private authService: AuthService,
    private confirmService: ConfirmService,
    private router: Router,
    private translate: TranslateService
  ) {
    super();
  }

  /**
   * Creates the Reference data query, and subscribes to the query changes.
   */
  ngOnInit(): void {
    this.referenceDatasQuery =
      this.apollo.watchQuery<ReferenceDatasQueryResponse>({
        query: GET_REFERENCE_DATAS,
        variables: {
          first: ITEMS_PER_PAGE,
          afterCursor: this.pageInfo.endCursor,
        },
      });

    this.referenceDatasQuery.valueChanges
      .pipe(takeUntil(this.destroy$))
      .subscribe(({ data, loading }) => {
        this.updateValues(data, loading);
      });
    // Initializing sort to an empty one
    this.sort = {
      active: '',
      sortDirection: '',
    };
  }

  /**
   * Handles page event.
   *
   * @param e page event.
   */
  onPage(e: UIPageChangeEvent): void {
    const cachedData = handleTablePageEvent(
      e,
      this.pageInfo,
      this.cachedReferenceDatas
    );
    if (cachedData && cachedData.length === this.pageInfo.pageSize) {
      this.dataSource = cachedData;
    } else {
      this.fetchReferenceDatas();
    }
  }

  /**
   * Filter data query
   *
   * @param filter Filter to apply
   */
  onFilter(filter: any) {
    this.filterLoading = true;
    this.cachedReferenceDatas = [];
    this.pageInfo.pageIndex = 0;
    this.filter = filter;
    this.referenceDatasQuery
      .fetchMore({
        variables: {
          first: this.pageInfo.pageSize,
          filter: this.filter,
        },
      })
      .then((results: ApolloQueryResult<ReferenceDatasQueryResponse>) => {
        this.updateValues(results.data, false);
      });
  }

  /**
   * Displays the AddReferenceData modal.
   * Creates a new reference data on closed if result.
   */
  async onAdd(): Promise<void> {
    const { AddReferenceDataComponent } = await import(
      './add-reference-data/add-reference-data.component'
    );
    const dialogRef = this.dialog.open(AddReferenceDataComponent);
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.apollo
          .mutate<AddReferenceDataMutationResponse>({
            mutation: ADD_REFERENCE_DATA,
            variables: {
              name: value.name,
            },
          })
          .subscribe({
            next: ({ errors, data }) => {
              if (errors) {
                this.snackBar.openSnackBar(
                  this.translate.instant(
                    'common.notifications.objectNotCreated',
                    {
                      type: this.translate
                        .instant('common.referenceData.one')
                        .toLowerCase(),
                      error: errors ? errors[0].message : '',
                    }
                  ),
                  { error: true }
                );
              } else {
                if (data) {
                  this.router.navigate([
                    '/referencedata',
                    data.addReferenceData.id,
                  ]);
                }
              }
            },
            error: (err) => {
              this.snackBar.openSnackBar(err.message, { error: true });
            },
          });
      }
    });
  }

  /**
   * Removes a reference data if authorized.
   *
   * @param element Reference data to delete.
   * @param e click event.
   */
  onDelete(element: any, e: any): void {
    e.stopPropagation();
    const dialogRef = this.confirmService.openConfirmModal({
      title: this.translate.instant('components.referenceData.delete.title'),
      content: this.translate.instant(
        'components.referenceData.delete.confirmationMessage',
        {
          name: element.name,
        }
      ),
      confirmText: this.translate.instant('common.delete'),
      confirmVariant: 'danger',
    });
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.apollo
          .mutate<DeleteReferenceDataMutationResponse>({
            mutation: DELETE_REFERENCE_DATA,
            variables: {
              id: element.id,
            },
          })
          .subscribe({
            next: (res) => {
              if (res && !res.errors) {
                this.snackBar.openSnackBar(
                  this.translate.instant('common.notifications.objectDeleted', {
                    value: this.translate.instant('common.referenceData.one'),
                  })
                );
                this.dataSource = this.dataSource.filter(
                  (x) => x.id !== element.id
                );
              } else {
                if (res.errors) {
                  this.snackBar.openSnackBar(
                    this.translate.instant(
                      'common.notifications.objectNotDeleted',
                      {
                        value: this.translate.instant(
                          'common.referenceData.one'
                        ),
                        error: res.errors ? res.errors[0] : '',
                      }
                    ),
                    { error: true }
                  );
                }
              }
            },
            error: (err) => {
              this.snackBar.openSnackBar(err.message, { error: true });
            },
          });
      }
    });
  }

  /**
   * Handle sort change.
   *
   * @param event sort event
   */
  onSort(event: TableSort): void {
    this.sort = event;
    this.fetchReferenceDatas(true);
  }

  /**
   * Update ref data value
   *
   * @param data query response data
   * @param loading loading status
   */
  private updateValues(data: ReferenceDatasQueryResponse, loading: boolean) {
    const mappedValues = data.referenceDatas.edges.map((x) => x.node);
    this.cachedReferenceDatas = updateQueryUniqueValues(
      this.cachedReferenceDatas,
      mappedValues
    );
    this.pageInfo.length = data.referenceDatas.totalCount;
    this.pageInfo.endCursor = data.referenceDatas.pageInfo.endCursor;
    this.dataSource = this.cachedReferenceDatas.slice(
      this.pageInfo.pageSize * this.pageInfo.pageIndex,
      this.pageInfo.pageSize * (this.pageInfo.pageIndex + 1)
    );
    this.loading = loading;
    this.filterLoading = false;
  }

  /**
   * Update reference datas query.
   *
   * @param refetch erase previous query results
   */
  private fetchReferenceDatas(refetch?: boolean): void {
    this.loading = true;
    const variables = {
      first: this.pageInfo.pageSize,
      afterCursor: refetch ? null : this.pageInfo.endCursor,
      sortField: this.sort?.sortDirection && this.sort.active,
      sortOrder: this.sort?.sortDirection,
    };
    const cachedValues: ReferenceDatasQueryResponse = getCachedValues(
      this.apollo.client,
      GET_REFERENCE_DATAS,
      variables
    );
    if (refetch) {
      this.cachedReferenceDatas = [];
      this.pageInfo.pageIndex = 0;
    }
    if (cachedValues) {
      this.updateValues(cachedValues, false);
    } else {
      if (refetch) {
        // Rebuild the query
        this.referenceDatasQuery.refetch(variables);
      } else {
        // Fetch more records
        this.referenceDatasQuery
          .fetchMore({
            variables,
          })
          .then((results: ApolloQueryResult<ReferenceDatasQueryResponse>) => {
            this.updateValues(results.data, results.loading);
          });
      }
    }
  }
}

import { Apollo } from 'apollo-angular';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import {
  EditResourceMutationResponse,
  ResourceQueryResponse,
  BreadcrumbService,
} from '@oort-front/shared';
import { EDIT_RESOURCE } from './graphql/mutations';
import { GET_RESOURCE_BY_ID } from './graphql/queries';
import { TranslateService } from '@ngx-translate/core';
import { SnackbarService } from '@oort-front/ui';

/**
 * Array of tab names sorted by position index.
 */
const ROUTE_TABS: string[] = [
  'forms',
  'layouts',
  'aggregations',
  'calculated-fields',
  'records',
];

/**
 * Component used to display resource in a table.
 */
@Component({
  selector: 'app-resource',
  templateUrl: './resource.component.html',
  styleUrls: ['./resource.component.scss'],
})
export class ResourceComponent implements OnInit {
  public loading = true;
  public id = '';
  public resource: any;
  public selectedTab = 0;

  /**
   * ResourceComponent constructor.
   *
   * @param apollo Used to load the resources.
   * @param route Used to get route arguments.
   * @param router Used to change app route.
   * @param snackBar Service used to show a snackbar.
   * @param translate Service used to get translations.
   * @param breadcrumbService Shared breadcrumb service
   */
  constructor(
    private apollo: Apollo,
    private route: ActivatedRoute,
    private router: Router,
    private snackBar: SnackbarService,
    private translate: TranslateService,
    private breadcrumbService: BreadcrumbService
  ) {}

  /** Load data from the id of the resource passed as a parameter. */
  ngOnInit(): void {
    const routeTab: string = this.router.url.split('/').pop() || '';
    this.selectedTab = ROUTE_TABS.findIndex((tab) => tab === routeTab);
    this.id = this.route.snapshot.paramMap.get('id') || '';
    if (this.id !== null) {
      this.getResourceData();
    } else {
      this.router.navigate(['/resources']);
    }
  }

  /**
   * Loads resource data.
   */
  private getResourceData(): void {
    this.loading = true;

    // get the resource and the form linked
    this.apollo
      .query<ResourceQueryResponse>({
        query: GET_RESOURCE_BY_ID,
        variables: {
          id: this.id,
        },
      })
      .subscribe({
        next: ({ data, loading }) => {
          if (data.resource) {
            this.resource = data.resource;
            this.breadcrumbService.setBreadcrumb(
              '@resource',
              this.resource.name as string
            );
            history.pushState({ resource: this.resource }, '');
            this.loading = loading;
          } else {
            this.snackBar.openSnackBar(
              this.translate.instant('common.notifications.accessNotProvided', {
                type: this.translate
                  .instant('common.resource.one')
                  .toLowerCase(),
                error: '',
              }),
              { error: true }
            );
            this.router.navigate(['/resources']);
          }
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
          this.router.navigate(['/resources']);
        },
      });
  }

  /**
   * Edits the permissions layer.
   *
   * @param e New permissions.
   */
  saveAccess(e: any): void {
    this.apollo
      .mutate<EditResourceMutationResponse>({
        mutation: EDIT_RESOURCE,
        variables: {
          id: this.id,
          permissions: e,
        },
      })
      .subscribe({
        next: ({ errors, data }) => {
          if (errors) {
            this.snackBar.openSnackBar(
              this.translate.instant('common.notifications.objectNotUpdated', {
                type: this.translate.instant('common.resource.one'),
                error: errors ? errors[0].message : '',
              }),
              { error: true }
            );
          } else {
            this.snackBar.openSnackBar(
              this.translate.instant('common.notifications.objectUpdated', {
                type: this.translate.instant('common.resource.one'),
                value: '',
              })
            );
            if (data) {
              this.resource = data.editResource;
            }
          }
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
        },
      });
  }
}

import { Apollo, QueryRef } from 'apollo-angular';
import { Component, OnInit } from '@angular/core';
import { DELETE_RESOURCE, ADD_FORM } from './graphql/mutations';
import { GET_RESOURCES_EXTENDED } from './graphql/queries';
import {
  AddFormMutationResponse,
  DeleteResourceMutationResponse,
  Resource,
  ConfirmService,
  UnsubscribeComponent,
  ResourcesQueryResponse,
} from '@oort-front/shared';
import { Router } from '@angular/router';
import { TranslateService } from '@ngx-translate/core';
import {
  getCachedValues,
  updateQueryUniqueValues,
} from '../../../utils/update-queries';
import { Dialog } from '@angular/cdk/dialog';
import {
  TableSort,
  UIPageChangeEvent,
  handleTablePageEvent,
} from '@oort-front/ui';
import { SnackbarService } from '@oort-front/ui';
import { takeUntil } from 'rxjs';

/**
 * Default number of resources that will be shown at once.
 */
const DEFAULT_PAGE_SIZE = 10;

/**
 * Component which will show all the resources in the app.
 */
@Component({
  selector: 'app-resources',
  templateUrl: './resources.component.html',
  styleUrls: ['./resources.component.scss'],
})
export class ResourcesComponent extends UnsubscribeComponent implements OnInit {
  // === DATA ===
  public loading = true;
  public filterLoading = false;
  private resourcesQuery!: QueryRef<ResourcesQueryResponse>;
  displayedColumns: string[] = ['name', 'createdAt', 'recordsCount', 'actions'];
  public cachedResources: Resource[] = [];
  public resources = new Array<Resource>();

  // === SORTING ===
  public updating = false;
  private sort: TableSort = { active: '', sortDirection: '' };

  // === FILTERING ===
  public filter: any = {
    filters: [],
    logic: 'and',
  };

  // === PAGINATION ===
  public pageInfo = {
    pageIndex: 0,
    pageSize: DEFAULT_PAGE_SIZE,
    length: 0,
    endCursor: '',
  };

  /**
   * ResourcesComponent constructor.
   *
   * @param dialog Used for opening a dialog.
   * @param apollo Used for loading the resources.
   * @param snackBar Service used to show the snackbar,
   * @param confirmService Service used to show the confirmation window
   * @param translate Service used to get translations
   * @param router Used to change the app route.
   */
  constructor(
    private dialog: Dialog,
    private apollo: Apollo,
    private snackBar: SnackbarService,
    private confirmService: ConfirmService,
    private translate: TranslateService,
    private router: Router
  ) {
    super();
  }

  /** Load the resources. */
  ngOnInit(): void {
    this.resourcesQuery = this.apollo.watchQuery<ResourcesQueryResponse>({
      query: GET_RESOURCES_EXTENDED,
      variables: {
        first: DEFAULT_PAGE_SIZE,
        sortField: 'name',
        sortOrder: 'asc',
        afterCursor: null,
        filter: this.filter,
      },
    });

    this.resourcesQuery.valueChanges.subscribe(({ data, loading }) => {
      this.updateValues(data, loading);
    });
  }

  /**
   * Handles page event.
   *
   * @param e page event.
   */
  onPage(e: UIPageChangeEvent): void {
    const cachedData = handleTablePageEvent(
      e,
      this.pageInfo,
      this.cachedResources
    );
    if (cachedData && cachedData.length === this.pageInfo.pageSize) {
      this.resources = cachedData;
    } else {
      this.fetchResources();
    }
  }

  /**
   * Filters applications and updates table.
   *
   * @param filter filter event.
   */
  onFilter(filter: any): void {
    this.filterLoading = true;
    this.filter = filter;
    this.fetchResources(true, filter);
  }

  /**
   * Handle sort change.
   *
   * @param event sort event
   */
  onSort(event: TableSort): void {
    this.sort = event;
    this.fetchResources(true);
  }

  /**
   * Update resources query.
   *
   * @param refetch erase previous query results
   * @param filter filter value
   */
  private fetchResources(refetch?: boolean, filter?: any): void {
    this.updating = true;
    const variables = {
      first: this.pageInfo.pageSize,
      afterCursor: refetch ? null : this.pageInfo.endCursor,
      filter: filter ?? this.filter,
      sortField:
        (this.sort?.sortDirection && this.sort.active) !== ''
          ? this.sort?.sortDirection && this.sort.active
          : 'name',
      sortOrder:
        this.sort?.sortDirection !== '' ? this.sort?.sortDirection : 'asc',
    };
    const cachedValues: ResourcesQueryResponse = getCachedValues(
      this.apollo.client,
      GET_RESOURCES_EXTENDED,
      variables
    );
    if (refetch) {
      this.cachedResources = [];
      this.pageInfo.pageIndex = 0;
    }
    if (cachedValues) {
      this.updateValues(cachedValues, false);
    } else {
      if (refetch) {
        this.resourcesQuery.refetch(variables);
      } else {
        this.loading = true;
        this.resourcesQuery
          .fetchMore({
            variables,
          })
          .then((results) => this.updateValues(results.data, results.loading));
      }
    }
  }

  /**
   * Removes a resource.
   *
   * @param resource Resource to delete.
   */
  onDelete(resource: Resource): void {
    const dialogRef = this.confirmService.openConfirmModal({
      title: this.translate.instant('common.deleteObject', {
        name: this.translate.instant('common.resource.one'),
      }),
      content: this.translate.instant(
        'components.resource.delete.confirmationMessage',
        {
          name: resource.name,
        }
      ),
      confirmText: this.translate.instant('components.confirmModal.delete'),
      confirmVariant: 'danger',
    });

    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        this.apollo
          .mutate<DeleteResourceMutationResponse>({
            mutation: DELETE_RESOURCE,
            variables: {
              id: resource.id,
            },
          })
          .subscribe(({ errors }) => {
            if (!errors) {
              this.resources = this.resources.filter(
                (x) => x.id !== resource.id
              );
              this.snackBar.openSnackBar(
                this.translate.instant('common.notifications.objectDeleted', {
                  value: this.translate.instant('common.resource.one'),
                })
              );
            } else {
              this.snackBar.openSnackBar(
                this.translate.instant(
                  'common.notifications.objectNotDeleted',
                  {
                    value: this.translate.instant('common.resource.one'),
                    error: errors[0].message,
                  }
                ),
                { error: true }
              );
            }
          });
      }
    });
  }

  /**
   * Displays the AddForm modal.
   * Creates a new form on closed if result.
   */
  async onAdd(): Promise<void> {
    const { AddResourceModalComponent } = await import(
      '../../../components/add-resource-modal/add-resource-modal.component'
    );
    const dialogRef = this.dialog.open(AddResourceModalComponent);
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe((value: any) => {
      if (value) {
        const variablesData = { name: value.name };
        this.apollo
          .mutate<AddFormMutationResponse>({
            mutation: ADD_FORM,
            variables: variablesData,
          })
          .subscribe({
            next: ({ errors, data }) => {
              if (errors) {
                this.snackBar.openSnackBar(
                  this.translate.instant(
                    'common.notifications.objectNotCreated',
                    {
                      type: this.translate
                        .instant('common.form.one')
                        .toLowerCase(),
                      error: errors[0].message,
                    }
                  ),
                  { error: true }
                );
              } else {
                if (data) {
                  const { id } = data.addForm;
                  this.router.navigate(['/forms/builder', id]);
                }
              }
            },
            error: (err) => {
              this.snackBar.openSnackBar(err.message, { error: true });
            },
          });
      }
    });
  }

  /**
   * Update resource data value
   *
   * @param data query response data
   * @param loading loading status
   */
  updateValues(data: ResourcesQueryResponse, loading: boolean) {
    const mappedValues = data.resources?.edges?.map((x) => x.node);
    this.cachedResources = updateQueryUniqueValues(
      this.cachedResources,
      mappedValues
    );
    this.pageInfo.length = data.resources.totalCount;
    this.pageInfo.endCursor = data.resources.pageInfo.endCursor;
    this.loading = loading;
    this.updating = loading;
    this.resources = this.cachedResources.slice(
      this.pageInfo.pageSize * this.pageInfo.pageIndex,
      this.pageInfo.pageSize * (this.pageInfo.pageIndex + 1)
    );
    this.filterLoading = false;
  }
}

