export class ViewIconSelectorComponent {
  /** Icon name form control */
  @Input() iconControl!: FormControl;
}

import { Component, EventEmitter, Inject, OnInit } from '@angular/core';
import { DIALOG_DATA, DialogRef } from '@angular/cdk/dialog';
import { FormBuilder } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import {
  TabsModule,
  DialogModule,
  ButtonModule,
  TooltipModule,
  SelectMenuModule,
  FormWrapperModule,
  IconModule,
  ToggleModule,
  AlertModule,
} from '@oort-front/ui';
import { OverlayModule } from '@angular/cdk/overlay';
import { TranslateModule } from '@ngx-translate/core';
import { ViewIconSelectorComponent } from '../view-icon-selector/view-icon-selector.component';
import {
  AccessModule,
  ApplicationService,
  Page,
  Step,
  UnsubscribeComponent,
  WorkflowService,
  AccessData,
  SearchMenuModule,
  AuthService,
  Application,
} from '@oort-front/shared';
import { takeUntil } from 'rxjs';
import { isNil } from 'lodash';
import { AbilityModule } from '@casl/angular';

/** Settings Dialog Data */
interface DialogData {
  type: 'page' | 'step';
  applicationId: string;
  page?: Page;
  step?: Step;
  icon?: string;
  visible?: boolean;
  accessData: AccessData;
  canUpdate: boolean;
}

/**
 * Application page and step settings component.
 * Available settings: icon, access, visibility and duplicate page.
 */
@Component({
  selector: 'app-view-settings',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    TabsModule,
    DialogModule,
    IconModule,
    ButtonModule,
    TooltipModule,
    SelectMenuModule,
    FormWrapperModule,
    TranslateModule,
    ViewIconSelectorComponent,
    AccessModule,
    ToggleModule,
    OverlayModule,
    SearchMenuModule,
    AlertModule,
    AbilityModule,
  ],
  templateUrl: './view-settings-modal.component.html',
  styleUrls: ['./view-settings-modal.component.scss'],
})
export class ViewSettingsModalComponent
  extends UnsubscribeComponent
  implements OnInit
{
  /** Reactive Form */
  public settingsForm!: ReturnType<typeof this.createSettingsForm>;
  /** Event to parent subscribe and update its own object after changes */
  public onUpdate = new EventEmitter();
  /** Show duplicate menu */
  public showDuplicateMenu = false;
  /** List of available applications */
  public applications: Application[] = [];
  /** Step object */
  private step?: Step;
  /** Page object */
  private page?: Page;

  /**
   * Common settings of pages / steps.
   *
   * @param dialogRef Dialog ref
   * @param data Data that will be passed to the dialog
   * @param fb This is the service that will be used to build forms.
   * @param workflowService Shared workflow service
   * @param applicationService Shared application service
   * @param authService Shared authentication service
   */
  constructor(
    public dialogRef: DialogRef<ViewSettingsModalComponent>,
    @Inject(DIALOG_DATA) public data: DialogData,
    private fb: FormBuilder,
    private workflowService: WorkflowService,
    private applicationService: ApplicationService,
    private authService: AuthService
  ) {
    super();
    if (this.data) {
      this.page = this.data?.page;
      this.step = this.data?.step;
    }
  }

  ngOnInit(): void {
    this.settingsForm = this.createSettingsForm();
    if (!this.data.canUpdate) {
      this.settingsForm.disable();
    }

    // Listen to icon updates
    this.settingsForm?.controls.icon.valueChanges
      .pipe(takeUntil(this.destroy$))
      .subscribe((value: string | null) => {
        if (value) {
          this.onUpdateIcon(value);
        }
      });

    // Listen to visibility updates (only for pages)
    if (this.data.type === 'page') {
      this.settingsForm?.controls.visible.valueChanges
        .pipe(takeUntil(this.destroy$))
        .subscribe((value: boolean | null) => {
          if (!isNil(value)) {
            this.onUpdateVisibility(value);
          }
        });
    }
  }

  /**
   * Save changes when access is updated.
   *
   * @param event new permissions object
   */
  public onUpdateAccess(event: any): void {
    if (this.data.type === 'step' && this.step) {
      const callback = (permissions: any) => {
        // Updates parent component
        const updates = { permissions };
        this.onUpdate.emit(updates);
      };
      this.workflowService.updateStepPermissions(
        this.step as Step,
        event,
        callback
      );
    } else {
      const callback = (permissions: any) => {
        this.page = {
          ...this.page,
          canDelete: permissions.canDelete,
          canUpdate: permissions.canUpdate,
          canSee: permissions.canSee,
        };
        // Updates parent component
        const updates = { permissions };
        this.onUpdate.emit(updates);
      };
      this.page &&
        this.applicationService.updatePagePermissions(
          this.page as Page,
          event,
          callback
        );
    }
  }

  /**
   * Duplicate page, in a new (or same) application
   *
   * @param event duplication event
   */
  public onDuplicate(event: any): void {
    const callback = () => {
      this.dialogRef.close();
    };
    this.applicationService.duplicatePage(
      event.id,
      {
        pageId: this.page?.id,
        stepId: this.step?.id,
      },
      callback
    );
  }

  /**
   * Toggle visibility of application menu.
   * Get applications to use duplicate setting.
   */
  public onAppSelection(): void {
    this.showDuplicateMenu = !this.showDuplicateMenu;
    const authSubscription = this.authService.user$.subscribe(
      (user: any | null) => {
        if (user) {
          this.applications = user.applications;
        }
      }
    );
    authSubscription.unsubscribe();
  }

  /**
   * Create the settings form
   *
   * @returns Form group
   */
  private createSettingsForm() {
    return this.fb.group({
      // initializes icon field with data info
      icon: this.fb.control(this.data.icon ?? ''),
      visible: this.fb.control(this.data.visible ?? true),
    });
  }

  /**
   * Save changes when icon is updated.
   *
   * @param icon new icon name
   */
  private onUpdateIcon(icon: string): void {
    if (this.data.type === 'step' && this.step) {
      const callback = () => {
        this.step = {
          ...this.step,
          icon,
        };
        // Updates parent component
        const updates = { icon };
        this.onUpdate.emit(updates);
      };
      this.workflowService.updateStepIcon(this.step as Step, icon, callback);
    } else {
      const callback = () => {
        this.page = {
          ...this.page,
          icon,
        };
        // Updates parent component
        const updates = { icon };
        this.onUpdate.emit(updates);
      };
      this.page &&
        this.applicationService.changePageIcon(
          this.page as Page,
          icon,
          callback
        );
    }
  }

  /**
   * Save page visibility on change.
   *
   * @param visible boolean
   */
  private onUpdateVisibility(visible: boolean): void {
    const callback = () => {
      this.page = {
        ...this.page,
        visible,
      };
      // Updates parent component
      const updates = { visible };
      this.onUpdate.emit(updates);
    };
    this.applicationService.togglePageVisibility(
      {
        id: this.page?.id,
        visible,
      },
      callback
    );
  }
}

import { Apollo } from 'apollo-angular';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import {
  ContentType,
  Step,
  Workflow,
  UnsubscribeComponent,
  WorkflowQueryResponse,
} from '@oort-front/shared';
import { GET_WORKFLOW_BY_ID } from './graphql/queries';
import { TranslateService } from '@ngx-translate/core';
import { takeUntil } from 'rxjs/operators';
import { SnackbarService } from '@oort-front/ui';
import { PreviewService } from '../../../services/preview.service';

/**
 * Workflow page component for application preview.
 */
@Component({
  selector: 'app-workflow',
  templateUrl: './workflow.component.html',
  styleUrls: ['./workflow.component.scss'],
})
export class WorkflowComponent extends UnsubscribeComponent implements OnInit {
  /** Loading indicator */
  public loading = true;
  /** Current workflow id */
  public id = '';
  /** Current workflow */
  public workflow?: Workflow;
  /** Workflow steps */
  public steps: Step[] = [];
  /** Current step index */
  public activeStep = 0;
  /** Role used for preview */
  public role = '';

  /**
   * Workflow page component for application preview
   *
   * @param apollo Apollo service
   * @param route Angular activated route
   * @param snackBar Shared snackbar service
   * @param router Angular router
   * @param previewService Shared preview service
   * @param translate Angular translate service
   */
  constructor(
    private apollo: Apollo,
    private route: ActivatedRoute,
    private snackBar: SnackbarService,
    private router: Router,
    private previewService: PreviewService,
    private translate: TranslateService
  ) {
    super();
  }

  /**
   * Gets the workflow from the route.
   */
  ngOnInit(): void {
    this.previewService.roleId$
      .pipe(takeUntil(this.destroy$))
      .subscribe((role) => {
        this.role = role;
      });
    this.route.params.pipe(takeUntil(this.destroy$)).subscribe((params) => {
      this.loading = true;
      this.id = params.id;
      this.apollo
        .watchQuery<WorkflowQueryResponse>({
          query: GET_WORKFLOW_BY_ID,
          variables: {
            id: this.id,
            asRole: this.role,
          },
        })
        .valueChanges.pipe(takeUntil(this.destroy$))
        .subscribe({
          next: ({ data, loading }) => {
            if (data.workflow) {
              this.workflow = data.workflow;
              this.steps = data.workflow.steps || [];
              this.loading = loading;
              if (this.steps.length > 0) {
                this.onOpenStep(0);
              }
            } else {
              this.snackBar.openSnackBar(
                this.translate.instant(
                  'common.notifications.accessNotProvided',
                  {
                    type: this.translate
                      .instant('common.workflow.one')
                      .toLowerCase(),
                    error: '',
                  }
                ),
                { error: true }
              );
            }
          },
          error: (err) => {
            this.snackBar.openSnackBar(err.message, { error: true });
          },
        });
    });
  }

  /**
   * Activates the clicked element.
   *
   * @param elementRef Element ref.
   */
  onActivate(elementRef: any): void {
    if (elementRef.changeStep) {
      elementRef.changeStep.subscribe((event: any) => {
        if (event > 0) {
          this.goToNextStep();
        } else {
          this.goToPreviousStep();
        }
      });
    }
  }

  /**
   * Navigates to the next step if possible and change selected step / index consequently
   */
  private goToNextStep(): void {
    if (this.activeStep + 1 < this.steps.length) {
      this.onOpenStep(this.activeStep + 1);
    } else if (this.activeStep + 1 === this.steps.length) {
      this.onOpenStep(0);
      this.snackBar.openSnackBar(
        this.translate.instant('models.workflow.notifications.goToStep', {
          step: this.steps[0].name,
        })
      );
    } else {
      this.snackBar.openSnackBar(
        this.translate.instant(
          'models.workflow.notifications.cannotGoToNextStep'
        ),
        { error: true }
      );
    }
  }

  /**
   * Navigates to the previous step if possible and change selected step / index consequently
   */
  private goToPreviousStep(): void {
    if (this.activeStep > 0) {
      this.onOpenStep(this.activeStep - 1);
    } else if (this.activeStep === 0) {
      this.onOpenStep(this.steps.length - 1);
      this.snackBar.openSnackBar(
        this.translate.instant('models.workflow.notifications.goToStep', {
          step: this.steps[this.steps.length - 1].name,
        })
      );
    } else {
      this.snackBar.openSnackBar(
        this.translate.instant(
          'models.workflow.notifications.cannotGoToPreviousStep'
        ),
        { error: true }
      );
    }
  }

  /**
   * Navigates to the new step.
   *
   * @param index Index of the step in the workflow.
   */
  public onOpenStep(index: number): void {
    if (index >= 0 && index < this.steps.length) {
      const step = this.steps[index];
      this.activeStep = index;
      if (step.type === ContentType.form) {
        this.router.navigate(['./' + step.type + '/' + step.id], {
          relativeTo: this.route,
        });
      } else {
        this.router.navigate(['./' + step.type + '/' + step.content], {
          relativeTo: this.route,
        });
      }
    } else {
      this.router.navigate(['./'], { relativeTo: this.route });
    }
  }
}

import { Apollo } from 'apollo-angular';
import { Component, OnInit } from '@angular/core';
import { Dialog } from '@angular/cdk/dialog';
import { ActivatedRoute, Router } from '@angular/router';
import {
  Workflow,
  Step,
  ConfirmService,
  ContentType,
  ApplicationService,
  WorkflowService,
  AuthService,
  Application,
  UnsubscribeComponent,
  DeleteStepMutationResponse,
  EditWorkflowMutationResponse,
} from '@oort-front/shared';
import { DELETE_STEP, EDIT_WORKFLOW } from './graphql/mutations';
import { TranslateService } from '@ngx-translate/core';
import { takeUntil } from 'rxjs/operators';
import { SnackbarService } from '@oort-front/ui';

/**
 * Application workflow page component.
 */
@Component({
  selector: 'app-workflow',
  templateUrl: './workflow.component.html',
  styleUrls: ['./workflow.component.scss'],
})
export class WorkflowComponent extends UnsubscribeComponent implements OnInit {
  // === DATA ===
  public loading = true;

  // === WORKFLOW ===
  public id = '';
  public applicationId?: string;
  public workflow?: Workflow;
  public steps: Step[] = [];

  // === WORKFLOW EDITION ===
  public canEditName = false;
  public formActive = false;
  public canUpdate = false;

  // === ACTIVE STEP ===
  public activeStep = 0;

  // === DUP APP SELECTION ===
  public showAppMenu = false;
  public applications: Application[] = [];

  /**
   * Application workflow page component
   *
   * @param apollo Apollo service
   * @param workflowService Shared workflow service
   * @param applicationService Shared application service
   * @param route Angular activated route
   * @param router Angular router
   * @param dialog Dialog service
   * @param snackBar Shared snackbar service
   * @param authService Shared authentication service
   * @param confirmService Shared confirm service
   * @param translate Angular translate module.
   */
  constructor(
    private apollo: Apollo,
    private workflowService: WorkflowService,
    private applicationService: ApplicationService,
    private route: ActivatedRoute,
    private router: Router,
    public dialog: Dialog,
    private snackBar: SnackbarService,
    private authService: AuthService,
    private confirmService: ConfirmService,
    private translate: TranslateService
  ) {
    super();
  }

  ngOnInit(): void {
    this.formActive = false;
    this.route.params.pipe(takeUntil(this.destroy$)).subscribe((params) => {
      this.loading = true;
      this.id = params.id;
      this.workflowService.loadWorkflow(this.id);
    });

    this.workflowService.workflow$
      .pipe(takeUntil(this.destroy$))
      .subscribe((workflow: Workflow | null) => {
        if (workflow) {
          this.steps = workflow.steps || [];
          this.loading = false;
          if (!this.workflow || workflow.id !== this.workflow.id) {
            const firstStep = this.steps[0];
            if (firstStep) {
              const firstStepIsForm = firstStep.type === ContentType.form;
              const currentStepId = this.router.url.split('/').pop();
              // If redirect to the workflow beginning, just go to the firstStep
              let currentStep: Step = firstStep;
              let currentActiveStep = 0;
              if (
                !(firstStepIsForm
                  ? firstStep.id === currentStepId
                  : firstStep.content === currentStepId)
              ) {
                // If not, URL contains the step id so redirect to the selected step (used for when refresh page or shared dashboard step link)
                workflow?.steps?.forEach((step: Step, index: number) => {
                  const stepIsForm = step.type === ContentType.form;
                  if (
                    (stepIsForm && step.id === currentStepId) ||
                    step.content === currentStepId
                  ) {
                    currentStep = step;
                    currentActiveStep = index;
                    return;
                  }
                });
              }
              if (currentStep.type === ContentType.form) {
                this.router.navigate(
                  ['./' + currentStep.type + '/' + currentStep.id],
                  { relativeTo: this.route }
                );
              } else {
                this.router.navigate(
                  ['./' + currentStep.type + '/' + currentStep.content],
                  { relativeTo: this.route }
                );
              }
              this.activeStep = currentActiveStep;
            }
            if (!firstStep) {
              this.router.navigate([`./`], { relativeTo: this.route });
            }
          } else {
            if (
              workflow.steps &&
              workflow.steps.length > (this.workflow.steps || []).length
            ) {
              this.activeStep = workflow.steps.length - 1;
            }
          }
          this.workflow = workflow;
          this.canEditName = this.workflow?.page?.canUpdate || false;
          this.applicationId = this.workflow.page?.application?.id;
          this.canUpdate = this.workflow.canUpdate || false;
        } else {
          this.loading = true;
          this.steps = [];
        }
      });
  }

  /**
   * Toggle workflow name form.
   */
  toggleFormActive(): void {
    if (this.workflow?.page?.canUpdate) {
      this.formActive = !this.formActive;
    }
  }

  /**
   * Updates the name of the workflow and his linked page.
   *
   * @param workflowName new workflow name
   */
  saveName(workflowName: string): void {
    if (workflowName && workflowName !== this.workflow?.name) {
      const callback = () => {
        this.workflow = { ...this.workflow, name: workflowName };
      };
      this.applicationService.updatePageName(
        {
          id: this.workflow?.page?.id,
          name: workflowName,
        },
        callback
      );
    }
  }

  /**
   * Deletes a step if authorized.
   *
   * @param index index of step to delete
   */
  onDeleteStep(index: number): void {
    if (index >= 0 && index < this.steps.length) {
      const step = this.steps[index];
      const currentStep =
        this.activeStep >= 0 ? this.steps[this.activeStep] : null;
      const dialogRef = this.confirmService.openConfirmModal({
        title: this.translate.instant('common.deleteObject', {
          name: this.translate.instant('common.step.one'),
        }),
        content: this.translate.instant(
          'pages.workflow.deleteStep.confirmationMessage',
          { step: step.name }
        ),
        confirmText: this.translate.instant('components.confirmModal.delete'),
        confirmVariant: 'danger',
      });
      dialogRef.closed
        .pipe(takeUntil(this.destroy$))
        .subscribe((value: any) => {
          if (value) {
            this.apollo
              .mutate<DeleteStepMutationResponse>({
                mutation: DELETE_STEP,
                variables: {
                  id: step.id,
                },
              })
              .subscribe({
                next: ({ errors, data }) => {
                  if (errors) {
                    this.snackBar.openSnackBar(
                      this.translate.instant(
                        'common.notifications.objectNotDeleted',
                        {
                          value: this.translate.instant('common.step.one'),
                          error: errors ? errors[0].message : '',
                        }
                      ),
                      { error: true }
                    );
                  } else {
                    if (data) {
                      this.snackBar.openSnackBar(
                        this.translate.instant(
                          'common.notifications.objectDeleted',
                          {
                            value: this.translate.instant('common.step.one'),
                          }
                        )
                      );
                      this.steps = this.steps.filter(
                        (x) => x.id !== data?.deleteStep.id
                      );
                      if (index === this.activeStep) {
                        this.onOpenStep(-1);
                      } else {
                        if (currentStep) {
                          this.activeStep = this.steps.findIndex(
                            (x) => x.id === currentStep.id
                          );
                        }
                      }
                    }
                  }
                },
                error: (err) => {
                  this.snackBar.openSnackBar(err.message, { error: true });
                },
              });
          }
        });
    }
  }

  /**
   * Navigate to the add-step component.
   */
  onAddStep(): void {
    this.router.navigate(['./add-step'], { relativeTo: this.route });
  }

  /**
   * Go to next step
   *
   * @param elementRef Element ref of workflow component
   */
  onActivate(elementRef: any): void {
    if (elementRef.changeStep) {
      elementRef.changeStep.subscribe((event: number) => {
        if (event > 0) {
          this.goToNextStep();
        } else {
          this.goToPreviousStep();
        }
      });
    }
  }

  /**
   * Saves the new ordering
   *
   * @param steps steps order.
   */
  onReorderSteps(steps: Step[]): void {
    const currentStep =
      this.activeStep >= 0 ? this.steps[this.activeStep] : null;
    this.apollo
      .mutate<EditWorkflowMutationResponse>({
        mutation: EDIT_WORKFLOW,
        variables: {
          id: this.id,
          steps: steps.map((step) => step.id),
        },
      })
      .subscribe({
        next: ({ errors, data }) => {
          if (data) {
            this.snackBar.openSnackBar(
              this.translate.instant('common.notifications.objectReordered', {
                type: this.translate.instant('common.step.one'),
              })
            );
            if (currentStep) {
              const index = steps.findIndex((x) => x.id === currentStep.id);
              this.activeStep = index;
            }
            this.steps = steps;
          } else {
            this.snackBar.openSnackBar(
              this.translate.instant('common.notifications.objectNotUpdated', {
                type: this.translate.instant('common.workflow.one'),
                error: errors ? errors[0].message : '',
              }),
              { error: true }
            );
          }
        },
        error: (err) => {
          this.snackBar.openSnackBar(err.message, { error: true });
        },
      });
  }

  /**
   * Navigates to the next step if possible and change selected step / index consequently
   */
  private goToNextStep(): void {
    if (this.activeStep + 1 < this.steps.length) {
      this.onOpenStep(this.activeStep + 1);
    } else if (this.activeStep + 1 === this.steps.length) {
      this.onOpenStep(0);
      this.snackBar.openSnackBar(
        this.translate.instant('models.workflow.notifications.goToStep', {
          step: this.steps[0].name,
        })
      );
    } else {
      this.snackBar.openSnackBar(
        this.translate.instant(
          'models.workflow.notifications.cannotGoToNextStep'
        ),
        { error: true }
      );
    }
  }

  /**
   * Navigates to the previous step if possible and change selected step / index consequently
   */
  private goToPreviousStep(): void {
    if (this.activeStep > 0) {
      this.onOpenStep(this.activeStep - 1);
    } else if (this.activeStep === 0) {
      this.onOpenStep(this.steps.length - 1);
      this.snackBar.openSnackBar(
        this.translate.instant('models.workflow.notifications.goToStep', {
          step: this.steps[this.steps.length - 1].name,
        })
      );
    } else {
      this.snackBar.openSnackBar(
        this.translate.instant(
          'models.workflow.notifications.cannotGoToPreviousStep'
        ),
        { error: true }
      );
    }
  }

  /**
   * Open selected step
   *
   * @param index index of selected step
   */
  public onOpenStep(index: number): void {
    if (index >= 0 && index < this.steps.length) {
      const step = this.steps[index];
      this.activeStep = index;
      if (step.type === ContentType.form) {
        this.router.navigate(['./' + step.type + '/' + step.id], {
          relativeTo: this.route,
        });
      } else {
        this.router.navigate(['./' + step.type + '/' + step.content], {
          relativeTo: this.route,
        });
      }
    } else {
      this.router.navigate(['./'], { relativeTo: this.route });
    }
  }

  /**
   * Open settings modal.
   */
  public async onOpenSettings(): Promise<void> {
    const { ViewSettingsModalComponent } = await import(
      '../../../components/view-settings-modal/view-settings-modal.component'
    );
    const dialogRef = this.dialog.open(ViewSettingsModalComponent, {
      data: {
        type: 'page',
        applicationId: this.applicationId,
        page: this.workflow?.page,
        icon: this.workflow?.page?.icon,
        visible: this.workflow?.page?.visible,
        accessData: {
          access: this.workflow?.permissions,
          application: this.applicationId,
          objectTypeName: this.translate.instant('common.page.one'),
        },
        canUpdate: this.workflow?.page?.canUpdate || false,
      },
    });
    // Subscribes to settings updates
    const subscription = dialogRef.componentInstance?.onUpdate
      .pipe(takeUntil(this.destroy$))
      .subscribe((updates: any) => {
        if (updates) {
          this.workflow = {
            ...this.workflow,
            ...(updates.permissions && updates),
            page: {
              ...this.workflow?.page,
              ...(!updates.permissions && updates),
            },
          };
        }
      });
    // Unsubscribe to dialog onUpdate event
    dialogRef.closed.pipe(takeUntil(this.destroy$)).subscribe(() => {
      subscription?.unsubscribe();
    });
  }
}

