import {
  AfterViewInit,
  ChangeDetectorRef,
  Component,
  ContentChildren,
  EventEmitter,
  Input,
  OnChanges,
  OnDestroy,
  Output,
  QueryList,
  SimpleChanges,
  ViewChild,
} from '@angular/core';
import { Variant } from '../types/variant';
import {
  trigger,
  state,
  style,
  transition,
  animate,
} from '@angular/animations';
import { TabComponent } from './components/tab/tab.component';
import { Subject, takeUntil, startWith } from 'rxjs';
import { TabBodyHostDirective } from './directives/tab-body-host.directive';

/**
 * UI Tabs component
 * Tabs are used to split content between multiple sections. Tabs can be either horizontal or vertical.
 */
@Component({
  selector: 'ui-tabs',
  templateUrl: './tabs.component.html',
  styleUrls: ['./tabs.component.scss'],
  animations: [
    trigger('tabContentDisplay', [
      // Initial state of content when loading component is visible(no animation)
      state('initial', style({ opacity: 1, transform: 'translateX(0%)' })),
      // Load content state is hidden when we load another tab that is not the default one
      state(
        'loadContent',
        style({ opacity: 0.1, transform: 'translateX(100%)' })
      ),
      // From the load content to content-in we would trigger the animation that loads the content in from bottom to top
      transition('loadContent => initial', [animate('.3s ease-in-out')]),
      transition('initial => loadContent', [animate('.001s ease-in-out')]),
    ]),
  ],
})
export class TabsComponent implements AfterViewInit, OnDestroy, OnChanges {
  @ContentChildren(TabComponent, { descendants: true })
  tabs!: QueryList<TabComponent>;

  /**
   * Index of the default selected tab
   */
  @Input() selectedIndex = 0;
  /**
   * True if the navigation tab is to be vertical, false otherwise
   */
  @Input() vertical = false;
  /**
   * True if the navigation tab is to be vertical, false otherwise
   */
  @Input() variant: Variant = 'default';
  /**
   * Output emitted whenever a new tab is clicked, gives the index of the new tab
   */
  @Output() selectedIndexChange = new EventEmitter<number>();
  /** Event emitter for when a tab is opened. */
  @Output() openedTab = new EventEmitter<TabComponent>();
  /** Reference to the TabBodyHostDirective. */
  @ViewChild(TabBodyHostDirective)
  tabBodyHost!: TabBodyHostDirective;

  previousTabsLength = 0;
  triggerAnimation = false;
  destroy$ = new Subject<void>();
  reorder$ = new Subject<void>();

  /**
   * Ui Sidenav constructor
   *
   * @param cdr ChangeDetectorRef
   */
  constructor(private cdr: ChangeDetectorRef) {}

  /** @returns general resolved position classes for navigation tabs*/
  get resolveTabPositionClasses(): string[] {
    const classes = [];
    if (this.vertical) {
      classes.push('col-span-3');
      classes.push('flex-col');
      classes.push('h-full');
      classes.push('mr-4');
    } else {
      classes.push('border-b');
      classes.push('mb-4');
      classes.push('overflow-x-auto');
    }
    return classes;
  }

  ngAfterViewInit() {
    // This ensures that the subscription logic is executed for both existing and new tab elements
    this.tabs.changes
      .pipe(startWith(this.tabs), takeUntil(this.destroy$))
      .subscribe((tabs: QueryList<TabComponent>) => {
        this.cdr.detectChanges();
        if (tabs.length !== this.previousTabsLength) {
          this.reorder$.next();
          this.previousTabsLength = tabs.length;
          this.subscribeToOpenTabEvents();
        }
      });
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['selectedIndex']) {
      this.setSelectedTab();
    }
  }

  /**
   * Show the content linked to the tab clicked + manages classes so the good tab is selected in design
   *
   * @param tab tab to display
   */
  showContent(tab: TabComponent) {
    if (tab.index !== this.selectedIndex || !this.tabBodyHost.hasAttached()) {
      this.selectedIndex = tab.index;
      this.setSelectedTab();

      // Clean up previous displayed content
      this.triggerAnimation = false;

      // Creates the content element thanks to the hidden html content of the tab component
      // Timeout so the animation has the time to render (elsewhere it can't cause delete then create is instantaneous)
      setTimeout(() => {
        this.triggerAnimation = true;
        this.openedTab.emit(tab);
      }, 100);
      // Emits the current selected index
      this.selectedIndexChange.emit(this.selectedIndex);
    }
  }

  /**
   * Update select state of all the tabs
   */
  public setSelectedTab() {
    this.tabs?.forEach((tab) => {
      if (tab.index === this.selectedIndex) {
        tab.selected = true;
      } else {
        tab.selected = false;
      }
    });
  }

  /**
   * Gets all the tabs, initialize them and listen to the openTab event
   */
  private subscribeToOpenTabEvents(): void {
    this.tabs?.forEach((tab, index) => {
      tab.variant = this.variant;
      tab.vertical = this.vertical;
      tab.index = index;
      tab.openTab
        .pipe(takeUntil(this.reorder$), takeUntil(this.destroy$))
        .subscribe(() => {
          this.showContent(tab);
          this.selectedIndex = index;
        });
    });
    // To avoid that we select all tabs by default
    this.tabs?.forEach((tab) => {
      if (tab.index === this.selectedIndex) {
        this.showContent(tab);
      }
    });
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}

import {
  Component,
  forwardRef,
  Input,
  Provider,
  ViewChild,
  EventEmitter,
  Optional,
  Self,
} from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { CdkTextareaAutosize } from '@angular/cdk/text-field';
import { FocusableDirective } from '@progress/kendo-angular-grid';

/**
 * Control value accessor
 */
const CONTROL_VALUE_ACCESSOR: Provider = {
  provide: NG_VALUE_ACCESSOR,
  useExisting: forwardRef(() => TextareaComponent),
  multi: true,
};

/**
 * UI Textarea component
 */
@Component({
  selector: 'ui-textarea',
  templateUrl: './textarea.component.html',
  styleUrls: ['./textarea.component.scss'],
  providers: [CONTROL_VALUE_ACCESSOR],
})
export class TextareaComponent implements ControlValueAccessor {
  /** The value of the textarea. */
  @Input() value = '';
  /** The placeholder text for the textarea. */
  @Input() placeholder = '';
  /** The name of the textarea. */
  @Input() name!: string;
  /**
   * Set minimal rows for the textarea
   *
   * If the minimal rows given is greater than the current max row values, or no max row values was provided
   * then we set the minimal rows to the max rows value
   *
   * @param rows row number
   */
  @Input() set minRows(rows: number) {
    if (rows) {
      this.minRowsNumber = rows;
      if (rows > this.maxRows) {
        this.maxRows = rows;
      }
    }
  }
  @Input() maxRows = 5;

  /**
   * UI Textarea constructor
   *
   * @param kendoFocus FocusableDirective
   */
  constructor(@Self() @Optional() public kendoFocus: FocusableDirective) {}
  /** The minimum number of rows in the textarea. */
  minRowsNumber = 2;
  /** Event emitter for value changes. */
  valueChange: EventEmitter<boolean> = new EventEmitter();
  disabled = false;
  /** Function to handle touch events. */
  onTouched!: () => void;
  /** Function to handle value changes. */
  onChanged!: (value: string) => void;
  /** Reference to the autosize directive. */
  @ViewChild('autosize') autosize!: CdkTextareaAutosize;

  /**
   * Register change of the control
   *
   * @param fn callback
   */
  public registerOnChange(fn: (value: string) => void): void {
    this.onChanged = fn;
  }

  /**
   * Register touch event
   *
   * @param fn callback
   */
  public registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  /**
   * Set disabled state of the control
   *
   * @param isDisabled is control disabled
   */
  public setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }

  /**
   * Write new value
   *
   * @param value text
   */
  writeValue(value: string): void {
    this.value = value;
  }

  /**
   * Detect text change
   *
   * @param e HTML event containing target
   */
  onTextChange(e: Event): void {
    this.value = (e.target as HTMLTextAreaElement).value;
    if (this.onTouched && this.onChanged) {
      this.onTouched();
      this.onChanged(this.value);
    }
    this.valueChange.emit(!!this.value);
  }
}

import { Component, Input, OnInit } from '@angular/core';
import { ControlContainer, FormGroup } from '@angular/forms';

/** TimePicker interface declaration */
export interface TimePickerModel {
  days: number;
  hours: number;
  minutes: number;
  seconds: number;
}

/**
 * generate numbers in some range
 *
 * @param start start range
 * @param end end range
 * @yields i
 */
function* range(start: number, end: number) {
  for (let i = start; i <= end; i++) {
    yield i;
  }
}

/**
 * UI TimePicker Component
 */
@Component({
  selector: 'ui-cron-time-picker',
  templateUrl: './time-picker.component.html',
  styleUrls: ['./time-picker.component.scss'],
})
export class TimePickerComponent implements OnInit {
  @Input() public disabled = false;
  @Input() public use24HourTime = true;
  @Input() public hideHours = false;
  @Input() public hideMinutes = false;
  @Input() public hideSeconds = true;

  allForm!: FormGroup;

  public minutes = [...range(0, 59)];
  public seconds = [...range(0, 59)];
  public hourTypes = ['AM', 'PM'];

  /** @returns hours */
  get hours(): number[] {
    return this.use24HourTime ? [...range(0, 23)] : [...range(0, 12)];
  }

  /**
   * Ui TimePicker constructor
   *
   * @param parent parent ControlContainer
   */
  constructor(public parent: ControlContainer) {}

  ngOnInit(): void {
    this.allForm = this.parent.control as FormGroup;
  }
}

import {
  Component,
  EventEmitter,
  Input,
  Provider,
  forwardRef,
} from '@angular/core';
import { ToggleType } from './types/toggle-type';
import { ToggleIcon } from './interfaces/toggle-icon.interface';
import { Variant } from '../types/variant';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';

/** A provider for the ControlValueAccessor interface. */
const CONTROL_VALUE_ACCESSOR: Provider = {
  provide: NG_VALUE_ACCESSOR,
  useExisting: forwardRef(() => ToggleComponent),
  multi: true,
};

/**
 * UI Toggle component
 * Toggle is a UI component that allows users to switch between two mutually exclusive options (checked or unchecked, on or off) through a single click or tap.
 */
@Component({
  selector: 'ui-toggle',
  templateUrl: './toggle.component.html',
  styleUrls: ['./toggle.component.scss'],
  providers: [CONTROL_VALUE_ACCESSOR],
})
export class ToggleComponent implements ControlValueAccessor {
  /** The type of the toggle. */
  @Input() type: ToggleType = 'short';
  /** The icon of the toggle. */
  @Input() icon!: ToggleIcon;
  /** The position of the label for the toggle. */
  @Input() labelPosition: 'right' | 'left' = 'right';
  /** The variant or style of the toggle. */
  @Input() variant: Variant = 'primary';
  /** The value of the toggle. */
  value = false;
  /** Boolean indicating whether the toggle is disabled. */
  disabled = false;
  /** Event emitter for value changes. */
  valueChange: EventEmitter<boolean> = new EventEmitter();
  /** Function to handle value changes. */
  onChange!: (value: boolean) => void;
  /** Function to handle touch events. */
  onTouch!: () => void;

  /** @returns general toggle classes and variant */
  get toggleClasses(): string[] {
    const classes: string[] = [];
    // Disable state
    classes.push(this.disabled ? 'opacity-70' : '');
    // Space from button to label depending on label position
    classes.push(this.labelPosition === 'left' ? 'ml-3' : 'mr-3');
    // Order of button depending on label position
    classes.push(this.labelPosition === 'left' ? 'order-2' : 'order-1');
    // Variants
    classes.push('focus-' + this.variant);
    if (this.type === 'simple') {
      classes.push('button-simple');
      if (!this.value) {
        classes.push('bg-gray-200');
      } else {
        classes.push('toggle-' + this.variant);
      }
    } else {
      classes.push('button-short');
    }
    return classes;
  }

  /** @returns shot toggle classes and variant */
  get shortToggleClasses(): string[] {
    const classes: string[] = [];
    if (!this.value) {
      classes.push('bg-gray-200');
    } else {
      classes.push('toggle-' + this.variant);
    }
    return classes;
  }

  /**
   * Handles the selection of a content
   *
   */
  public onSelect(): void {
    this.value = !this.value;
    if (this.onTouch && this.onChange) {
      this.onTouch();
      this.onChange(this.value);
    }
    this.valueChange.emit(this.value);
  }

  /**
   * Write value of control.
   *
   * @param value new value
   */
  public writeValue(value: boolean): void {
    this.value = value;
  }

  /**
   * Register new method to call when control state change
   *
   * @param fn callback function
   */
  public registerOnChange(fn: (value: boolean) => void): void {
    if (!this.onChange) {
      this.onChange = fn;
    }
  }

  /**
   * Register new method to call when control touch state change
   *
   * @param fn callback function
   */
  public registerOnTouched(fn: () => void): void {
    if (!this.onTouch) {
      this.onTouch = fn;
    }
  }

  /**
   * Set disabled state of the control
   *
   * @param isDisabled is control disabled
   */
  public setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }
}

